UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to approach a table and pull out a chair
• Demonstrate proper sitting posture with feet flat
• Practice sitting with the chair properly tucked in
• Show how to stand up and push in the chair
• Emphasize control and quiet movement',
  presentation_steps = '[{"step": 1, "title": "Approaching the Table", "description": "Invite the child to observe you approaching a table. Show how to walk up calmly and position yourself at the chair.", "tip": "Choose a table and chair set appropriately sized for the child."}, {"step": 2, "title": "Pulling Out the Chair", "description": "Demonstrate grasping the chair seat with both hands and pulling it out far enough to sit, doing so quietly and with control.", "tip": "Show how much space you need to be comfortable without being too far from the table."}, {"step": 3, "title": "Sitting Down", "description": "Show how to turn and sit down gently, placing feet flat on the floor and positioning yourself comfortably at the table.", "tip": "Demonstrate good posture: back straight, feet flat, arms able to rest on the table."}, {"step": 4, "title": "Tucking in the Chair", "description": "Show how to gently push the chair in toward the table, ensuring you are close enough to work comfortably.", "tip": "Explain that this keeps the classroom organized and creates a neat appearance."}, {"step": 5, "title": "Standing and Leaving", "description": "Demonstrate how to push the chair back gently with both hands, stand up, and then push the chair back under the table.", "tip": "Show how to ensure the chair is tucked neatly before walking away."}]',
  control_of_error = 'The child feels the comfort or discomfort of their posture. The sound of the chair scraping or the table bumping provides feedback. Observing other children demonstrates the norms of the classroom.',
  direct_aims = '["To learn how to sit at a table properly", "To develop body awareness and posture", "To understand how to prepare and maintain a workspace"]',
  materials = '["Child-sized table and chair set", "Proper clearance around the table", "Clear sightlines for demonstrating the activity"]',
  parent_description = 'Your child is learning proper table etiquette and body awareness while sitting. This fundamental skill develops good posture, coordination, and classroom norms. It prepares your child for extended periods of work at a table, including eating, writing, and other focused activities.',
  why_it_matters = 'Learning to sit and stand properly at a table introduces children to classroom behavior expectations and develops the body awareness and posture needed for future academic work. It establishes habits of orderliness, respect for shared spaces, and physical control that support a calm, focused learning environment.'
