UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Explain that this is a special game about listening quietly
• Demonstrate sitting or moving in complete silence
• Use a signal to start and end the silence
• Encourage listening to quiet sounds in the environment
• Praise effort and participation in maintaining silence',
  presentation_steps = '[{"step": 1, "title": "Introduction and Invitation", "description": "Gather children together and introduce the Silence Game as a special activity where everyone practices being very quiet together.", "tip": "Use an inviting, calm tone to introduce this activity."}, {"step": 2, "title": "Explanation of the Rules", "description": "Explain that during the game, we will all sit or move in complete silence, listening to the quiet sounds around us.", "tip": "Be clear and simple: no talking, no movement noise, just listening."}, {"step": 3, "title": "Demonstrating Silence", "description": "Show what silence looks like by sitting very still and quiet for a moment. Then ask a volunteer child to do the same.", "tip": "Make it clear this is a peaceful, enjoyable activity, not a punishment."}, {"step": 4, "title": "Beginning the Game", "description": "Use a soft signal (bell, hand gesture, or whisper) to begin the silence. Participate in the silence yourself.", "tip": "Keep the initial period short (even 30 seconds is an achievement for younger children)."}, {"step": 5, "title": "Ending and Reflection", "description": "Use a gentle signal to end the silence. Ask children what they heard or felt during the quiet time.", "tip": "Celebrate their success and listen to their observations about the sounds they noticed."}]',
  control_of_error = 'The sound itself provides feedback. If a child makes noise, everyone can hear it and becomes aware of the sound. The silence is immediately broken and must be restored.',
  direct_aims = '["To develop the ability to sit quietly and listen", "To build concentration and inner awareness", "To experience peace and calm"]',
  materials = '["A quiet, calm space in the classroom", "A soft signal to begin and end (bell, chime, or hand gesture)", "Children sitting comfortably (floor, mat, or chairs)"]',
  parent_description = 'Your child is learning to sit quietly and listen. The Silence Game develops concentration, self-control, and an appreciation for quiet and calm. It''s a powerful tool for helping children slow down, become aware of their inner world, and build the foundation for focused learning.',
  why_it_matters = 'The Silence Game is a unique Montessori activity that develops self-control, concentration, and inner awareness. It teaches children to manage their own behavior and create peace within themselves. This skill is essential for success in the classroom and life, providing a tool for self-regulation that children can use throughout their lives.'
WHERE LOWER(name) = LOWER('The Silence Game');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to open a book gently
• Demonstrate holding the book with one hand
• Show how to turn pages from right to left carefully
• Emphasize respect for the book and care of materials
• Practice turning pages repeatedly for mastery',
  presentation_steps = '[{"step": 1, "title": "Invitation and Selection", "description": "Invite the child to work with a book. Choose a sturdy, beautifully illustrated book with thick pages.", "tip": "Select a book appropriate for the child''s age and interests."}, {"step": 2, "title": "Holding the Book", "description": "Show how to hold the book with one hand on the left side of the spine and allow the right side to rest on a table or your other hand.", "tip": "Demonstrate that the book should be held gently, not squeezed."}, {"step": 3, "title": "Opening the Book", "description": "Show how to open the book slowly and carefully, allowing the front cover to lie flat.", "tip": "Demonstrate respect for the binding and care in how the cover opens."}, {"step": 4, "title": "Turning the Pages", "description": "With thumb and forefinger, grasp the upper right corner of the page and turn it slowly from right to left, allowing it to flip gently to reveal the next page.", "tip": "Emphasize the careful, deliberate motion. Show how to wait for the page to settle before turning the next one."}, {"step": 5, "title": "Closing the Book", "description": "Show how to close the book gently, placing the front cover down slowly and carefully.", "tip": "Demonstrate that we close the book with the same care and respect we used to open it."}]',
  control_of_error = 'If a page tears, the child learns immediately that roughness damages books. The visual progression through the book shows if they are turning pages in the correct order.',
  direct_aims = '["To learn how to handle books with care", "To develop fine motor control and precision", "To understand the sequence and flow of pages"]',
  materials = '["Age-appropriate books with thick pages", "Beautifully illustrated books with engaging images", "A clean, flat surface (table or mat) for working with the book", "Clear lighting to see the pages"]',
  parent_description = 'Your child is learning to handle books with care and respect. This activity develops fine motor control and an appreciation for reading materials. It introduces your child to the joy of looking at beautiful pictures and the care that books deserve.',
  why_it_matters = 'Turning pages is a foundational skill that prepares children for reading and respects books as precious materials. It develops fine motor control and care for the environment while fostering a love of books and reading. This early positive experience with books is crucial for developing literacy and a lifelong appreciation for reading.'
WHERE LOWER(name) = LOWER('Turning Pages of a Book');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Fold FROM BODY AWAY (bottom to top) - stitched line appears on fold edge
• Match CORNERS EXACTLY - this precision IS the point of interest
• SLIDE fingers along fold to smooth - don''t press hard (no crease yet)
• Use cloths with CONTRASTING STITCHED LINES as visual guides
• Progress: single horizontal → double → diagonal → double diagonal',
  presentation_steps = '[{"step": 1, "title": "Position Cloth", "description": "Place cloth in front with wrong side up, stitched line horizontal.", "tip": "Line orientation matters"}, {"step": 2, "title": "Run Finger Along Line", "description": "Run index finger along the stitched line from left to right.", "tip": "Shows child where to fold"}, {"step": 3, "title": "Fold Motion", "description": "Place dominant hand above line, grasp lower corner with other hand, fold upward.", "tip": "Fold away from body"}, {"step": 4, "title": "Match Corners", "description": "Align corners EXACTLY - take time, this is the key skill.", "tip": "Precision is the goal"}, {"step": 5, "title": "Smooth Fold", "description": "Slide fingers gently along fold from center outward.", "tip": "Smooth, don''t crease hard"}, {"step": 6, "title": "Release", "description": "Lift index finger first, then thumb - deliberate sequence.", "tip": "Clean release prevents shifting"}, {"step": 7, "title": "Unfold and Repeat", "description": "Open cloth, return to start, invite child to try.", "tip": "Always unfold before child''s turn"}]',
  control_of_error = 'Stitched lines show correct alignment. Child can see if fold is even.',
  direct_aims = '["Fine motor", "Precision"]',
  materials = '["Napkins", "Small cloths", "Folding cloths with lines"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Folding Cloths');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up two bowls or containers with dry material (beads, buttons, beans)
• Show how to pick up one item at a time using fingers
• Transfer items carefully from one bowl to another
• Emphasize precision and control
• Allow multiple repetitions for mastery',
  presentation_steps = '[{"step": 1, "title": "Preparation and Invitation", "description": "Set up a tray with two small bowls, one filled with dry objects (beads, buttons, or dried beans) and one empty.", "tip": "Use items that are easy for small fingers to grasp, such as large beads or buttons."}, {"step": 2, "title": "Demonstrating the Activity", "description": "Invite the child to watch. Pick up one item with your fingers and transfer it to the empty bowl. Then pick up another.", "tip": "Move slowly and deliberately, allowing the child to see each movement clearly."}, {"step": 3, "title": "Showing Precision", "description": "Demonstrate transferring items one at a time, showing how to grasp, lift, and place each item gently.", "tip": "Emphasize control and care rather than speed."}, {"step": 4, "title": "Completing the Activity", "description": "Continue until all items have been transferred from one bowl to the other, then show how to reverse the process.", "tip": "Demonstrate that the activity can continue indefinitely, allowing for repetition."}, {"step": 5, "title": "Inviting the Child to Work", "description": "Invite the child to try the activity themselves. Step back and allow them to work independently.", "tip": "Observe and support as needed, but allow the child to discover the activity through their own exploration."}]',
  control_of_error = 'The objects themselves provide feedback. If grasped improperly, they may fall or be difficult to lift. Spilled items show if control is lacking.',
  direct_aims = '["To develop fine motor skills and hand strength", "To practice the refined pincer grasp needed for writing", "To build concentration and focus through repetition"]',
  materials = '["Two small bowls or containers", "Dry transfer objects (beads, buttons, beans, or pom-poms)", "A tray to hold all materials", "A table or mat for working"]',
  parent_description = 'Your child is developing fine motor skills through this simple but powerful activity. Transferring objects by hand helps build the small muscle control needed for writing and other precise tasks. It also develops concentration and the ability to focus on a repetitive activity.',
  why_it_matters = 'Dry transfer with hands is one of the foundational practical life activities that develops the refined pincer grasp essential for writing. It builds hand strength, coordination, and concentration through engaging, repetitive work that children find naturally satisfying. This activity is the stepping stone to more complex hand work activities.'
WHERE LOWER(name) = LOWER('Dry Transfer - Hands');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• THREE-FINGER GRIP on spoon (thumb, index, middle) - EXACT pencil grip preparation
• Scoop TOWARD body, lift ABOVE bowl rim, pause, then release over target bowl
• Non-dominant hand GROUNDS the bowl throughout - stability is key
• Progress: large spoon + large items → small spoon + small items
• Let child continue HALFWAY through - don''t demonstrate entire transfer',
  presentation_steps = '[{"step": 1, "title": "Setup", "description": "Sit to child''s right. Place full bowl on left, empty bowl on right, spoon between.", "tip": "Left-to-right flow prepares for reading"}, {"step": 2, "title": "Demonstrate Grip", "description": "Show three-finger grip: thumb on top, index and middle below handle.", "tip": "This is exact pencil grip - indirect writing prep"}, {"step": 3, "title": "Stabilize Bowl", "description": "Place non-dominant hand on edge of source bowl to steady it.", "tip": "Grounding prevents bowl from sliding"}, {"step": 4, "title": "Scoop Motion", "description": "Scoop toward body, lifting spoon with material.", "tip": "Toward body gives better control"}, {"step": 5, "title": "Lift Above Rim", "description": "Lift spoon UP and OVER the rim of bowl, pause briefly.", "tip": "Pause prevents drips between bowls"}, {"step": 6, "title": "Transfer", "description": "Move spoon to target bowl, tilt to release material gently.", "tip": "Rotate spoon toward body to release"}, {"step": 7, "title": "Return Spoon", "description": "Return spoon to starting position, ready for next scoop.", "tip": "Return spoon flat, not tilted"}, {"step": 8, "title": "Invite Child", "description": "After 3-4 transfers, invite child to continue.", "tip": "Don''t complete entire transfer - leave work for child"}]',
  control_of_error = 'Spills provide immediate tactile feedback. Container design prevents excessive spillage.',
  direct_aims = '["Fine motor control", "Spoon grip"]',
  materials = '["Two bowls", "Spoon", "Dry materials (rice, beans, lentils)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Spooning');

