UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up containers with water and a sponge
• Show how to soak the sponge and squeeze out excess water
• Transfer water from one container to another
• Emphasize control and preventing spills
• Allow for water play and exploration',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a low table or tray with two containers, one filled with water and one empty. Place a natural sponge nearby.", "tip": "Use a waterproof tray underneath to catch any spills. Provide towels for cleaning up."}, {"step": 2, "title": "Demonstrating Water Absorption", "description": "Show how to place the sponge in the water and allow it to absorb the water fully.", "tip": "Ask the child to feel the weight of the sponge as it absorbs water."}, {"step": 3, "title": "Squeezing Out Excess", "description": "Demonstrate lifting the sponge and gently squeezing it over the water container to remove excess water.", "tip": "Show that some water remains in the sponge even after squeezing."}, {"step": 4, "title": "Transferring Water", "description": "Carry the damp sponge to the second container and squeeze it out, transferring the water.", "tip": "Move slowly and deliberately to avoid splashing."}, {"step": 5, "title": "Repeating the Cycle", "description": "Show how the activity continues: soak, squeeze, transfer, repeat.", "tip": "Demonstrate that this activity can continue as long as there is water to transfer."}]',
  control_of_error = 'Spilled water provides immediate feedback about control. The weight of the sponge and the amount of water remaining provide sensory information about the process.',
  direct_aims = '["To learn to squeeze and control water", "To develop hand strength through squeezing", "To practice transferring liquids with care"]',
  materials = '["Two small containers or bowls", "A natural sponge (or synthetic child-safe sponge)", "Water", "A tray to contain spills", "Towels for cleanup", "Apron for water protection"]',
  parent_description = 'Your child is learning to control water and absorptive materials through squeezing and transferring. This water-based activity develops hand strength, coordination, and understanding of cause and effect. It''s a foundational skill for future practical life activities involving water.',
  why_it_matters = 'Sponging is a transfer activity that introduces children to water control and the properties of absorbent materials. It develops the hand strength and coordination needed for squeezing, which is essential for many practical life tasks. This activity also provides sensory exploration and builds confidence in handling liquids, preparing children for more complex water activities.'
WHERE LOWER(name) = LOWER('Sponging');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Transferring liquid using a baster (turkey baster style)
• Develops hand strength and coordination
• Squeezing action strengthens writing muscles
• Water transferred from one container to another
• Progression from large to small containers',
  presentation_steps = '[{"step": 1, "title": "Material Setup", "description": "Two containers on tray: one with colored water, one empty.", "tip": "Contrasting color shows transfer."}, {"step": 2, "title": "Grip Demonstration", "description": "Hold bulb with thumb and fingers. Squeeze to expel air.", "tip": "Same muscles as writing grip."}, {"step": 3, "title": "Drawing Up", "description": "Insert tube in water, release squeeze to draw up water.", "tip": "Slow release for maximum fill."}, {"step": 4, "title": "Transfer", "description": "Move to empty container, squeeze to release water.", "tip": "Complete transfer."}, {"step": 5, "title": "Continue", "description": "Repeat until all water transferred.", "tip": "Careful not to drip."}, {"step": 6, "title": "Cleanup", "description": "Wipe any spills. Return materials to shelf.", "tip": "Complete work cycle."}]',
  control_of_error = 'Water drips',
  direct_aims = '["Hand strength", "Squeeze-release coordination"]',
  materials = '["Two bowls", "Turkey baster", "Water"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Basting (Turkey Baster)');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Dip dropper into colored water and squeeze until bubbles appear", "Release pressure to fill dropper with water", "Carefully transfer drops to empty container", "Focus on smooth, controlled movements", "Repeat until mastery and concentration develop"]',
  presentation_steps = '[{"title": "Invitation & Setup", "description": "Invite the child with enthusiasm. Prepare a tray with two clear containers\u2014one filled with colored water (add 1-2 drops of food coloring), one empty. Place beside the child.", "tip": "Use a small, child-sized tray so everything is visible and accessible."}, {"title": "Demonstrating the Dropper Technique", "description": "Hold the dropper vertically. Show how to squeeze the bulb until bubbles appear, then release pressure slowly as the dropper fills with water. Do this slowly and deliberately, narrating each movement.", "tip": "Exaggerate the movements so the child clearly sees the squeeze-release action."}, {"title": "Transferring Water", "description": "Lift the filled dropper over the empty container. Squeeze gently to release one or two drops into the container. Return the dropper to the source water for refilling.", "tip": "Keep movements slow and graceful; this is a meditation in control."}, {"title": "Repetition & Observation", "description": "Complete several transfers while the child observes. Maintain perfect posture and calm concentration throughout.", "tip": "Do not rush; let the child see the meditative quality of the work."}, {"title": "Invitation to Child", "description": "Ask, ''Would you like to try?'' Step back and allow the child to practice. Observe without correcting unless safety is at risk.", "tip": "Resist the urge to guide their hand; allow discovery through repetition."}, {"title": "Care of Materials", "description": "When finished, show the child how to carefully wipe the tray dry with a cloth and return all materials to the shelf.", "tip": "This completes the full cycle of care and responsibility."}]',
  control_of_error = '["Water spills if too much pressure is applied", "Drops miss the container if dropper is not positioned correctly", "Child naturally sees when container is getting full"]',
  direct_aims = '["Develop pincer grasp (thumb and two fingers)", "Practice fine motor control and hand-eye coordination", "Learn proper dropper technique"]',
  materials = '["Plastic eyedropper or pipette", "Two clear containers or small bowls", "Water", "Food coloring (optional but engaging)", "Small tray", "Cloth for cleanup"]',
  parent_description = 'Eye dropper work develops the precise hand control your child needs for writing. Through hundreds of small, focused repetitions, the hand learns to work with delicate instruments. This quiet activity builds concentration and calm.',
  why_it_matters = 'The eyedropper is a foundational transfer tool developing the pincer grasp. It builds the fine motor control essential for writing, drawing, and future academic work. The meditative quality fosters deep concentration.'
WHERE LOWER(name) = LOWER('Eye Dropper');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how velcro strips connect with a light pressing motion
• Demonstrate how to align the strips properly
• Practice pressing together and pulling apart gently
• Emphasize care in handling the velcro
• Allow for repetition to develop skill',
  presentation_steps = '[{"step": 1, "title": "Introduction to the Frame", "description": "Present the velcro frame. Explain that this helps us practice a type of fastener found on clothing.", "tip": "The frame should have soft velcro on one side and hook velcro on the other for a realistic fastening experience."}, {"step": 2, "title": "Demonstrating Alignment", "description": "Show how to line up the two velcro sides so they meet properly.", "tip": "Demonstrate that the strips need to be facing each other correctly for them to adhere."}, {"step": 3, "title": "Pressing Together", "description": "Show how to press the velcro strips together with gentle, firm pressure.", "tip": "Explain that too light a press won''t work, but pressing too hard isn''t necessary."}, {"step": 4, "title": "Separating the Velcro", "description": "Demonstrate how to gently pull the velcro apart, showing that it requires some force but comes apart smoothly.", "tip": "Show how to pull straight away from the fabric rather than at angles."}, {"step": 5, "title": "Independent Practice", "description": "Invite the child to practice fastening and unfastening the velcro multiple times.", "tip": "Allow the child to discover the right amount of pressure and alignment through repetition."}]',
  control_of_error = 'The velcro provides immediate feedback. If not aligned properly, it won''t adhere. If pressed correctly, the connection feels secure.',
  direct_aims = '["To learn how velcro fasteners work", "To develop hand strength and dexterity", "To understand a common clothing fastening"]',
  materials = '["Velcro frame (wooden frame with velcro strips)", "Safe, durable velcro that can withstand repeated use", "Attractive, child-friendly presentation"]',
  parent_description = 'Your child is learning to use velcro fasteners through a simple, engaging frame. This is the easiest dressing frame and introduces the concept of fastening clothing. It develops hand strength and coordination needed for self-dressing.',
  why_it_matters = 'The Velcro Frame is the first dressing frame, chosen because velcro is the easiest fastener for young children to manage. It introduces the concept of fastening and unfastening clothing through a simple, sensory-rich activity that develops the hand strength and coordination essential for self-dressing and clothing independence.'
WHERE LOWER(name) = LOWER('Velcro Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how snap halves align and connect
• Demonstrate pushing together firmly and evenly
• Practice separating snaps with controlled force
• Emphasize the alignment of snap components
• Allow for gradual skill development through repetition',
  presentation_steps = '[{"step": 1, "title": "Introduction to Snaps", "description": "Present the snaps frame and explain that this fastener requires pressing together firmly.", "tip": "Ensure the snaps are appropriately sized and not too stiff for the child''s strength level."}, {"step": 2, "title": "Understanding Snap Mechanics", "description": "Show the two halves of a snap and explain that they need to press together to fasten.", "tip": "Point out the ball-and-socket mechanism if it''s visible on larger snaps."}, {"step": 3, "title": "Demonstrating Alignment", "description": "Show how to position the two snap halves so they are directly aligned.", "tip": "Explain that snaps only fasten when perfectly aligned."}, {"step": 4, "title": "Pressing Snaps Together", "description": "Demonstrate pressing the snaps together with firm, even pressure, using finger strength.", "tip": "Show that this requires more pressure than velcro and more precision than buttons."}, {"step": 5, "title": "Separating Snaps", "description": "Show how to pull the snap halves apart by grasping firmly and pulling with controlled force.", "tip": "Demonstrate that you might need to wiggle slightly while pulling to separate the snaps."}]',
  control_of_error = 'Snaps provide tactile and auditory feedback. Correct alignment and pressure produce a satisfying ''pop.'' Incorrect pressure or alignment fails silently.',
  direct_aims = '["To learn how snap fasteners work", "To develop finger strength for snapping", "To practice precise alignment and positioning"]',
  materials = '["Snaps frame (with appropriately sized snaps)", "Sturdy fabric backing for the snaps", "Multiple snaps to practice with"]',
  parent_description = 'Your child is learning to use snap fasteners. This requires more finger strength than velcro but less precision than buttons. Snaps help develop the hand strength and coordination your child will need for many types of clothing.',
  why_it_matters = 'The Snaps Frame is a progression from velcro that introduces a fastener requiring more strength and precision. Snaps are found on many clothing items and learning to manipulate them is essential for dressing independence. This activity builds hand strength and problem-solving skills as children figure out the alignment needed for snaps to work properly.'
WHERE LOWER(name) = LOWER('Snaps Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to hold the button and pass it through the buttonhole
• Demonstrate alignment and smooth entry
• Emphasize using both hands for control
• Practice repeated buttoning and unbuttoning
• Allow patience for developing this complex skill',
  presentation_steps = '[{"step": 1, "title": "Introduction to Buttons", "description": "Present the large buttons frame. Explain that buttons are a common fastener on clothing.", "tip": "Start with large buttons (at least 1.5 inches) with appropriately sized buttonholes."}, {"step": 2, "title": "Understanding Button Structure", "description": "Show the button and the buttonhole. Explain that the button must pass through the hole to fasten.", "tip": "Let the child hold and feel the button to understand its size and shape."}, {"step": 3, "title": "Demonstrating Pulling the Button", "description": "Show how to grasp the button between thumb and forefinger and prepare to guide it through the hole.", "tip": "Demonstrate slow, deliberate movements."}, {"step": 4, "title": "Passing Through the Buttonhole", "description": "Show how to push or pull the button through the buttonhole with controlled pressure.", "tip": "Explain that sometimes you need to wiggle the button slightly to get it through."}, {"step": 5, "title": "Unbuttoning", "description": "Show how to hold the fabric and push or pull the button back through the hole to unfasten.", "tip": "Demonstrate that unbuttoning often requires less force than buttoning."}]',
  control_of_error = 'The button and buttonhole provide feedback. If grasped correctly and positioned properly, the button passes through the hole. Improper technique results in repeated failures.',
  direct_aims = '["To learn how to button and unbutton clothing", "To develop fine motor control with small fasteners", "To understand a fundamental dressing skill"]',
  materials = '["Large buttons frame (with buttons at least 1.5 inches diameter)", "Appropriately sized buttonholes", "Sturdy fabric backing", "Buttons with good contrast from the frame"]',
  parent_description = 'Your child is learning to button and unbutton clothing, an important step toward dressing independence. This activity requires fine motor control and hand coordination. Your child will eventually apply this skill to buttoning their own clothes.',
  why_it_matters = 'Large buttons frame is a critical dressing frame that introduces buttoning, one of the most common clothing fasteners. Learning to button and unbutton develops hand-eye coordination and bilateral control essential for many practical life tasks. This skill is foundational for independence in self-dressing.'
WHERE LOWER(name) = LOWER('Large Buttons Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to handle smaller buttons with more precision
• Demonstrate alignment with smaller buttonholes
• Practice refined finger control and positioning
• Emphasize patience with this challenging task
• Allow for gradual mastery through repetition',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Introduction", "description": "Ensure child has mastered large buttons before introducing small buttons. Present the small buttons frame.", "tip": "Small buttons should be 0.75 to 1 inch in diameter with proportionally sized buttonholes."}, {"step": 2, "title": "Comparing Button Sizes", "description": "Allow the child to hold and compare large and small buttons, understanding the increased challenge.", "tip": "Discuss how smaller buttons require more precise hand positioning."}, {"step": 3, "title": "Demonstrating Refined Grip", "description": "Show how to grasp a small button with refined thumb and forefinger positioning.", "tip": "Emphasize that small buttons require more careful handling but the basic process is the same."}, {"step": 4, "title": "Precise Alignment and Insertion", "description": "Demonstrate aligning the small button with the buttonhole and carefully pushing it through.", "tip": "Show that small buttons may need to be positioned at a specific angle for easier insertion."}, {"step": 5, "title": "Practice and Mastery", "description": "Invite the child to practice repeatedly, offering support as needed.", "tip": "Celebrate small successes and encourage persistence with this more challenging task."}]',
  control_of_error = 'The small buttons and buttonholes provide very clear feedback. If aligned incorrectly, the button won''t fit. The small size makes success or failure immediately obvious.',
  direct_aims = '["To develop advanced button manipulation skills", "To practice refined fine motor control", "To master fastening with smaller buttons"]',
  materials = '["Small buttons frame (0.75 - 1 inch diameter buttons)", "Appropriately sized buttonholes", "Sturdy fabric backing", "Buttons with good visual contrast"]',
  parent_description = 'Your child is mastering smaller button fastening. This advanced dressing frame requires refined finger control and patience. Completing this activity shows significant progress in fine motor development and persistence.',
  why_it_matters = 'Small buttons frame is an advanced dressing activity that challenges children to apply their buttoning skills with greater precision. It develops the refined finger control needed for complex clothing fasteners and prepares children for complete independence in dressing. Success with small buttons builds significant confidence and self-esteem.'
WHERE LOWER(name) = LOWER('Small Buttons Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to position the zipper slider and fabric
• Demonstrate pulling the slider up and down smoothly
• Emphasize care in handling the zipper mechanism
• Practice the complete zipper motion repeatedly
• Allow for gradual mastery of this complex fastener',
  presentation_steps = '[{"step": 1, "title": "Introduction to Zippers", "description": "Present the zipper frame and explain how zippers work differently from other fasteners.", "tip": "Show zippers of different sizes and types to introduce variety."}, {"step": 2, "title": "Understanding Zipper Parts", "description": "Point out the slider, zipper teeth, and pull tab. Explain how these parts work together.", "tip": "Let the child feel the teeth and slider to understand the mechanism."}, {"step": 3, "title": "Demonstrating Starting Position", "description": "Show how to position the slider at the bottom of the zipper, ready to pull up.", "tip": "Explain that zippers have a top and bottom, unlike other fasteners."}, {"step": 4, "title": "Pulling the Slider Up", "description": "Demonstrate pulling the slider smoothly up the zipper track with controlled pressure.", "tip": "Show that the slider should move evenly, and the zipper should fasten smoothly."}, {"step": 5, "title": "Pulling the Slider Down", "description": "Show how to reverse the process, pulling the slider down to unfasten the zipper.", "tip": "Demonstrate that the same technique works in reverse."}]',
  control_of_error = 'Zippers provide immediate feedback. If the slider is pulled off-track, the zipper jams. If teeth aren''t aligned, the slider binds. This immediate feedback helps children understand cause and effect.',
  direct_aims = '["To learn how zippers work", "To develop the smooth pulling motion needed for zippers", "To understand a complex dressing fastener"]',
  materials = '["Zipper frame with appropriately sized zippers", "Smooth-working zippers that don''t jam easily", "Sturdy backing fabric", "Easy-to-grip pull tabs for young children"]',
  parent_description = 'Your child is learning to use zippers, a more complex fastener than buttons or snaps. Zippers require smooth, coordinated pulling and understanding of directionality. This is an important skill for independence with many types of clothing.',
  why_it_matters = 'Zipper frame introduces children to mechanical fastening systems that are more sophisticated than simple buttons or snaps. Learning to manipulate zippers develops smooth, coordinated hand movements and understanding of directional concepts (up/down). This prepares children for independence with jackets, coats, and other zip-up clothing.'
WHERE LOWER(name) = LOWER('Zipper Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how the hook and eye pieces align
• Demonstrate positioning the hook into the eye loop
• Emphasize precise alignment and gentle pressure
• Practice repeated hooking and unhooking
• Allow patience for developing this fine skill',
  presentation_steps = '[{"step": 1, "title": "Introduction to Hooks and Eyes", "description": "Present the hook and eye frame and explain that this fastener uses two different shaped pieces.", "tip": "Show the hook and eye side by side to illustrate how they work together."}, {"step": 2, "title": "Understanding Parts", "description": "Point out the hook (curved metal piece) and the eye (loop-shaped piece). Explain how they connect.", "tip": "Let the child examine the parts to understand the mechanism."}, {"step": 3, "title": "Demonstrating Positioning", "description": "Show how to align the hook with the eye, explaining that they must face each other to connect.", "tip": "Demonstrate the precise positioning needed for hooks and eyes to engage."}, {"step": 4, "title": "Hooking Together", "description": "Show how to gently push or pull the hook into the eye with controlled pressure.", "tip": "Explain that this requires finesse and precision, not just force."}, {"step": 5, "title": "Unhooking", "description": "Demonstrate how to carefully pull the hook away from the eye to separate the fastener.", "tip": "Show that gentle, controlled pulling works better than rough yanking."}]',
  control_of_error = 'Hooks and eyes provide tactile feedback. Correct alignment results in a satisfying engagement. Incorrect positioning or force results in misalignment.',
  direct_aims = '["To learn how hooks and eyes fasten together", "To practice precise alignment and gentle manipulation", "To master a more complex fastener"]',
  materials = '["Hook and eye frame with appropriately sized components", "Sturdy backing fabric", "Hooks and eyes that are smooth and not too stiff", "Good visual contrast between components"]',
  parent_description = 'Your child is learning to use hook and eye fasteners, a more advanced closure found on some clothing. This requires precise alignment and gentle manipulation. Successfully mastering hooks and eyes shows significant development in fine motor control.',
  why_it_matters = 'Hook and eye frame is an advanced dressing activity that teaches children to work with complementary-shaped fastening components. It develops the precision and gentle touch needed for delicate fasteners and introduces understanding of how different shapes can work together. This skill enhances independence with a variety of clothing types.'
WHERE LOWER(name) = LOWER('Hook and Eye Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how the buckle strap passes through the buckle clasp
• Demonstrate inserting the strap smoothly and evenly
• Practice pulling the strap tight after fastening
• Emphasize proper positioning and tension
• Allow for repeated practice to develop skill',
  presentation_steps = '[{"step": 1, "title": "Introduction to Buckles", "description": "Present the buckles frame and explain that buckles are common fasteners on bags, shoes, and clothing.", "tip": "Show real buckles from bags or shoes to make the connection to practical use."}, {"step": 2, "title": "Understanding Buckle Components", "description": "Point out the strap and the buckle clasp. Explain how the strap passes through the buckle to fasten.", "tip": "Let the child feel the strap and buckle to understand their relationship."}, {"step": 3, "title": "Demonstrating Strap Insertion", "description": "Show how to feed the strap through the buckle slot smoothly and evenly.", "tip": "Demonstrate that both sides of the strap should come through the buckle with equal length."}, {"step": 4, "title": "Fastening and Tightening", "description": "Show how to pull the strap to tighten the buckle, creating a snug closure.", "tip": "Explain that the strap should be tight enough that it doesn''t slip back through."}, {"step": 5, "title": "Loosening and Releasing", "description": "Demonstrate pulling the opposite direction to loosen and unfasten the buckle.", "tip": "Show that the same strap motion works for both fastening and unfastening."}]',
  control_of_error = 'The buckle and strap provide feedback. If the strap isn''t threaded correctly, it won''t fasten securely. If pulled too tight, it may be difficult to loosen.',
  direct_aims = '["To learn how buckles fasten with a strap", "To develop hand coordination in managing straps", "To understand practical fastening systems"]',
  materials = '["Buckles frame with appropriately sized buckles and straps", "Straps that are easy to manipulate but stable", "Buckles that are secure and smooth-working", "Frame with clear, accessible buckle positioning"]',
  parent_description = 'Your child is learning to use buckles, practical fasteners found on many items children encounter daily. This activity develops hand coordination and understanding of how to secure straps. It helps prepare your child for independence with bags, backpacks, and other buckled items.',
  why_it_matters = 'Buckles frame teaches children to manage practical fastening systems they''ll encounter in daily life. Buckles require coordination of two hands and understanding of tension and security. This skill prepares children for independence with a variety of practical items and reinforces that different fasteners require different techniques.'
WHERE LOWER(name) = LOWER('Buckles Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Demonstrate proper handling of safety pins for safety
• Show how to open and close the safety pin mechanism
• Practice securing fabric with the pin
• Emphasize careful, controlled movements
• Supervise closely for safety',
  presentation_steps = '[{"step": 1, "title": "Safety Orientation", "description": "Before beginning, establish clear safety rules. Explain that safety pins are sharp and require careful handling.", "tip": "Demonstrate proper respect for the sharp point and mechanism of the safety pin."}, {"step": 2, "title": "Understanding Safety Pin Parts", "description": "Show the spring, clasp, and point of the safety pin. Explain how they work together to keep the pin closed and safe.", "tip": "Let the child feel (carefully) the mechanisms, emphasizing safety and care."}, {"step": 3, "title": "Demonstrating Opening the Pin", "description": "Show how to hold the safety pin and carefully pull the clasp back to open it.", "tip": "Emphasize that the pointed end must be kept away from the body."}, {"step": 4, "title": "Securing Fabric", "description": "Demonstrate pushing the safety pin through a piece of fabric on the frame, keeping the point controlled.", "tip": "Show that the pin should go through cleanly without forcing or unnecessary pressure."}, {"step": 5, "title": "Closing the Pin", "description": "Show how to push the clasp forward to lock the pin and secure the fabric.", "tip": "Emphasize that the closed pin is safe and secure."}]',
  control_of_error = 'The safety pin itself is the control of error. The open point is clearly dangerous, and closing the clasp provides safety and security.',
  direct_aims = '["To learn how to safely handle and use safety pins", "To understand safety mechanisms", "To practice fastening with safety pins"]',
  materials = '["Safety pins frame with sturdy fabric", "Child-sized or appropriately manageable safety pins", "Clear visual markers on the fabric to fasten", "Adult supervision and guidance throughout activity"]',
  parent_description = 'Your child is learning to use safety pins with proper care and respect. Safety pins require understanding of how safety mechanisms work and respect for sharp objects. This is an important lesson in tool use and responsibility, taught with careful supervision.',
  why_it_matters = 'Safety pins frame teaches children about responsibility, safety, and careful tool use. It introduces the concept that tools have safety features designed to protect us, and that proper handling is important. Learning to respect and correctly use safety pins prepares children for responsible handling of tools throughout life and develops the dexterity needed for small mechanical operations.'
WHERE LOWER(name) = LOWER('Safety Pins Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to thread laces through eyelets
• Demonstrate the crisscross lacing pattern
• Practice tightening and loosening the laces
• Emphasize coordination and pattern awareness
• Allow for skill development through repetition',
  presentation_steps = '[{"step": 1, "title": "Introduction to Lacing", "description": "Present the lacing frame and explain that laces are used on shoes and other items.", "tip": "Show real laced shoes to make a connection to practical application."}, {"step": 2, "title": "Understanding the Pattern", "description": "Explain the lacing sequence (crisscross pattern). Show how laces weave through eyelets.", "tip": "Use a frame with pre-made laces and eyelets clearly marked."}, {"step": 3, "title": "Demonstrating Threading", "description": "Show how to thread one lace end through an eyelet, pulling it through smoothly.", "tip": "Demonstrate slow, deliberate movements that the child can follow."}, {"step": 4, "title": "Creating the Pattern", "description": "Show the full lacing sequence, demonstrating how each lace creates the characteristic crisscross.", "tip": "Allow the child to observe the pattern emerging as laces are threaded."}, {"step": 5, "title": "Tightening and Loosening", "description": "Show how to pull the laces tight and how to loosen them for removal.", "tip": "Demonstrate that the pattern holds the laces in place and tightens securely."}]',
  control_of_error = 'The physical lacing pattern provides feedback. If threaded incorrectly, the pattern doesn''t form properly or the laces don''t tighten securely.',
  direct_aims = '["To learn how to lace shoes and other items", "To develop hand coordination in threading", "To understand sequential lacing patterns"]',
  materials = '["Lacing frame with eyelets and pre-made laces", "Thick, manageable laces (approximately 0.5 inches wide)", "Frame with clear eyelet positioning", "Visual guides for lacing sequence (optional)"]',
  parent_description = 'Your child is learning to lace, a fundamental skill for tying shoes. Lacing develops hand coordination and understanding of sequential patterns. It''s a stepping stone to the more complex skill of bow tying.',
  why_it_matters = 'Lacing frame introduces children to threading techniques and sequential patterns essential for shoe lacing and other practical applications. It develops hand-eye coordination and understanding that patterns serve a functional purpose. Mastering lacing is the foundation for bow tying and eventual independence with shoe fastening.'
WHERE LOWER(name) = LOWER('Lacing Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to cross the laces to create the first knot
• Demonstrate making loops with the second lace
• Practice pulling loops tight to complete the bow
• Emphasize sequencing and hand coordination
• Allow for gradual mastery through repetition',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Introduction", "description": "Ensure child has mastered lacing. Present the bow tying frame with ribbons or laces.", "tip": "The frame typically has red and white ribbons that provide visual clarity for the tying sequence."}, {"step": 2, "title": "Understanding the Bow Components", "description": "Show how a bow is created from two pieces coming together and explain the sequence.", "tip": "Break down the complex motion into clear, observable steps."}, {"step": 3, "title": "Demonstrating the First Knot", "description": "Show how to cross the two laces and pull one through to create the foundation knot.", "tip": "This is the most important step - practice it repeatedly until comfortable."}, {"step": 4, "title": "Making the Loops", "description": "Show how to make two loops (bunny ears) from the laces and position them properly.", "tip": "Demonstrate that both loops should be roughly equal in size."}, {"step": 5, "title": "Tying the Bow", "description": "Show how to cross the loops and pull them tight to create the completed bow.", "tip": "Demonstrate the complete sequence slowly, then practice repeatedly with the child."}]',
  control_of_error = 'A properly tied bow holds securely and looks balanced. An improperly tied bow either falls apart or looks lopsided, providing immediate visual feedback.',
  direct_aims = '["To learn to tie bows for shoes and clothing", "To develop bilateral hand coordination", "To master a complex sequential motor skill"]',
  materials = '["Bow tying frame with red and white ribbons sewn to both sides", "Frame designed to allow clear observation of the tying process", "Ribbons that are appropriately sized for small hands", "Visual guides or demonstrations available"]',
  parent_description = 'Your child is learning to tie bows, one of the most practical and important self-dressing skills. Bow tying requires coordination of both hands and understanding of sequential steps. Successfully tying bows shows significant progress toward complete dressing independence.',
  why_it_matters = 'Bow tying frame is the culmination of the Montessori dressing frames progression. It teaches children to tie their own shoes and fasteners, a fundamental life skill. This complex motor activity requires both hands working together in a precise sequence, building bilateral coordination and motor planning essential for future success in writing and other fine motor activities. Achieving independence with bow tying represents a major milestone in self-care.'
WHERE LOWER(name) = LOWER('Bow Tying Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Brush away loose dirt with hard brush", "Apply polish with applicator brush in circular motions", "Buff with soft brush using long strokes", "Final polish with buffer cloth until shine appears", "Sequence builds pride in personal appearance"]',
  presentation_steps = '[{"title": "Preparation & Invitation", "description": "Invite the child warmly. Have aprons ready for both of you. Explain: ''I''m going to show you how to polish your shoes so they shine beautifully.''", "tip": "Model the respect you have for this care-of-self work."}, {"title": "Setting Up the Mat & Materials", "description": "Place an oilcloth mat on the table. Arrange materials left to right: hard brush, cloth, polish bottle, polish applicator brush, soft brush, buff cloth. Name each as you place it.", "tip": "The left-to-right arrangement teaches order and sequence naturally."}, {"title": "Brushing Away Loose Dirt", "description": "Place the shoe on the oilcloth. Hold it steady with your non-dominant hand. Using the hard brush, brush away loose dirt with back-and-forth strokes, starting from the base and moving upward.", "tip": "Show controlled, methodical strokes\u2014not hasty scrubbing."}, {"title": "Applying Polish", "description": "Open the polish. Gently press the applicator bristles into the polish, then apply in small circular motions to the entire shoe\u2014front, sides, then back. Keep pressure light and even.", "tip": "The circular motion is soothing and effective; avoid streaking."}, {"title": "Buffing & Shining", "description": "Using the large soft brush, brush the shoe with long, light strokes in the direction of the shoe''s length. Repeat with the buffer cloth until a shine emerges. Show the transformation with visible pride.", "tip": "The appearance of the shine is deeply satisfying to the child."}, {"title": "Completing the Care Cycle", "description": "Clean the brushes and materials. Wipe down the tray. Remove aprons and wash hands together. Place shoes in a designated spot with care.", "tip": "Completing all care steps teaches responsibility from start to finish."}, {"title": "Reflection", "description": "Look at the shoes together. Comment on the transformation: ''See how they shine now? Your shoes feel proud to be so well-cared for.''", "tip": "Connect the physical work to self-respect and care."}]',
  control_of_error = '["Unpolished areas are visible", "Streaking shows when technique is off", "Lack of shine indicates insufficient buffing", "Child naturally sees the improvement"]',
  direct_aims = '["Learn the complete shoe-polishing sequence", "Develop care for personal belongings", "Improve hand strength and coordination"]',
  materials = '["Leather shoes (child''s or classroom pair)", "Hard-bristled brush", "Soft-bristled brush", "Buff cloth", "Shoe polish (appropriate for the shoe type)", "Polish applicator brush", "Oilcloth mat", "Cloth for general cleanup", "Apron for child and teacher"]',
  parent_description = 'Shoe polishing teaches your child that caring for belongings reflects self-respect. The repetitive, orderly sequence builds deep concentration and responsibility. Each time your child polishes shoes, they''re learning that effort produces visible results.',
  why_it_matters = 'This work combines fine motor development, cognitive sequencing, and emotional investment in self-care. It bridges the gap between personal hygiene and environmental responsibility, showing children that they can make real, visible improvements in their world.'
WHERE LOWER(name) = LOWER('Shoe Polishing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Understand three-strand braiding pattern", "Practice cross-over motion of strands", "Develop hand coordination for complex movement", "Build fine motor strength", "Prepares for real hair braiding"]',
  presentation_steps = '[{"title": "Introduction & Showing the Frame", "description": "Invite the child and show the braiding frame. Explain: ''This frame teaches us how to braid. You''ll practice the pattern, and later you might braid real hair or rope.''", "tip": "Show excitement about this advanced work."}, {"title": "Understanding Three Strands", "description": "Point to the three strands hanging from the frame. Name them: ''Left, middle, right.'' Have the child name them too. Explain: ''To braid, we cross the strands in a special pattern.''", "tip": "Naming helps the child remember the sequence."}, {"title": "Demonstrating the Braid Pattern", "description": "Slowly demonstrate the pattern: Take the right strand, cross it over the middle strand (it becomes the new middle). Take the left strand, cross it over the middle (it becomes the new middle). Repeat slowly and deliberately. The child should watch closely.", "tip": "Go very slowly; this is a complex visual and motor task."}, {"title": "Hands-On Practice", "description": "Guide the child''s hands through the first few crossovers. Then step back and let them try independently. Offer gentle guidance if needed: ''Right strand next'' or ''Cross it over the middle.''", "tip": "Muscle memory develops through doing, not explaining."}, {"title": "Building Speed & Smoothness", "description": "As the child repeats, the motion becomes more automatic and smooth. Resist the urge to correct every small mistake; let the pattern emerge through practice.", "tip": "This work improves significantly with time and repetition."}, {"title": "Completion & Reflection", "description": "When the braid reaches the bottom, tie off the end with a small fastener. Look at the completed braid together. Celebrate: ''You created that braid all by yourself!''", "tip": "The finished product is deeply satisfying."}]',
  control_of_error = '["Strands tangle if pattern is incorrect (child learns by undoing and trying again)", "Braid becomes loose if tension inconsistent (child adjusts tension naturally)", "Pattern breaks down if not followed (visible in the braid''s appearance)"]',
  direct_aims = '["Master the three-strand braiding pattern", "Develop hand coordination for complex, repetitive motions", "Build finger strength and dexterity"]',
  materials = '["Wooden braiding frame with 3 strands of rope or yarn (12-18 inches each)", "Strands should be different colors for clarity", "Small tie or clip to hold completed braid at bottom", "Storage space for frame"]',
  parent_description = 'The braiding frame is complex work that teaches rhythm and coordination. Children often need weeks of practice before the pattern becomes automatic. This slow mastery builds confidence and prepares the hands for braiding real hair.',
  why_it_matters = 'This work develops bilateral coordination (both hands working together) and abstract thinking (following a repeating pattern without constant visual reference). The meditative quality of repetitive braiding builds deep concentration and calm.'
WHERE LOWER(name) = LOWER('Braiding Frame');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up a low sink with child-accessible controls
• Show how to wet hands and apply soap
• Demonstrate scrubbing all surfaces of hands
• Practice rinsing and drying completely
• Emphasize thoroughness and independence',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Ensure a low sink or water table is accessible. Gather soap, a towel, and a brush if using.", "tip": "Position a step stool so the child can reach the sink independently."}, {"step": 2, "title": "Turning On Water", "description": "Show how to turn on the water (or use a lever or push faucet) and adjust temperature.", "tip": "Ensure water is warm but not hot enough to cause burns."}, {"step": 3, "title": "Wetting Hands", "description": "Demonstrate how to hold hands under running water to wet them thoroughly.", "tip": "Show that both hands should be wet, and palms and backs need coverage."}, {"step": 4, "title": "Applying Soap", "description": "Show how to apply soap (bar or liquid) and create a lather by rubbing hands together.", "tip": "Demonstrate scrubbing the palms, backs, between fingers, and wrists."}, {"step": 5, "title": "Rinsing and Drying", "description": "Show how to rinse hands under running water until all soap is gone and dry with a clean towel.", "tip": "Demonstrate proper drying technique to prevent water dripping."}]',
  control_of_error = 'Clean hands demonstrate success. Remaining soap, dirt, or wetness shows the need for continued work.',
  direct_aims = '["To learn proper hand washing technique", "To develop independence in personal hygiene", "To understand the importance of cleanliness"]',
  materials = '["Low sink or water table", "Step stool for reaching the sink", "Warm water source", "Gentle soap (bar or liquid)", "Hand towel", "Optional: nail brush for scrubbing"]',
  parent_description = 'Your child is learning proper hand washing, a fundamental hygiene habit. Hand washing prevents illness and builds independence in personal care. This skill will serve your child throughout their entire life.',
  why_it_matters = 'Hand washing is a foundational self-care activity essential for health and hygiene. It teaches children responsibility for their own cleanliness and prevention of illness. Learning proper hand washing technique establishes healthy habits that children maintain throughout life and demonstrates that individual actions contribute to community health and wellness.'
WHERE LOWER(name) = LOWER('Hand Washing');

UPDATE montree_classroom_curriculum_works SET
