UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Prepare soapy water and scrubbing cloth", "Work in circular motions from edges to center", "Scrub thoroughly with control and care", "Rinse with clean water thoroughly", "Dry completely with dry cloth"]',
  presentation_steps = '[{"title": "Invitation & Purpose", "description": "Invite the child with enthusiasm: ''Would you like to help me scrub this table? It makes our environment beautiful and clean.''", "tip": "Frame this as meaningful community care, not a chore."}, {"title": "Gathering Materials", "description": "Together, gather: bucket of warm water with mild soap (test temperature together), sponge or scrubbing cloth, rinse water in a pitcher, dry towel or cloth. Arrange all materials within easy reach.", "tip": "Let the child help gather; it builds investment in the work."}, {"title": "Wetting & Wringing the Cloth", "description": "Show how to dip the sponge/cloth in soapy water, then wring out excess so it''s damp but not dripping. Demonstrate the wringing motion with both hands.", "tip": "Proper wringing prevents puddles and teaches control."}, {"title": "Scrubbing the Table Surface", "description": "Starting at the far edge of the table, use circular motions to scrub the surface, working toward the near edge. Cover the entire surface methodically. Maintain consistent pressure\u2014firm enough to clean, gentle enough to be graceful.", "tip": "The circular motion is soothing and effective; narrate the motion as you work."}, {"title": "Rinsing Thoroughly", "description": "Once scrubbed, rinse with clean water using fresh cloth or sponge, working in the same circular pattern. Rinse multiple times until no soap residue remains. Show how to squeeze out excess water.", "tip": "Thorough rinsing prevents sticky tables and soap residue."}, {"title": "Drying the Table", "description": "Using a dry towel or cloth, dry the entire surface using circular motions. Work from edges toward center. The table should be completely dry, shiny, and clean.", "tip": "Drying completes the care cycle and prevents water spots."}, {"title": "Care of Materials", "description": "Show the child how to rinse out the sponge, wring it thoroughly, and hang it to dry. Empty and rinse the bucket. Put all tools away carefully.", "tip": "Caring for the tools teaches respect for materials and the full cycle of work."}]',
  control_of_error = '["Dirty water visible if not rinsed enough (child sees it and rinses again)", "Sticky residue if soap not thoroughly rinsed (child feels it and fixes it)", "Water spots if not dried fully (visible and easily remedied)", "Wet floor if wringing not controlled (child learns proper technique)"]',
  direct_aims = '["Learn the complete table-scrubbing sequence", "Understand care of the environment through physical work", "Develop proper scrubbing technique with controlled movements"]',
  materials = '["Warm water in appropriate bucket or container", "Mild, child-safe soap", "Sponges or scrubbing cloths (multiple)", "Bucket for rinse water", "Pitcher for rinsing", "Dry towels or cloths", "Apron or waterproof clothing"]',
  parent_description = 'Table scrubbing is meaningful environmental care that children can understand immediately: the table was dirty, now it''s clean, and they made that happen. The rhythmic, meditative quality builds concentration while contributing genuinely to community.',
  why_it_matters = 'This work directly connects children''s actions to visible environmental improvement. It teaches that their work matters, that they can improve their surroundings through effort and care, and that this contributes to a beautiful, pleasant community for everyone.'
WHERE LOWER(name) = LOWER('Table Scrubbing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up window washing supplies (water, cloth, squeegee)
• Show how to apply water and soap gently
• Demonstrate wiping and drying technique
• Practice creating transparent, clean windows
• Emphasize the transformation and care involved',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a low window or window-like surface with water, soap, and cloths.", "tip": "Use a child-accessible window or pane that can be cleaned without safety concerns."}, {"step": 2, "title": "Applying Water and Soap", "description": "Show how to dampen a cloth with soapy water and apply it to the window.", "tip": "Demonstrate that wet windows need soap to remove dirt effectively."}, {"step": 3, "title": "Wiping and Cleaning", "description": "Show how to wipe the window with circular or straight motions to remove dirt.", "tip": "Demonstrate that all areas need attention for complete cleanliness."}, {"step": 4, "title": "Rinsing and Drying", "description": "Show how to rinse with clean water and dry thoroughly with a towel.", "tip": "Explain that drying prevents water spots and reveals the clean window."}, {"step": 5, "title": "Observing the Result", "description": "Help the child observe how the clean window allows light through and reveals the view.", "tip": "Celebrate the transformation that cleaning creates."}]',
  control_of_error = 'A clean, transparent window demonstrates success. Streaks or remaining dirt show the need for more work.',
  direct_aims = '["To learn how to clean windows", "To develop strength and controlled motions", "To care for windows and improve the environment"]',
  materials = '["Child-accessible window or pane", "Basin with soapy water", "Soft cloths for wiping", "Dry cloths or paper towels", "Optional: squeegee or window scraper", "Towels for water protection"]',
  parent_description = 'Your child is learning to clean windows, a practical care of environment skill. Window washing develops arm strength and demonstrates dramatically how cleanliness improves the environment. This activity shows your child''s ability to create brightness and clarity in their surroundings.',
  why_it_matters = 'Window washing is a practical care of environment activity with visually immediate results that build satisfaction and motivation. It develops arm strength through the repetitive motions needed for effective cleaning. This activity demonstrates that regular care transforms spaces and allows light and views to shine through, teaching that cleanliness improves both the function and beauty of environments.'
WHERE LOWER(name) = LOWER('Window Washing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Arrange polishing materials left to right in order", "Fold cloth over fingers properly", "Dip cloth lightly in polish", "Apply polish in smooth, even strokes", "Buff with clean cloth until shine appears"]',
  presentation_steps = '[{"title": "Preparation & Invitation", "description": "Invite the child warmly: ''I''m going to show you how to make wood shine beautifully. It takes care and attention to detail.''", "tip": "Use language that conveys respect for the work."}, {"title": "Organizing Materials in Sequence", "description": "Lay out materials left to right on the mat: polish bottle (cap off), dish or shallow container, cotton ball or applicator cloth, polishing cloth, buffing cloth. Name each item as you place it: ''Polish, container, cloth, buff cloth.''", "tip": "The left-to-right sequence teaches order and memory naturally."}, {"title": "Folding the Cloth", "description": "Show how to fold a polishing cloth over two or three fingers, creating a small padded area. This protects fingers from excess liquid and creates a good surface for applying polish.", "tip": "Practice the fold separately until smooth; it''s an important technique."}, {"title": "Dipping in Polish", "description": "Gently dip the cloth-wrapped fingers into the polish. Show that less is more\u2014only a light coating on the cloth is needed. Excess polish wastes product and creates streaks.", "tip": "Model restraint; this is not a heavy scrubbing job."}, {"title": "Applying Polish to Wood", "description": "Using smooth, even strokes in the direction of the wood grain, apply the polish to the wooden surface. Cover the entire surface methodically. Maintain consistent, light pressure.", "tip": "Going with the grain is more effective and gentler on the wood."}, {"title": "Buffing & Creating Shine", "description": "Once the surface has a light coat of polish, use the buffing cloth with firm, smooth strokes (still following the grain) to polish away excess and create a shine. The transformation is visible and satisfying.", "tip": "The emergence of shine is the reward that children find deeply satisfying."}, {"title": "Care of Materials & Storage", "description": "Wipe excess polish from cloths. Close the polish bottle securely. Hang cloths to dry. Return all materials to their proper storage places.", "tip": "Complete care of materials teaches responsibility for tools."}]',
  control_of_error = '["Streaks appear if too much polish applied (child sees them and adjusts)", "Uneven shine if buffing not thorough (child can feel the difference)", "Dull finish if not buffed enough (child learns through repetition)", "Cloudy appearance if polished against grain (child learns direction matters)"]',
  direct_aims = '["Learn proper wood-polishing technique", "Develop care for wooden materials in the environment", "Understand the sequence of polishing (apply, buff, shine)"]',
  materials = '["Wooden surface or furniture piece appropriate for child", "Furniture polish suitable for wood (non-toxic, child-safe)", "Polishing cloths (cotton, lint-free)", "Buffing cloths (soft, lint-free)", "Small dish or container for polish", "Oilcloth mat for workspace", "Apron (optional, as polish can stain)"]',
  parent_description = 'Wood polishing teaches children that natural materials deserve care and attention. The visible transformation from dull to lustrous is highly motivating, and the meditative quality of the work builds deep concentration and calm.',
  why_it_matters = 'This work combines fine motor development with environmental stewardship. Children learn that their careful, thoughtful actions can enhance beauty and preserve materials. The meditative, repetitive quality builds concentration while creating genuine value for the community.'
WHERE LOWER(name) = LOWER('Wood Polishing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Set out materials left to right: polish, stick, cotton, cloth, sponge", "Swirl metal object in polish using circular motions", "Watch tarnish transfer to cotton ball", "Buff with back-and-forth motion on cloth", "Dispose of used cotton, return items to shelf"]',
  presentation_steps = '[{"title": "Invitation with Context", "description": "Invite the child: ''This shiny metal piece has become tarnished. Let me show you how to make it shine beautifully again.'' Show the before\u2014the dull, tarnished metal.", "tip": "The dramatic before/after is visually motivating."}, {"title": "Organizing Materials in Sequence", "description": "Arrange all materials left to right on a mat: metal polish bottle (open), wooden stick or applicator, cotton ball, polishing cloth, and sponge for cleanup. Name each as you place it.", "tip": "The orderly arrangement is calming and teaches sequence naturally."}, {"title": "Introducing the Metal Object", "description": "Hold the tarnished metal object (spoon, decorative piece, etc.) and point out the tarnish\u2014the dark, dull coating. Explain: ''Polish will remove this and make it shine.''", "tip": "Children are fascinated by the visible tarnish and the promise of transformation."}, {"title": "Applying Polish with Circular Motion", "description": "Using the wooden stick (or directly with cotton), apply a small amount of polish to the metal. Using circular motions, work the polish into the tarnish. Show how the tarnish begins to transfer to the cotton ball, making it dark.", "tip": "The color transfer to the cotton is visually rewarding proof of the work happening."}, {"title": "Swirling & Working the Polish", "description": "Continue swirling in circular motions, occasionally showing the child the dark cotton ball: ''See? The tarnish is coming off.'' Change to a fresh cotton ball when needed and repeat the process.", "tip": "Multiple cotton balls may be needed; this is normal and expected."}, {"title": "Buffing with Back-and-Forth Motion", "description": "Once the major tarnish is removed, switch to the polishing cloth. Using quick back-and-forth motions (not circular), buff the metal surface vigorously. Show how the shine gradually emerges.", "tip": "The buffing motion is different from application; model it clearly."}, {"title": "Admiring the Transformation", "description": "Once polished to a high shine, hold the object up to the light. Point out the reflection and the transformation: ''We made it shine again! Look at your work!''", "tip": "This moment of pride cements the child''s desire to do the work again."}, {"title": "Care of Materials & Cleanup", "description": "Dispose of the soiled cotton ball. Wipe the metal object clean with a final cloth. Wipe up any excess polish. Close the polish bottle. Put all materials away.", "tip": "The complete care cycle teaches responsibility for tools and materials."}]',
  control_of_error = '["Tarnish remaining visible if polish not worked long enough (child can see it and continue)", "Streaks or dull spots from incomplete buffing (visible to child, who can fix it)", "Cloth becoming saturated with polish (child learns to use moderate amounts)", "Smudged appearance if not wiped clean after polishing (child sees the issue)"]',
  direct_aims = '["Learn metal-polishing technique with circular swirling and back-and-forth buffing", "Understand how tarnish is removed from metal objects", "Develop care and appreciation for metal items"]',
  materials = '["Metal polish (appropriate for the metal type)", "Wooden stick or polish applicator", "Cotton balls (several)", "Polishing cloths (lint-free)", "Metal object(s) to polish\u2014spoon, decorative piece, small sculpture", "Oilcloth mat for workspace", "Sponge or cloth for wiping excess polish", "Apron (optional, as polish can stain)"]',
  parent_description = 'Metal polishing is satisfying work because the results are visible and dramatic. Children see their effort directly transform a dull object into a gleaming one. This work teaches that care and attention restore beauty and preserve valuable items.',
  why_it_matters = 'This work develops persistence and attention to detail while building pride in contribution. The dramatic transformation from tarnished to shining teaches children that sustained effort produces visible results—a profound lesson about the value of work and care.'
WHERE LOWER(name) = LOWER('Metal Polishing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Arrange materials: dust cloth, glass cleaner, polishing cloths", "Dust glass object first to remove particles", "Spray or apply glass cleaner carefully", "Wipe thoroughly with circular motions", "Buff to clear shine with dry, lint-free cloth"]',
  presentation_steps = '[{"title": "Invitation & Showing the Need", "description": "Invite the child: ''This mirror has fingerprints and dust on it. Let me show you how to make it clear and shiny again.'' Point out the smudges and dust clearly.", "tip": "Children understand this work immediately\u2014they see dirty mirrors all the time."}, {"title": "Organizing Materials on Mat", "description": "Lay out materials left to right: damp cloth for dusting, spray glass cleaner or liquid cleaner in small container, dry polishing cloths (2-3), and final buff cloth. Place the glass object (mirror, framed glass) in the center.", "tip": "The orderly setup creates a sense of calm and preparedness."}, {"title": "Dusting Off Particles", "description": "Using a soft, slightly damp cloth, gently wipe the glass surface to remove dust and large particles. Use gentle circular motions. Show how the cloth picks up dust.", "tip": "Removing large particles first prevents scratching when applying cleaner."}, {"title": "Applying Glass Cleaner", "description": "Apply glass cleaner carefully\u2014either spray (with light misting) or apply with cloth. Don''t oversaturate. Show how the cleaner begins to break down fingerprints and smudges.", "tip": "Model careful, controlled application to avoid waste."}, {"title": "Wiping with Circular Motions", "description": "Using a clean, soft cloth, wipe the glass with firm, circular motions. Work across the entire surface methodically. Show how the smudges gradually disappear.", "tip": "The circular motion is more effective than random wiping."}, {"title": "Repeating if Necessary", "description": "If streaks remain, dampen the cloth slightly with cleaner and wipe again in circular motions. Repeat until the glass is clear.", "tip": "Multiple passes may be needed; this teaches persistence and thoroughness."}, {"title": "Final Buff to Clarity", "description": "Using a completely dry, lint-free cloth, buff the glass with firm, circular motions to remove any remaining streaks and create crystal clarity. Hold the glass up to the light to admire the clarity.", "tip": "The final buff creates the satisfying ''crystal clear'' appearance."}, {"title": "Care of Materials", "description": "Wipe cloths clean. Store all materials in their proper places. The workspace should be left clean and organized.", "tip": "The complete care cycle teaches responsibility and respect for tools."}]',
  control_of_error = '["Streaks visible if not buffed thoroughly (child sees them and continues buffing)", "Cloudy appearance from too much cleaner (child can feel the difference and adjust)", "Smudges remaining if first wipe not thorough (child can see and repeat process)", "Scratches if dried mud/grit not removed first (child learns the importance of dusting first)"]',
  direct_aims = '["Learn glass-polishing technique with proper dusting and buffing", "Understand how to care for and maintain glass surfaces", "Develop proper pressure and motion for streak-free cleaning"]',
  materials = '["Glass object\u2014mirror, window pane, or framed glass (child-safe size)", "Glass cleaner (mild, non-toxic, child-safe) or vinegar solution", "Soft cloths for dusting (damp)", "Polishing cloths (lint-free cotton or microfiber)", "Dry buff cloth (soft, clean, completely dry)", "Spray bottle (optional, for controlled application)", "Oilcloth mat for workspace", "Apron (optional)"]',
  parent_description = 'Glass polishing teaches children that clear surfaces make environments feel bright and open. The work is straightforward—even young children understand ''dirty'' and ''clean''—making it deeply satisfying. The clarity that emerges is rewarding motivation.',
  why_it_matters = 'This work combines practical care with visual satisfaction. Children see immediate results of their effort, understanding that their work directly improves the environment''s functionality and beauty. It teaches attention to detail and the value of thorough work.'
WHERE LOWER(name) = LOWER('Glass Polishing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Complete care routine for classroom plants: watering, dusting, pruning
• Develops responsibility for living things and scientific observation
• Teaches that living things have needs like water and light
• Child observes growth changes over time
• Connection to botany curriculum',
  presentation_steps = '[{"step": 1, "title": "Observation", "description": "''Let''s check if our plant needs water. Feel the soil.''", "tip": "Teach moisture testing."}, {"step": 2, "title": "Watering", "description": "If dry, demonstrate watering at base of plant.", "tip": "Not too much - drainage check."}, {"step": 3, "title": "Leaf Care", "description": "Dust leaves gently with soft cloth.", "tip": "Leaves breathe and need light."}, {"step": 4, "title": "Dead Leaf Removal", "description": "Remove yellow or dead leaves.", "tip": "Plant health maintenance."}, {"step": 5, "title": "Light Check", "description": "Observe if plant needs to be turned toward light.", "tip": "Plants grow toward light."}, {"step": 6, "title": "Recording", "description": "Older children may record observations in plant journal.", "tip": "Science integration."}]',
  control_of_error = 'Dry soil, wilting, dusty leaves',
  direct_aims = '["Care of living things", "Responsibility"]',
  materials = '["Plants", "Watering can", "Spray bottle", "Cloth for leaves", "Small shovel"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Plant Care');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Arranging cut flowers in a vase for classroom beautification
• Complete sequence: prepare vase, cut stems, arrange aesthetically
• Develops aesthetic sense and care for beauty
• Child contributes to classroom environment
• Grace and courtesy application',
  presentation_steps = '[{"step": 1, "title": "Material Preparation", "description": "Gather: vase, scissors, water pitcher, flowers, tray, cloth.", "tip": "Fresh flowers or foliage."}, {"step": 2, "title": "Vase Preparation", "description": "Fill vase with water to appropriate level.", "tip": "Not too full - stems need room."}, {"step": 3, "title": "Stem Cutting", "description": "Cut stem at angle under water for better water uptake.", "tip": "Adult may need to help with cutting."}, {"step": 4, "title": "Arrangement", "description": "Place flowers one at a time, considering height and color.", "tip": "Turn vase to check all sides."}, {"step": 5, "title": "Placement", "description": "Choose location for arrangement.", "tip": "Where it will bring beauty."}, {"step": 6, "title": "Maintenance", "description": "Change water, remove wilting flowers as needed.", "tip": "Ongoing responsibility."}]',
  control_of_error = 'Arrangement unbalanced',
  direct_aims = '["Aesthetic appreciation", "Sequencing"]',
  materials = '["Flowers", "Vase", "Scissors", "Pitcher", "Funnel", "Sponge", "Apron", "Newspaper"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Flower Arranging');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Check animal''s food and water daily", "Provide fresh food appropriate for the species", "Refresh water completely", "Clean living space gently", "Observe animal behavior with respect and interest"]',
  presentation_steps = '[{"title": "Introduction to the Animal", "description": "Introduce the classroom animal gently: ''This is [animal name]. We care for [him/her] together. Let me show you how to help [him/her] be happy and healthy.''", "tip": "Create a sense of partnership and responsibility from the beginning."}, {"title": "Observing the Animal", "description": "Before doing anything, sit quietly with the child and simply observe the animal. Watch its movements, behaviors, and habits. Ask questions: ''What do you see? How do you think it feels?''", "tip": "Observation builds empathy and connection before task-focused care."}, {"title": "Checking Food Supply", "description": "Show the child the food container. Explain: ''[Animal] needs fresh food every day to stay strong.'' Check together to see if food is present and if it looks fresh. Remove any old food.", "tip": "This teaches the daily rhythm of animal needs."}, {"title": "Providing Fresh Food", "description": "Show the appropriate food for the animal (hay, pellets, seed, vegetables\u2014depending on the animal). Measure the correct amount together. Place it in the food dish gently. Explain: ''[Animal] eats this to stay healthy.''", "tip": "Children should understand what the animal eats and why."}, {"title": "Checking & Refreshing Water", "description": "Check the water container. If water is present but old or dirty, empty it completely. Show the child how to fill a fresh, clean container with water at room temperature. Place it where the animal can access it easily.", "tip": "Explain: ''Fresh water is very important\u2014just like you need clean water to drink.''"}, {"title": "Gentle Cleaning of Living Space", "description": "If appropriate for the animal, show the child how to gently clean the enclosure. Remove soiled bedding and replace with fresh. Do this calmly and slowly, ensuring the animal feels safe.", "tip": "Move slowly and speak softly; animals respond to the child''s calm energy."}, {"title": "Respectful Interaction", "description": "Teach the child how to pet the animal gently (if appropriate) or observe it respectfully from a distance. Explain: ''We touch [animal] very gently and kindly. We respect [his/her] space and feelings.''", "tip": "Emphasize that the animal has its own needs and boundaries."}, {"title": "Closing the Care Cycle", "description": "Once care is complete, wash hands together. Reflect: ''We took good care of [animal] today. Now [animal] is comfortable, has food and water, and feels safe.''", "tip": "Closure reinforces the completeness of the care cycle."}]',
  control_of_error = '["Animal won''t eat if food is inappropriate or wrong amount (child learns animal preferences)", "Water becomes dirty if not changed regularly (child sees need for daily care)", "Animal seems stressed or unhappy if environment not clean (child feels responsibility to improve conditions)", "Child''s hands are wet/dirty if hygiene not practiced (natural consequence of forgetting)"]',
  direct_aims = '["Learn the daily care routine for a specific animal", "Understand animal needs (food, water, clean environment, safety)", "Develop responsibility for a living being"]',
  materials = '["Classroom animal appropriate for the age group (rabbit, guinea pig, fish, hamster, etc.)", "Food appropriate for the species", "Water container (clean, appropriate size)", "Food dish (appropriate size and material)", "Bedding or litter (if applicable)", "Cleaning supplies (small brush, dustpan, etc.)", "Hand soap for washing after care"]',
  parent_description = 'Classroom animals teach children that other living beings depend on them. This work builds genuine responsibility—not an abstract concept, but real, daily care with visible consequences. Children develop empathy and understand that their actions directly affect another creature''s wellbeing.',
  why_it_matters = 'Animal care is among the most important practical life works because it connects children to life itself. It teaches empathy, responsibility, and the understanding that all living things deserve respect and care. These lessons transfer directly to all future relationships and ethical development.'
WHERE LOWER(name) = LOWER('Animal Care');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up wash station with warm soapy water and clear water for rinsing
• Show how to wash each dish thoroughly
• Demonstrate proper rinsing to remove all soap
• Practice drying and putting away dishes
• Emphasize care to prevent breakage',
  presentation_steps = '[{"step": 1, "title": "Preparation and Safety", "description": "Set up a low table with two basins of water (warm soapy, then clear rinse water) and a drying area.", "tip": "Use unbreakable dishes initially or very sturdy ceramic. Ensure water is warm but not too hot."}, {"step": 2, "title": "Selecting Dishes", "description": "Show the child dishes that need washing and explain the washing process.", "tip": "Start with one or two dishes rather than a full load."}, {"step": 3, "title": "Scrubbing in Soapy Water", "description": "Demonstrate placing a dish in soapy water and using a soft sponge or cloth to scrub both sides thoroughly.", "tip": "Show all surfaces, including the back and rim of the dish."}, {"step": 4, "title": "Rinsing", "description": "Show how to transfer the washed dish to clear water and rinse thoroughly to remove all soap.", "tip": "Demonstrate rinsing under running water if available, or multiple rinses in clear water."}, {"step": 5, "title": "Drying and Storage", "description": "Show how to place the dish in a drying rack or wipe it with a clean cloth, then put it away properly.", "tip": "Teach the child where each dish belongs."}]',
  control_of_error = 'Clean dishes that sparkle demonstrate success. Remaining food, soap, or water spots show the need for more work.',
  direct_aims = '["To learn how to wash dishes", "To develop care of environment skills", "To understand cleanliness and order"]',
  materials = '["Two basins or sinks (warm soapy water and clear rinse water)", "Soft sponge or cloth for washing", "Dish soap", "Drying rack or cloth", "Unbreakable or sturdy dishes initially", "Apron for water protection"]',
  parent_description = 'Your child is learning to wash dishes, a practical care of environment skill. This activity develops hand strength, responsibility, and the understanding that everyone contributes to keeping the environment clean and orderly. Dish washing teaches independence and the satisfaction of meaningful work.',
  why_it_matters = 'Dish washing is a fundamental care of environment activity that teaches practical skills needed for managing daily life. It develops muscular strength through scrubbing motions, builds responsibility and independence, and demonstrates that individual efforts contribute to a clean, orderly environment. This activity also provides a sense of accomplishment and the understanding that everyone can contribute meaningfully to the care of their community.'
WHERE LOWER(name) = LOWER('Dish Washing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Fill basin with warm water and add mild soap", "Wet cloth item thoroughly", "Rub soap on cloth or scrub with brush", "Scrub along washboard in circular motions", "Rinse in clean water multiple times until clear"]',
  presentation_steps = '[{"title": "Introduction & Purpose", "description": "Invite the child: ''Let me show you how to wash clothes. When clothes get dirty, we wash them by hand to make them clean again.''", "tip": "Frame this as purposeful care, connecting to real-world skills."}, {"title": "Gathering Materials", "description": "Together, gather: large basin or sink, warm water (test temperature), mild soap or laundry detergent, cloth items to wash (small towels, aprons, napkins), washboard (optional), clean towels for drying.", "tip": "Let the child help gather; it builds investment in the work."}, {"title": "Filling Basin & Preparing Water", "description": "Fill the basin with warm (not hot) water. Add a small amount of mild soap. Stir to distribute. Explain: ''The soap helps remove dirt from the clothes.''", "tip": "Keep the water warm but safe enough that child can put hands in comfortably."}, {"title": "Submerging & Wetting Clothes", "description": "Place cloth item in the soapy water. Push it down gently, ensuring it''s completely submerged. Let it soak briefly, then pull it out and show how the water has absorbed into the fabric.", "tip": "Children find this tactile exploration interesting."}, {"title": "Scrubbing with Hands or Brush", "description": "Show two methods: (1) Rubbing the cloth between your hands with circular motions, or (2) Using a soft brush to scrub the cloth gently. Work especially on soiled areas. Demonstrate with focus and care.", "tip": "Gentle scrubbing is effective; harsh scrubbing damages fibers."}, {"title": "Using the Washboard (if available)", "description": "Show how to place the wet cloth on the washboard and rub it against the ridges using controlled motions. This removes stubborn dirt. Model the technique clearly.", "tip": "The washboard provides physical feedback and effectiveness."}, {"title": "First Rinse", "description": "Squeeze out soapy water from the cloth. Submerge it in a basin of clean water and work it through the water, squeezing repeatedly to remove soap.", "tip": "Multiple rinses are essential; residual soap leaves stiff, uncomfortable clothes."}, {"title": "Additional Rinses", "description": "Change the rinse water and repeat the process 2-3 more times until the water stays clear and there are no more bubbles when you squeeze the cloth.", "tip": "Thoroughly rinsed clothes feel soft and clean."}, {"title": "Wringing & Drying", "description": "Show the child how to hold the cloth over the rinse basin and gently wring out excess water using both hands. Squeeze slowly and deliberately, not harshly.", "tip": "Controlled wringing prevents damage and excess water on floors."}, {"title": "Hanging to Dry", "description": "Hang the washed cloth on a drying rack or clothesline. Spread it out so air can circulate and dry it evenly. Explain: ''Now we let the sun and air dry it.''", "tip": "This completes the full cycle of washing to dry."}]',
  control_of_error = '["Cloth still dirty if not scrubbed thoroughly enough (child sees it and scrubs again)", "Stiffness in cloth if soap not fully rinsed (child feels the difference)", "Water still soapy if not rinsed enough (child sees bubbles and rinses again)", "Water dripping if not wrung properly (child learns better technique)"]',
  direct_aims = '["Learn the hand-washing laundry sequence", "Understand how to clean cloth items thoroughly", "Develop proper scrubbing and rinsing techniques"]',
  materials = '["Large basin or access to sink", "Warm water (warm but safe for child)", "Mild laundry soap or castile soap", "Small cloth items (aprons, towels, napkins)", "Washboard (optional but helpful)", "Soft brush (optional)", "Clean water for rinsing (3-4 basins worth)", "Drying rack or clothesline", "Towel for drying workspace"]',
  parent_description = 'Hand washing laundry connects children to the work that keeps clothing clean. This real, necessary work teaches that effort produces results. Many children find the water and scrubbing satisfying and meditative.',
  why_it_matters = 'This work teaches practical life skills while building arm strength, concentration, and understanding of care. Children learn that clothes don''t clean themselves—their care keeps things comfortable and hygienic. It''s foundational to independence.'
WHERE LOWER(name) = LOWER('Laundry - Hand Washing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Start with smooth, square items (napkins, cloths)", "Fold in half lengthwise, then in half again", "Create neat, rectangular shapes", "Stack folded items in organized piles", "Progress to larger items as skill develops"]',
  presentation_steps = '[{"title": "Introduction & Showing the Need", "description": "Show the child a basket of clean, damp laundry. Explain: ''These clothes have been washed and dried. Now we fold them neatly to put away.''", "tip": "Connect folding to the complete care cycle."}, {"title": "Choosing the First Item", "description": "Select a small, square item: cloth napkin, washcloth, or dish towel. These are ideal starting points because they fold evenly.", "tip": "Starting with simple shapes builds confidence."}, {"title": "First Fold - Lengthwise", "description": "Hold the cloth with two hands, opposite corners. Show how to bring one long edge to meet the other long edge, creating a long rectangle. Press along the fold to crease it.", "tip": "The crease is satisfying and helps the next fold."}, {"title": "Second Fold - Bringing Ends Together", "description": "With the long rectangle before you, show how to bring one short end to meet the other short end, creating a smaller rectangle. Press this fold too.", "tip": "This creates the typical ''quarters'' fold."}, {"title": "Creating the Final Rectangle", "description": "Depending on size, fold again (in half) to create a final, compact rectangle. For small napkins, three folds might create the perfect size.", "tip": "Each fold becomes neater and more satisfying."}, {"title": "Checking the Appearance", "description": "Show the child the neatly folded cloth. Point out: ''See how straight it is? How smooth? That makes it easy to put away and find again.''", "tip": "Help the child see the beauty and purpose in neat folding."}, {"title": "Creating Stacks", "description": "Fold additional items of the same type. Stack them neatly on top of each other, creating an organized, visible pile. This provides visual reinforcement of accumulating work.", "tip": "The growing stack is motivating and shows progress."}, {"title": "Organizing Storage", "description": "Show the child where these folded items are stored. Place each stack in its designated location: napkins together, cloths together, etc. Explain the organization system.", "tip": "Understanding where things go teaches care and respect for belongings."}]',
  control_of_error = '["Crooked fold if not aligned properly (child can see it and refold)", "Uneven stacks if pieces aren''t folded consistently (child visually notes the unevenness)", "Wrinkles if not pressed during folding (child learns the importance of creasing)", "Disorganized storage if items not grouped (child experiences difficulty finding things)"]',
  direct_aims = '["Learn proper folding techniques for various items", "Develop spatial awareness and geometric understanding", "Understand organization and storage systems"]',
  materials = '["Clean, dry or nearly-dry laundry\u2014start with small items", "Large, flat surface (table or bed)", "Basket or container for organized stacks", "Storage locations (shelves, drawers) with labels or visual cues"]',
  parent_description = 'Folding laundry teaches organization and care. While it might seem simple, the precision required—aligning corners, creating straight lines, consistent size—develops fine motor skills and spatial awareness. It''s also deeply satisfying to create order from disorder.',
  why_it_matters = 'This work combines practical care with mathematical thinking (geometry, sequencing). Children learn that organized systems make life easier and more beautiful. The meditative quality of repetitive folding builds concentration and calm.'
WHERE LOWER(name) = LOWER('Folding Laundry');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Make eye contact and smile", "Use appropriate greeting word (hello, good morning)", "Stand with good posture", "Offer hand for handshake if appropriate", "Practice across multiple interactions daily"]',
  presentation_steps = '[{"title": "Introduction & Modeling", "description": "Gather a small group. Explain: ''I''m going to show you how to greet someone. A greeting is how we say hello and show we''re happy to see them.''", "tip": "Make this warm and positive, not rigid or awkward."}, {"title": "Demonstrating Eye Contact & Smile", "description": "Face the child. Make direct eye contact. Smile warmly. Hold it for a moment. Explain: ''When we greet someone, we look at them and smile. This shows we like them.''", "tip": "Model genuine warmth, not forced or artificial."}, {"title": "Saying the Greeting", "description": "With sustained eye contact and smile, say clearly: ''Good morning'' or ''Hello.'' Ask the child to repeat. Practice this exchange back and forth: You: ''Good morning.'' Child: ''Good morning.''", "tip": "Repetition makes the habit automatic."}, {"title": "Adding a Handshake", "description": "Show how to extend your right hand for a handshake. Demonstrate a firm (but gentle) grip, eyes still on the person, slight smile still present. Practice the full greeting with handshake: eye contact, smile, greeting, handshake.", "tip": "The handshake is optional but teaches respect and confidence."}, {"title": "Practicing Together", "description": "Have the child practice with you first. Then invite them to greet classmates. Praise each attempt: ''I saw you smile at [classmate]. Great greeting!''", "tip": "Positive reinforcement builds the habit faster than correction."}, {"title": "Real-World Application", "description": "Throughout the day, when appropriate, greet the child in the morning: ''Good morning! I''m happy to see you.'' Watch to see if they initiate greetings with others.", "tip": "Children naturally absorb patterns they see modeled consistently."}]',
  control_of_error = '["If no eye contact, the greeting feels incomplete (child eventually learns this naturally)", "If smile is missing, greeting seems cold (child learns warmth matters)", "If voice is too quiet, greeting might not be heard (child learns to speak clearly)", "If child doesn''t respond when greeted, they naturally miss the connection"]',
  direct_aims = '["Learn proper greeting technique including eye contact, smile, and appropriate words", "Develop confidence in social interactions"]',
  materials = '["Simply people to greet", "A calm, intentional approach"]',
  parent_description = 'Greetings are the foundation of courtesy. A warm, genuine greeting sets the tone for positive relationships. Children who learn to greet genuinely develop stronger social connections and greater confidence in social situations.',
  why_it_matters = 'Greetings teach children that other people matter and that they have power to affect others'' emotions positively. This simple skill builds the foundation for all future relationships and community participation.'
WHERE LOWER(name) = LOWER('Greetings');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Use proper introduction formula", "Make eye contact with both people", "Speak clearly and with respect", "Encourage handshake or appropriate greeting", "Practice with new people or classroom visitors"]',
  presentation_steps = '[{"title": "Introduction & Purpose", "description": "Explain: ''Sometimes we meet someone new. Let me show you how to introduce two people to each other. This helps them become friends.''", "tip": "Frame introductions as a gift you''re giving to both people."}, {"title": "Learning the Introduction Formula", "description": "Teach the basic formula: ''Person A, this is Person B. Person B, this is Person A.'' Or: ''I''d like you to meet [name]. [Name], this is [name].''", "tip": "Keep it simple and memorable."}, {"title": "Demonstrating with Role-Play", "description": "Use two adults or children to demonstrate. Face both people and speak to both: ''[Child''s name], I''d like you to meet [Visitor''s name]. [Visitor''s name], this is [Child''s name]. They love building with blocks.'' Add a relevant detail about each person.", "tip": "Adding a detail about each person helps them start a conversation."}, {"title": "Practicing with You", "description": "Introduce a third adult to the child. Role-play introducing them: ''Let me introduce you to [teacher]. [Teacher], this is [child]. [Child] is an excellent mathematician.''", "tip": "Practice multiple times with different scenarios."}, {"title": "Supporting First Introductions", "description": "When a new person arrives in class, invite the child to help introduce them to others. Stand nearby to support and model if needed. Celebrate their efforts.", "tip": "Real situations provide powerful learning opportunities."}, {"title": "Encouraging Conversation Starters", "description": "After an introduction, prompt the child with conversation starters: ''You could ask [person] about...'', ''You might tell [person] about...''", "tip": "This bridges introduction into actual connection."}]',
  control_of_error = '["If introduction is unclear, the people might not know each other''s names (child sees confusion and clarifies)", "If introduction forgets one person, it''s awkward (child naturally learns completeness matters)", "If done without warmth, people don''t connect well (child learns warmth matters)"]',
  direct_aims = '["Learn proper introduction technique", "Develop confidence in facilitating new relationships"]',
  materials = '["People to introduce", "Calm, welcoming environment", "Your own modeling and support"]',
  parent_description = 'Learning to introduce people is a valuable social skill. Children who can comfortably introduce others gain confidence and become bridges in their communities. This skill serves them throughout life in school, work, and social settings.',
  why_it_matters = 'Introductions teach children that they have power to create connections. Understanding how to facilitate relationships builds social confidence and generosity of spirit—they''re giving the gift of connection to others.'
WHERE LOWER(name) = LOWER('Introductions');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Proper use of polite words in daily interactions
• Includes please, thank you, you''re welcome, excuse me
• Grace and courtesy foundation for all social situations
• Role-play and modeling for natural integration
• Develops appreciation and respectful communication',
  presentation_steps = '[{"step": 1, "title": "Modeling", "description": "Teacher models using please and thank you throughout day.", "tip": "Children absorb from environment."}, {"step": 2, "title": "Lesson Introduction", "description": "''Magic words make people feel good. Let me show you.''", "tip": "Demonstrate with another adult."}, {"step": 3, "title": "Role Play: Please", "description": "Practice asking for things using please.", "tip": "''May I please have...''"}, {"step": 4, "title": "Role Play: Thank You", "description": "Practice responding with thank you when receiving.", "tip": "Eye contact while thanking."}, {"step": 5, "title": "You''re Welcome", "description": "Complete the exchange with you''re welcome.", "tip": "Full gracious exchange."}, {"step": 6, "title": "Integration", "description": "Gentle reminders during real situations.", "tip": "Whisper prompts if needed."}]',
  control_of_error = 'Social feedback',
  direct_aims = '["Courtesy", "Communication"]',
  materials = '["Role-play scenario cards", "Puppets or dolls (optional)", "Picture books about manners", "Thank you note materials", "Small items for practicing giving and receiving"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Please and Thank You');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Use when needing to pass or interrupt gently", "Speak softly and politely", "Wait for acknowledgment before proceeding", "Thank the person for listening", "Practice respectful interruption"]',
  presentation_steps = '[{"title": "Introduction & Context", "description": "Explain: ''Sometimes we need to pass someone or get their attention. We use special words to be respectful: Excuse me.''", "tip": "Connect this to respect for others'' space and time."}, {"title": "Demonstrating the Words", "description": "Say ''Excuse me'' gently and clearly. Model the exact tone: not demanding, not rude, just a polite request for attention. Have the child repeat several times.", "tip": "The tone matters as much as the words."}, {"title": "Scenario: Passing Someone", "description": "Act out a scenario: You''re standing in a doorway. The child needs to pass. They say ''Excuse me.'' You move. They say ''Thank you.'' Show: request \u2192 response \u2192 gratitude.", "tip": "The complete sequence teaches full courtesy."}, {"title": "Scenario: Needing Attention", "description": "Act out another scenario: You''re busy. The child approaches and says ''Excuse me'' politely. You acknowledge them. They share their question/need. Show attentiveness to a polite request.", "tip": "Children learn that polite requests get positive responses."}, {"title": "Scenario: Interrupting Work", "description": "Simulate a child working. The child who''s practicing approaches and says ''Excuse me, I need help.'' Show respect for the working child''s concentration and graceful pause/response.", "tip": "This teaches respecting others'' focus while still allowing necessary interruption."}, {"title": "Real-World Practice", "description": "Throughout the day, point out when ''excuse me'' would be helpful: ''That was a good time to say excuse me.'' Praise actual usage: ''I heard you say excuse me politely. Thank you.''", "tip": "Real-life modeling and praise embed the habit."}]',
  control_of_error = '["If not said clearly, they''re not heard (child learns to speak clearly and repeat if needed)", "If said rudely or demandingly, response is less positive (child learns tone matters)", "If said without waiting for response, they interrupt anyway (child learns to wait)"]',
  direct_aims = '["Learn polite language for interrupting and passing", "Develop respect for others'' space and time"]',
  materials = '["People to practice with", "Scenarios to role-play", "Your consistent modeling and response"]',
  parent_description = 'Teaching ''excuse me'' teaches children that other people''s space, time, and attention matter. This simple phrase, used consistently, shows respect and generally gets positive responses. It''s a building block for successful social interaction.',
  why_it_matters = 'This work teaches children to balance their own needs with respect for others. They learn that politeness is powerful—people are more willing to help and accommodate polite requests. This directly impacts their ability to navigate social and professional situations throughout life.'
WHERE LOWER(name) = LOWER('Saying Excuse Me');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Approach gently with ''excuse me''", "Wait for appropriate moment", "Make brief, clear statement of need", "Show appreciation for interruption acceptance", "Practice with real-world scenarios"]',
  presentation_steps = '[{"title": "Understanding When to Interrupt", "description": "Explain: ''Sometimes we have something important to say or need help. We can interrupt, but we do it politely and carefully.''", "tip": "Normalize that some interruptions are necessary and okay."}, {"title": "Demonstration of Polite Approach", "description": "Role-play: You''re teaching a small group. The child approaches, says ''Excuse me'' quietly, and waits. You acknowledge them. They state their need briefly. You help or listen.", "tip": "Model the waiting\u2014sometimes a teacher needs a moment before responding."}, {"title": "Finding the Right Moment", "description": "Teach the child to look for a pause or moment of transition: when you''re finishing a sentence, looking up from writing, etc. Explain: ''We interrupt when there''s a small break, not in the middle of someone''s words.''", "tip": "This develops awareness and respect for others'' rhythm."}, {"title": "Stating the Need Clearly & Briefly", "description": "Practice what to say: ''I need help with the puzzle.'' ''May I have a tissue?'' Keep it short. Model this repeatedly.", "tip": "Brief statements are more likely to get positive responses."}, {"title": "Accepting a ''Just a Moment'' Response", "description": "Teach the child to accept: ''I''m finishing this. I''ll help you in a moment.'' This is still a positive response. Wait patiently without repeated interrupting.", "tip": "Children learn that waiting is part of respecting others'' time."}, {"title": "Expressing Gratitude", "description": "Model: ''Thank you for helping me'' or ''Thank you for listening.'' This closes the interaction gracefully and shows appreciation.", "tip": "Gratitude teaches that the person did something kind by responding."}, {"title": "Real-World Practice & Feedback", "description": "Throughout the day, notice and praise polite interruptions: ''I noticed you said excuse me and waited for me to finish. Thank you.'' Redirect gently if needed.", "tip": "Specific praise teaches exactly what behavior you want to see repeated."}]',
  control_of_error = '["Interrupting rudely gets a negative response (child learns politeness matters)", "Interrupting during someone''s sentence causes frustration (child learns timing matters)", "Not waiting for acknowledgment means the message isn''t received (child learns to wait)"]',
  direct_aims = '["Learn appropriate interruption technique", "Develop respect for others'' time and attention"]',
  materials = '["People to practice interrupting", "Real situations (child needing help, having question)", "Your consistent modeling and response"]',
  parent_description = 'Learning to interrupt respectfully teaches children to balance their needs with respect for others. It''s a crucial skill for school and life—knowing how to ask for help appropriately increases their success and confidence.',
  why_it_matters = 'This work teaches children that their needs matter AND that others'' time matters. They learn to advocate for themselves while respecting others—a balance essential for healthy relationships and social success throughout life.'
WHERE LOWER(name) = LOWER('How to Interrupt');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Notice when someone needs help", "Offer assistance politely and respectfully", "Accept help graciously when offered", "Say thank you genuinely", "Understand different needs different help"]',
  presentation_steps = '[{"title": "Introduction & Importance", "description": "Explain: ''We all need help sometimes. Today I''m showing you how to offer help kindly and how to accept help with appreciation.''", "tip": "Frame help as natural and mutual, not weakness."}, {"title": "Recognizing When Help Is Needed", "description": "Point out situations: ''See how [child] is struggling with the block tower? That looks like a moment when help would be welcome.'' Teach observation of difficulty or frustration.", "tip": "Empathy and observation are prerequisites for offering help."}, {"title": "Offering Help Respectfully", "description": "Model: ''Would you like help?'' or ''May I help you?'' or ''Can I give you a hand?'' Not commanding (''Let me do it'') but offering choice.", "tip": "Respecting autonomy even when offering help is crucial."}, {"title": "Waiting for a Response", "description": "Show that sometimes the answer is ''yes'' and sometimes ''no, thank you, I''m practicing.'' Respect both answers. Explain: ''Sometimes people want to do it themselves. That''s okay.''", "tip": "Children learn that practice and independence matter to others too."}, {"title": "Giving Help Gracefully", "description": "If help is accepted, show how to help without taking over: assist with one part, encourage the person to complete the rest. Work alongside, not for them.", "tip": "This preserves their learning and dignity."}, {"title": "Accepting Help Graciously", "description": "Model receiving help: ''Thank you so much for helping me. That made it much easier.'' Say it warmly and genuinely.", "tip": "Showing gratitude teaches the helping person their action was valued."}, {"title": "Declining Help Respectfully", "description": "Model: When offered help, if you don''t need it: ''Thank you for offering. I''ve got it, but I appreciate you asking.'' This honors the offer without accepting it.", "tip": "Declining respectfully is also courteous."}, {"title": "Real-World Observation", "description": "Throughout the day, notice and name what''s happening: ''I saw you offer to help [child] clean up. That was kind.'' ''I saw [child] accept your help and say thank you. Beautiful.''", "tip": "Specific observation reinforces the behavior you want to see."}]',
  control_of_error = '["If help is offered bossily, it''s rejected (child learns politeness matters)", "If help is taken away entirely, the person doesn''t learn (child learns to assist, not take over)", "If gratitude isn''t expressed, the helper feels unappreciated (child learns to thank)"]',
  direct_aims = '["Learn to offer help respectfully", "Learn to accept help with grace and appreciation", "Develop empathy and observation skills"]',
  materials = '["Real situations where help is needed", "Your own modeling and narration", "A classroom culture that values mutual support"]',
  parent_description = 'Learning to offer and accept help graciously teaches children that interdependence is healthy. They learn that community means supporting each other, and that receiving help is an opportunity to learn gratitude. This builds strong relationships throughout life.',
  why_it_matters = 'This work teaches that people are interconnected and that we help each other. It normalizes receiving help (preventing shame or resistance to assistance) while teaching the joy of giving help. It builds a foundation for healthy relationships and community.'
WHERE LOWER(name) = LOWER('Offering and Accepting Help');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Recognize when you''ve done something unkind or hurtful", "Approach the person sincerely", "Say ''I''m sorry'' and name what happened", "Show understanding of impact on the other person", "Make amends if possible"]',
  presentation_steps = '[{"title": "Introduction & Honesty", "description": "Explain: ''Sometimes we do something that hurts someone''s feelings or isn''t kind. We can fix it by apologizing. An apology is honest and kind.''", "tip": "Frame apology as strength, not weakness."}, {"title": "Recognizing the Mistake", "description": "Point out moments: ''I notice you knocked over [child''s] tower. I can see they''re upset. That''s a time to apologize.'' Help the child recognize cause and effect.", "tip": "Developing awareness is the first step."}, {"title": "Demonstrating a Sincere Apology", "description": "Model: Approach the person. Make eye contact. Say: ''I''m sorry I knocked over your tower. I can see you worked hard on it and now it''s broken. That wasn''t kind.''", "tip": "Sincere apology includes acknowledgment of impact, not just words."}, {"title": "Showing Understanding of Impact", "description": "Help the child understand what they caused: sadness, frustration, extra work, broken trust, embarrassment. Say: ''I see how that made you feel...''", "tip": "Empathy is the heart of a sincere apology."}, {"title": "Making Amends", "description": "Ask: ''What can I do to make it better?'' Help rebuild the tower, replace the broken item, or do something kind to show the apology is genuine.", "tip": "Actions speak louder than words; making amends shows sincerity."}, {"title": "Accepting Forgiveness", "description": "If the person accepts the apology, acknowledge it: ''Thank you for forgiving me.'' If they''re not ready, respect that too: ''I understand. I''m still sorry.''", "tip": "The other person gets to decide when they''re ready to forgive."}, {"title": "Not Repeating the Mistake", "description": "The most sincere apology is followed by changed behavior. Observe and celebrate when the child avoids repeating the mistake: ''I noticed you were careful around the tower this time. Thank you.''", "tip": "Follow-through matters more than words."}]',
  control_of_error = '["If apology is insincere (''Sorry, but...''), the person remains upset (child learns sincerity matters)", "If no amends are made, trust isn''t fully restored (child learns actions matter)", "If the behavior repeats, the apology seemed false (child learns consistency matters)"]',
  direct_aims = '["Learn to apologize sincerely", "Understand that apologies repair relationships", "Develop accountability for one''s actions"]',
  materials = '["Real situations where apologies are needed", "Your modeling of sincere apology", "A classroom culture that normalizes mistakes and repair"]',
  parent_description = 'Teaching children to apologize sincerely teaches them that mistakes can be repaired. They learn that acknowledging harm and making amends strengthens relationships rather than weakening them. This is crucial for healthy relationships throughout life.',
  why_it_matters = 'This work teaches that accountability and sincerity matter. Children learn that mistakes are part of being human, and that how you respond to mistakes defines your character. This builds emotional maturity, resilience, and the capacity for genuine relationships.'
WHERE LOWER(name) = LOWER('Apologizing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to sit properly at a table with food
• Demonstrate using utensils correctly
• Teach chewing with mouth closed
• Practice napkin use and wiping hands
• Emphasize respectful, quiet eating behaviors',
  presentation_steps = '[{"step": 1, "title": "Preparing for the Meal", "description": "Explain that table manners show respect for food and others eating with us.", "tip": "Set a properly prepared table with napkins and appropriate utensils."}, {"step": 2, "title": "Sitting Properly", "description": "Show how to sit upright at the table with good posture.", "tip": "Demonstrate that sitting well allows for comfortable eating and conversation."}, {"step": 3, "title": "Using Utensils", "description": "Demonstrate holding and using utensils (spoon, fork, knife) appropriately.", "tip": "Show how to use the proper utensil for different foods."}, {"step": 4, "title": "Eating Respectfully", "description": "Show how to take bites of appropriate size, chew with mouth closed, and avoid talking with mouth full.", "tip": "Explain that these behaviors show respect and consideration for others."}, {"step": 5, "title": "Wiping and Cleanup", "description": "Demonstrate using a napkin to wipe mouth and hands, and clearing one''s place.", "tip": "Show that leaving the table clean is part of respectful eating."}]',
  control_of_error = 'Positive responses from others and a pleasant mealtime atmosphere show successful table manners. Discomfort or negative reactions show the need for adjustment.',
  direct_aims = '["To learn proper table manners and etiquette", "To develop respectful mealtime behavior", "To understand the importance of courtesy during meals"]',
  materials = '["A table set for eating", "Appropriate utensils (spoon, fork, knife)", "Napkins", "Food appropriate for demonstrating manners", "Optional: pictures or models of proper table setting"]',
  parent_description = 'Your child is learning table manners, skills that demonstrate respect and create pleasant mealtimes. Proper table etiquette shows consideration for others and builds confidence in social dining situations. These practical skills will serve your child throughout their entire life.',
  why_it_matters = 'Table manners is a fundamental grace and courtesy lesson that teaches children to eat respectfully and considerately. Learning proper mealtime behavior demonstrates respect for food, others, and the meal itself. These skills create pleasant mealtimes, build confidence in social eating situations, and teach children that courtesy and consideration enhance everyone''s enjoyment of meals and community.'
WHERE LOWER(name) = LOWER('Table Manners');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Gather appropriate items: placemats, dishes, utensils, napkins", "Arrange placemats in organized placement", "Place dishes and utensils in proper positions", "Add napkins and water glasses", "Check alignment and organization"]',
  presentation_steps = '[{"title": "Introduction & Purpose", "description": "Explain: ''Setting the table is how we prepare a beautiful, organized space for eating together. It shows care for the people who will eat here.''", "tip": "Connect table-setting to community and hospitality."}, {"title": "Gathering Materials", "description": "Together, gather: placemats, plates/bowls, napkins, utensils (forks, spoons, knives\u2014appropriately sized for age), water glasses, and any centerpiece.", "tip": "Let the child help gather; it builds understanding of what''s needed."}, {"title": "Placing Placemats", "description": "Show how to place placemats evenly spaced around the table, with equal spacing between. Align them parallel to the table edge. This creates visual order.", "tip": "The geometric precision is visually satisfying."}, {"title": "Placing Plates/Bowls", "description": "On each placemat, center a plate or bowl. Explain: ''The plate goes in the middle. This is where the food goes. It''s the center of the place setting.''", "tip": "Center alignment is calming and beautiful."}, {"title": "Placing Napkins", "description": "Show how to place a napkin to the left of the plate or on top of it. Explain: ''The napkin keeps us clean. It goes where it''s easy to reach.''", "tip": "Different placements (left, on plate) are both acceptable."}, {"title": "Placing Utensils Correctly", "description": "Place forks on the left side of plate, spoons and knives on the right (knife blade facing inward). Explain the order: ''We use utensils from the outside in, so the fork is on the outside left.''", "tip": "This teaches proper table etiquette naturally through placement."}, {"title": "Adding Water Glass", "description": "Place water glass above the plate, toward the right (where the knife is). Show the placement clearly. Explain: ''We have water to drink with our meal.''", "tip": "The glass placement completes the formal setting."}, {"title": "Adding a Centerpiece (Optional)", "description": "If appropriate, add a simple centerpiece to the center of the table: flowers, a candle, a small plant. Explain: ''This makes the table beautiful for our meal together.''", "tip": "Beauty matters; it shows we''re creating something special."}, {"title": "Checking the Work", "description": "Step back and look at the completed table. Are placemats evenly spaced? Are dishes centered? Are utensils aligned? Make adjustments as needed. Celebrate: ''What a beautiful table we''ve set!''", "tip": "Reflection builds quality consciousness."}]',
  control_of_error = '["Uneven spacing is visible (child sees it and adjusts)", "Off-center dishes look wrong (child develops eye for alignment)", "Utensils in wrong position are uncomfortable to use (child learns correct placement through use)"]',
  direct_aims = '["Learn proper table-setting technique", "Understand table etiquette and formal dining"]',
  materials = '["Placemats", "Dishes and bowls (child-sized or full-sized, depending on age)", "Napkins", "Utensils (forks, spoons, knives\u2014appropriately sized)", "Water glasses", "Optional: centerpiece (flowers, small plant, candle)", "Table to set"]',
  parent_description = 'Setting a beautiful table teaches children that creating a welcoming, organized space matters. It''s a tangible way they contribute to family meals, and it teaches respect for gathering together. Many families have traditions around table-setting and meals.',
  why_it_matters = 'This work combines practical skill with aesthetic awareness and hospitality. Children learn that their actions create the environment for community gathering. Setting a table is a small act of care that makes everyone''s experience more pleasant and respectful.'
WHERE LOWER(name) = LOWER('Setting the Table');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Wait until work is in progress or complete", "Watch quietly without interrupting", "Make brief, genuine observation or compliment", "Don''t offer help unless asked", "Respect the person''s concentration"]',
  presentation_steps = '[{"title": "Introduction & Why It Matters", "description": "Explain: ''When someone is working, we can look at what they''re doing. We watch quietly and don''t interrupt. This shows we respect their work.''", "tip": "Frame observation as a form of respect and courtesy."}, {"title": "Demonstrating Quiet Observation", "description": "Have a child do a work (building, drawing, puzzle). Approach quietly, stand nearby where they can see you, and watch without talking. Maintain a calm, interested expression.", "tip": "Show through your body language that you respect their focus."}, {"title": "Waiting for an Appropriate Moment", "description": "Show that you wait until there''s a natural pause or completion. Don''t interrupt mid-task. When they look up or finish, then you can comment.", "tip": "Teaching children to read social cues about when to speak."}, {"title": "Making a Genuine Observation", "description": "Comment briefly and authentically: ''I noticed you used a lot of red and blue together. They look beautiful.'' or ''You worked on that puzzle for a long time. You were very patient.''", "tip": "Specific, genuine observations are more meaningful than generic praise."}, {"title": "Not Offering Unsolicited Help", "description": "Show that you don''t suggest improvements or offer help unless asked. Just observe and appreciate. If they ask, then you can help.", "tip": "This respects their autonomy and the value of their learning."}, {"title": "Showing Respect for Concentration", "description": "Demonstrate by leaving them to continue their work. Don''t linger or distract. Your brief observation honors their focus without breaking it.", "tip": "Respecting work time is a form of courtesy."}, {"title": "Real-World Practice", "description": "Point out when you see this happening: ''I noticed [child] was watching [other child] work on the painting. They were so quiet and respectful.'' Praise the observer for honoring the worker''s space.", "tip": "Specific recognition makes the behavior more likely to continue."}]',
  control_of_error = '["If observation is interrupted and disruptive, the worker gets annoyed (child learns quiet matters)", "If unsolicited suggestions are offered, the worker might feel criticized (child learns to wait for asking)", "If work is rushed or distracted by observation, quality suffers (child learns timing matters)"]',
  direct_aims = '["Learn to observe others'' work respectfully", "Understand what respectful observation looks like"]',
  materials = '["Children engaged in various works", "Your own modeling of respectful observation", "A classroom culture that values concentration"]',
  parent_description = 'Teaching children to observe respectfully teaches them to value others'' processes and concentration. It''s a quiet form of respect that shows genuine interest without intrusion. This skill supports both the observer and the observed.',
  why_it_matters = 'This work teaches that being present and attentive is a form of respect and kindness. Children learn that they can support others'' learning through quiet, genuine appreciation—without needing to do anything or say much. It builds the foundation for respectful, contemplative presence.'
WHERE LOWER(name) = LOWER('Observing Another''s Work');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Recognize that someone''s work takes up floor space", "Look at what they''re doing before moving near them", "Walk around their work at a respectful distance", "Never step on or disturb their materials", "Practice graceful, aware navigation"]',
  presentation_steps = '[{"title": "Introduction & Awareness", "description": "Explain: ''In a classroom, many children are working all around. We need to walk carefully so we don''t disturb anyone''s work.''", "tip": "Frame this as shared responsibility for the community space."}, {"title": "Demonstrating Awareness", "description": "Set up a work on the floor (art, block building, puzzle). Walk past it casually. Stop and point it out: ''Look, there''s work here. We need to go around it.''", "tip": "Make observation explicit so children develop the habit."}, {"title": "Determining the Work Boundary", "description": "Show where the work is and where you can safely walk: ''The work is here [point]. The safe space to walk is here [point].''", "tip": "Visual clarity helps children understand spatial relationships."}, {"title": "Demonstrating the Graceful Walk", "description": "Walk slowly and carefully around the work, giving it plenty of space. Show awareness of your body and feet. Move smoothly, not hurried.", "tip": "Model the exact movement you want to see\u2014graceful and mindful."}, {"title": "Practicing Together", "description": "Set up a work. Invite the child to walk around it. Give gentle guidance: ''Wider. Yes, nice and wide. Beautiful.'' Praise successful navigation.", "tip": "Hands-on practice embeds the behavior."}, {"title": "Real-World Observation", "description": "Throughout the day, notice and praise graceful walking: ''I saw you walk around [child''s] work. You gave them lots of space. Thank you for being respectful.''", "tip": "Specific praise reinforces the behavior."}, {"title": "Gentle Redirection if Needed", "description": "If a child steps near work: ''Let''s go around [work]. We don''t want to disturb it.'' Show the wider path and walk alongside them.", "tip": "Gentle modeling is more effective than correction."}]',
  control_of_error = '["If work is disturbed, the child doing it becomes frustrated (natural consequence teaches awareness)", "If materials are kicked or scattered, the child has to remake their work (teaches the importance of careful walking)", "If someone''s work is stepped on, they''re upset (child learns the impact of carelessness)"]',
  direct_aims = '["Learn to navigate around others'' work respectfully", "Develop awareness of shared classroom space"]',
  materials = '["Classroom with works on floor or at table edge", "Your own modeling and mindful walking", "A physical environment with natural walking paths around work areas"]',
  parent_description = 'Teaching children to walk around others'' work teaches spatial awareness and respect. It shows them that their actions impact others and that shared spaces require consideration. This develops the foundation for respectful community participation.',
  why_it_matters = 'This work teaches that awareness matters. As children develop the habit of observing, considering, and moving mindfully, they naturally become more respectful and less destructive. It builds the foundation for grace, consideration, and community responsibility.'
WHERE LOWER(name) = LOWER('Walking Around Someone''s Work');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Recognize when materials are shared or limited", "Wait patiently for your turn", "Respect the person currently using materials", "Take your turn gracefully, aware of waiting person", "Return materials promptly"]',
  presentation_steps = '[{"title": "Introduction & Context", "description": "Explain: ''In our classroom, there''s often only one of each special work. We take turns. That''s how everyone gets a chance.''", "tip": "Frame turns as fair and necessary, not as deprivation."}, {"title": "Understanding Turn-Taking", "description": "Demonstrate with two children: One has the work. Point out: ''Now it''s [child A''s] turn. We wait for them to finish, then it''s [child B''s] turn.''", "tip": "Make the sequence visible and clear."}, {"title": "Waiting Patiently", "description": "Show how to wait: Find something else to do. Check back periodically. Don''t rush the person. Develop the habit of patience by waiting together.", "tip": "Waiting is a skill that improves with practice."}, {"title": "Observing Someone Else''s Turn", "description": "Point out: ''While we''re waiting, we can watch. We can learn from seeing how [child] works. We''re patient and respectful.''", "tip": "Observation while waiting develops both learning and patience."}, {"title": "Taking Your Turn With Awareness", "description": "When it''s the waiting child''s turn, ask: ''Did you see how carefully [first child] used the material? Let''s use it the same way.'' This shows respect for the material and the next waiting person.", "tip": "Awareness that someone is waiting often makes children take better care."}, {"title": "Finishing and Passing Along", "description": "When done, put the work away properly (don''t leave it for the next person). Then tell the waiting person: ''It''s ready for you now.'' Pass along care and respect.", "tip": "Graceful passing makes turn-taking feel good to everyone."}, {"title": "Real-World Recognition", "description": "Notice and name what''s happening: ''I saw you waiting so patiently while [child] had the map. Then you took a beautiful turn. Thank you for being such a respectful member of our community.''", "tip": "Specific recognition of both waiting and turn-taking is powerful."}]',
  control_of_error = '["If someone takes too long, the waiting person becomes frustrated (natural consequence teaches consideration of time)", "If materials are returned in poor condition, the next person is unhappy (child learns to care for shared items)", "If turns aren''t respected, conflict arises (child learns fairness matters)"]',
  direct_aims = '["Learn to wait patiently for a turn", "Understand fairness and sharing", "Develop awareness that others are waiting"]',
  materials = '["Limited, highly desirable materials (special work, popular item)", "A classroom system for managing turns (chart, verbal understanding, etc.)", "Your own patience modeling"]',
  parent_description = 'Teaching children to share and take turns teaches patience and fairness. In a classroom with limited materials, this is essential. Children learn that waiting isn''t punishment—it''s how fair communities work. This builds empathy and community responsibility.',
  why_it_matters = 'This work teaches fundamental social and emotional skills: patience, empathy, fairness, and delayed gratification. Children learn that they''re part of a community where everyone''s needs matter, including their own. This builds the foundation for healthy relationships and social participation throughout life.'
WHERE LOWER(name) = LOWER('Sharing and Taking Turns');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Gather fresh produce and place in basin", "Fill basin with cool, clean water", "Gently rub each item to remove dirt", "Rinse under running water thoroughly", "Pat dry and place on clean towel"]',
  presentation_steps = '[{"title": "Introduction & Purpose", "description": "Explain: ''We wash fruits and vegetables to remove dirt and keep them safe to eat. Let me show you how.''", "tip": "Connect washing to food safety and care."}, {"title": "Gathering Produce", "description": "Together, gather produce: apples, carrots, tomatoes, cucumbers\u2014whatever''s appropriate for the season. Place them in a large, shallow basin.", "tip": "Let the child select items; it builds interest in the food."}, {"title": "Filling the Basin with Water", "description": "Fill the basin with cool, clean water. Show the water level: ''Enough to cover the produce, but not so much it overflows.'' Demonstrate water pouring.", "tip": "Children find the water work enjoyable and meaningful."}, {"title": "Gently Washing Each Item", "description": "Take one piece of produce. Show how to rub it gently under your fingers in the water, turning it to wash all sides. Explain: ''Gentle rubbing removes dirt without damaging the food.''", "tip": "Model the exact gentle motion."}, {"title": "Rinsing Under Running Water", "description": "Once washed, rinse under running water. Rub gently as you rinse. Explain: ''We want to rinse away all the dirt and soap residue.'' Show clear water running off.", "tip": "Children are fascinated by the water and the transformation."}, {"title": "Patting Dry", "description": "Pat the produce dry with a clean cloth or paper towel. Show the gentle patting motion: ''We dry it so it''s comfortable to eat.''", "tip": "Drying is satisfying and completes the care cycle."}, {"title": "Organizing the Clean Produce", "description": "Place washed, dried produce in a clean bowl or on a clean surface. Arrange it neatly. Point out: ''Now our produce is clean and ready to eat or cook.''", "tip": "The organized presentation of finished work is satisfying."}]',
  control_of_error = '["Dirt remaining visible on produce (child sees it and washes again)", "Water left on produce making it wet and uncomfortable (child learns to dry thoroughly)", "Rough handling damaging the produce (child learns gentle pressure is effective)"]',
  direct_aims = '["Learn proper fruit and vegetable washing technique", "Understand food safety and cleanliness"]',
  materials = '["Fresh produce (apples, carrots, cucumbers, tomatoes, etc.)", "Large, shallow basin", "Cool water", "Soft brush (optional, for produce with deep ridges)", "Clean cloth or paper towels for drying", "Running water access for rinsing"]',
  parent_description = 'Washing fruits and vegetables teaches children that food requires care and preparation. They learn that clean food is important for health and safety. The tactile exploration of produce builds connection to food sources.',
  why_it_matters = 'This work teaches practical life skills while building respect for food and awareness of food safety. Children learn that their careful preparation contributes to family health and wellbeing. It''s a foundational skill for lifelong eating and cooking.'
WHERE LOWER(name) = LOWER('Washing Fruits and Vegetables');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up spreads (butter, jam, peanut butter) and bread
• Show how to hold the spreader and bread
• Demonstrate smooth spreading motions
• Practice spreading evenly across the surface
• Emphasize precision and neatness',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a tray with spreader, bread, and spread (butter, jam, or peanut butter).", "tip": "Start with soft spreads and soft bread before progressing to harder combinations."}, {"step": 2, "title": "Selecting the Spreader", "description": "Show the child a child-sized spreader and explain its purpose.", "tip": "Use a spreader with a rounded edge to prevent accidental cuts."}, {"step": 3, "title": "Gathering the Spread", "description": "Show how to gather a small amount of spread on the spreader.", "tip": "Demonstrate that a little spread goes a long way."}, {"step": 4, "title": "Spreading Technique", "description": "Show how to hold the bread steady with one hand and spread with smooth, even motions.", "tip": "Demonstrate working from the center outward or from one edge to the other."}, {"step": 5, "title": "Completing and Cleanup", "description": "Show how to place the finished bread on a plate and clean the work surface.", "tip": "Demonstrate that a neat, clean work space is part of the activity."}]',
  control_of_error = 'Even, smooth spreading demonstrates proper technique. Uneven spreading or spread that is clumpy shows the need for adjustment.',
  direct_aims = '["To learn to spread food evenly", "To develop fine motor control with tools", "To prepare simple foods independently"]',
  materials = '["Child-sized spreader (butter knife or rounded spreader)", "Bread (soft bread for beginners)", "Spreads (butter, jam, peanut butter, cream cheese)", "Plate for finished bread", "Towels for cleanup", "Optional: small dishes for spreads"]',
  parent_description = 'Your child is learning to spread, a practical food preparation skill. Spreading develops fine motor control and builds independence in preparing simple foods. This activity teaches your child that they can prepare their own snacks and meals.',
  why_it_matters = 'Spreading is a fundamental food preparation activity that develops fine motor control and builds confidence in the kitchen. It teaches children to work with precision and care, and demonstrates that they can independently prepare simple, nutritious foods. This activity also builds appreciation for food preparation and independence in meeting their own nutritional needs.'
WHERE LOWER(name) = LOWER('Spreading');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Start with easy-peel items (bananas, mandarins)", "Show proper hand position and technique", "Peel slowly and deliberately", "Contain pieces in a bowl", "Dispose of peels appropriately"]',
  presentation_steps = '[{"title": "Introduction & Choosing Items", "description": "Explain: ''Some foods have skins we peel before eating. Let me show you with easy items first\u2014bananas and mandarins.'' These are forgiving for learners.", "tip": "Success early builds confidence for harder peeling."}, {"title": "Banana Peeling", "description": "Hold a banana. Show where the ''seam'' is (the ridge). Explain: ''We peel along the seam. It''s easier.'' Gently break the peel at the top and peel back slowly, working around the banana.", "tip": "Show the exact thumb and finger placement."}, {"title": "Demonstrating Mandarin Peeling", "description": "For a mandarin, show how to poke a thumb in at the top and peel back the skin in sections. Explain: ''The peel comes away easily because the skin is loose.'' Work slowly.", "tip": "Let the child see and smell the citrus oils releasing."}, {"title": "Controlling Pieces", "description": "Show how to peel into a bowl: ''Peels go in the bowl, the fruit goes in another bowl. This keeps things organized.''", "tip": "Organization during work teaches care and prevents messes."}, {"title": "Inviting the Child to Practice", "description": "Give the child their own banana or mandarin. Sit nearby to support if needed, but let them work. Praise effort: ''You''re peeling so carefully.''", "tip": "Hands-on practice is essential; step back and let them discover."}, {"title": "Handling Challenges", "description": "If peeling gets stuck, show gently: ''Try here. Work around it slowly.'' Don''t take over unless safety is at risk.", "tip": "Problem-solving through gentle guidance builds confidence."}, {"title": "Completing the Task", "description": "Once peeled, dispose of peels in compost or trash. Wash hands. Place the peeled fruit on a clean plate. Celebrate: ''You did it!''", "tip": "Completion and cleanliness matter as much as the peeling."}]',
  control_of_error = '["Peel stuck on fruit means they need to keep working (natural feedback)", "Fruit flesh damaged means they were too rough (child learns gentleness matters)", "Messy process teaches need for a bowl or towel"]',
  direct_aims = '["Learn to peel easy fruits (bananas, mandarins)", "Develop fine motor control and dexterity"]',
  materials = '["Bananas, mandarins, or other easy-peel fruits", "Bowls for peels and fruit", "Clean plate for finished fruit", "Towel for cleanup if needed", "Trash or compost container"]',
  parent_description = 'Peeling easy fruits is a perfect stepping stone to more complex food preparation. Children gain confidence and hand strength while learning practical nutrition skills. It''s easy enough to feel successful while challenging enough to be satisfying.',
  why_it_matters = 'This work builds practical independence. As children learn to peel and prepare their own food, they develop confidence in their ability to care for themselves. It''s a tangible skill that translates directly to home and self-sufficiency.'
WHERE LOWER(name) = LOWER('Peeling - Easy Items');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Hold vegetable firmly with non-dominant hand", "Grip peeler with thumb and fingers", "Draw peeler toward yourself with steady pressure", "Work all around the vegetable systematically", "Continue until thin skin is completely removed"]',
  presentation_steps = '[{"title": "Introduction & Tool Handling", "description": "Explain: ''Now we''re learning to use a peeler. It''s a tool that removes the thin outer skin from vegetables. We use it carefully and respectfully.''", "tip": "Frame tool use as a responsibility."}, {"title": "Holding the Peeler", "description": "Show how to grip the peeler: thumb on one side, fingers on the other, with the blade facing away from your body and down. Demonstrate the grip clearly.", "tip": "Safety in tool handling is paramount."}, {"title": "Holding the Vegetable", "description": "Show how to hold a carrot or potato: firmly with your non-dominant hand, over the bowl to catch peels. Keep fingers curled inward away from the peeler path.", "tip": "Proper hand position prevents injuries."}, {"title": "Demonstrating the Peeling Stroke", "description": "Show one smooth stroke: draw the peeler from the far end of the vegetable toward yourself, removing a thin strip of skin. Lift off and repeat, working around the vegetable.", "tip": "Demonstrate the exact speed and pressure needed."}, {"title": "Working Systematically", "description": "Show how to rotate the vegetable as you peel, ensuring full coverage all the way around. Work from one end toward the other. Make it rhythmic and consistent.", "tip": "Systematic work is more effective and safer than random peeling."}, {"title": "Practicing with Supervision", "description": "Give the child their own vegetable and peeler. Stand nearby. Offer gentle guidance: ''Nice even stroke. Go all around. You''re doing great.''", "tip": "Hands-on learning with nearby support builds confidence."}, {"title": "Handling Challenges", "description": "If they struggle, guide gently: ''Hold it firm. Lighter pressure. Try again.'' Don''t take over unless necessary for safety.", "tip": "Problem-solving together builds skill."}, {"title": "Completing & Cleanup", "description": "Once peeled, wash the vegetable. Properly store the peeler. Dispose of peels. Wash hands. Place the peeled vegetable on a clean plate.", "tip": "Care for tools and completion matter as much as the work itself."}]',
  control_of_error = '["If too much vegetable is removed, it wastes food (child learns lighter pressure)", "If peel isn''t fully removed, vegetable isn''t ready (child sees remaining skin and continues)", "If hand isn''t positioned safely, risk of cuts (child learns proper hand position)"]',
  direct_aims = '["Learn proper peeler technique and tool handling", "Develop fine motor control and hand-tool coordination", "Understand vegetable preparation"]',
  materials = '["Vegetable peeler (child-sized or regular, depending on hand size)", "Vegetables to peel (carrots, potatoes, cucumbers, etc.)", "Bowl for catching peels", "Clean plate for peeled vegetables", "Towel for drying if needed", "Running water for rinsing"]',
  parent_description = 'Using a vegetable peeler is a more advanced skill that teaches tool handling and food preparation. Once mastered, it opens the door to independent food preparation at home. The confidence gained is substantial.',
  why_it_matters = 'This work teaches responsible tool use and builds toward greater independence in the kitchen. Children learn that they can handle real tools safely and competently. It''s a bridge to more complex food preparation and cooking.'
WHERE LOWER(name) = LOWER('Peeling - With Peeler');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Start with NARROW BLANK STRIPS (½" wide) - single snip success builds confidence
• Thumb UP in smaller hole, fingers DOWN in larger hole - check grip before cutting
• Present SAFETY first: carry closed, cut away from body, pass handles first
• Progress: blank strips → straight lines → curves → zigzag → shapes
• Dominant hand cuts, non-dominant hand FEEDS paper toward scissors',
  presentation_steps = '[{"step": 1, "title": "Safety Introduction", "description": "Show closed scissors. Demonstrate safe carrying: pointed end in fist, walk slowly.", "tip": "Safety MUST come before cutting"}, {"step": 2, "title": "Hand Position", "description": "Show thumb in small hole pointing UP, other fingers in large hole pointing DOWN.", "tip": "Check child''s grip before allowing cuts"}, {"step": 3, "title": "Open-Close Practice", "description": "Practice opening and closing motion WITHOUT paper first.", "tip": "Build muscle memory before adding paper"}, {"step": 4, "title": "First Cuts", "description": "Present narrow strip (\u00bd inch). Show single snip that cuts strip in one motion.", "tip": "Narrow strips = single snip success"}, {"step": 5, "title": "Paper Feeding", "description": "Demonstrate: non-dominant hand holds and FEEDS paper toward scissors.", "tip": "Paper moves, not scissors"}, {"step": 6, "title": "Cutting Motion", "description": "Cut with slow, controlled movements. Place cut pieces in designated container.", "tip": "Container on right for cut pieces"}, {"step": 7, "title": "Clean Up", "description": "Show returning scissors to holder, discarding scraps properly.", "tip": "Scissors ALWAYS returned closed"}]',
  control_of_error = 'Child can see if cut was successful. Paper provides resistance feedback.',
  direct_aims = '["Knife skills", "Coordination"]',
  materials = '["Cutting board", "Child-safe knife", "Soft foods (banana, cheese, cucumber)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Cutting Soft Foods');

