UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up a child-safe grater and foods to grate
• Show how to hold the grater and food properly
• Demonstrate controlled grating motions
• Practice with different foods (cheese, vegetables)
• Emphasize safety to avoid grating fingers',
  presentation_steps = '[{"step": 1, "title": "Introduction to the Grater", "description": "Show the child a child-safe grater and explain that grating transforms food into smaller pieces.", "tip": "Start with soft foods (cheese, cucumber) before progressing to harder vegetables."}, {"step": 2, "title": "Proper Grater Position", "description": "Demonstrate holding the grater firmly on a stable surface (cutting board or towel base).", "tip": "Explain that the grater must not move while grating to ensure safety."}, {"step": 3, "title": "Food Holding Technique", "description": "Show how to hold the food to be grated, keeping fingers away from the grating surface.", "tip": "Demonstrate the \"claw\" position to protect fingers from the sharp grater."}, {"step": 4, "title": "Grating Motion", "description": "Show controlled, downward grating strokes, moving the food against the grater.", "tip": "Demonstrate that even pressure and controlled movement produce better results."}, {"step": 5, "title": "Completing the Task", "description": "Show how to set down the grater safely and collect the grated food.", "tip": "Demonstrate that a protective towel catches most of the grated food."}]',
  control_of_error = 'Successfully grated food of consistent size demonstrates proper technique. Large chunks or powder shows incorrect pressure or speed.',
  direct_aims = '["To learn how to grate food", "To develop control and safety with graters", "To prepare food in different forms"]',
  materials = '["Child-safe grater (box grater or microplane)", "Foods to grate (cheese, cucumber, carrot, apple)", "Cutting board or protective surface", "Towel to catch grated food", "Bowl for collecting grated food", "Apron for protection"]',
  parent_description = 'Your child is learning to grate food, an important food preparation skill. Grating develops hand strength and teaches safe tool use. This activity demonstrates that different tools transform food in different ways, building confidence in the kitchen.',
  why_it_matters = 'Grating is a practical food preparation activity that teaches children to use a multi-surfaced grater safely and effectively. It develops arm strength through controlled grating motions and provides immediate visual feedback as food is transformed into smaller pieces. This activity builds confidence with kitchen tools and demonstrates that food can be prepared in many different forms through various techniques.'
WHERE LOWER(name) = LOWER('Grating');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up a juicer and citrus fruits
• Show how to cut fruit in half (with assistance)
• Demonstrate proper juicing press technique
• Practice extracting juice without waste
• Emphasize control and care with the tool',
  presentation_steps = '[{"step": 1, "title": "Introduction to Juicing", "description": "Show the child a juicer and citrus fruit, explaining that juicing extracts liquid from fruit.", "tip": "Start with lemons or limes before progressing to oranges or other citrus."}, {"step": 2, "title": "Preparing the Fruit", "description": "Show how the fruit is cut in half. (Adult may do this step for safety.)", "tip": "Demonstrate that fresh fruit yields the most juice."}, {"step": 3, "title": "Positioning in the Juicer", "description": "Show how to place the fruit half, cut side down, into the center of the juicer.", "tip": "Explain that the fruit must be positioned correctly for the juicer to work properly."}, {"step": 4, "title": "Pressing Down", "description": "Show how to press the handle down with firm, controlled pressure to extract juice.", "tip": "Demonstrate that a steady pressure works better than aggressive pressing."}, {"step": 5, "title": "Collecting and Cleanup", "description": "Show how juice is collected and the fruit pulp is disposed of properly.", "tip": "Demonstrate that pulp can go in compost or trash."}]',
  control_of_error = 'Extracted juice demonstrates successful pressing. A poorly positioned fruit or inadequate pressure yields little juice.',
  direct_aims = '["To learn how to juice citrus fruits", "To develop arm strength and control", "To prepare fresh, healthy juice"]',
  materials = '["Juicer (citrus press or manual juicer)", "Citrus fruits (lemon, lime, orange)", "Sharp knife for cutting fruit (adult use)", "Container for collecting juice", "Cutting board", "Towels for cleanup"]',
  parent_description = 'Your child is learning to juice citrus fruits, a practical food preparation skill. Juicing develops arm strength and teaches the value of fresh, natural foods. This activity builds independence and appreciation for nutritious beverages prepared from whole fruits.',
  why_it_matters = 'Juicing is a practical food preparation activity that demonstrates how natural foods can be processed into different forms. It develops arm strength through pressing and teaches children the source of common beverages. This activity builds appreciation for fresh, whole foods and demonstrates that healthy nutrition comes from natural ingredients that children can prepare themselves.'
WHERE LOWER(name) = LOWER('Juicing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Hold egg firmly with both hands", "Tap sharply against a hard edge to create a crack", "Use thumbs to pull shell halves apart gently", "Let egg contents slide into a bowl", "Separate shell pieces and discard"]',
  presentation_steps = '[{"title": "Introduction & Purpose", "description": "Explain: ''Eggs are an important food. Today we''re learning to crack eggs open. Let me show you how.''", "tip": "Frame egg preparation as a practical, valuable skill."}, {"title": "Demonstrating the Tap", "description": "Hold an egg in both hands with your thumbs opposite each other. Show a sharp, confident tap against the bowl''s edge or a hard surface. Demonstrate the exact motion.", "tip": "The confidence of the tap matters; tentative taps don''t create clean cracks."}, {"title": "Creating the Crack", "description": "Explain what you''re doing: ''We tap to create a crack, not to smash it.'' Show that even a small crack is enough to start.", "tip": "Controlled force is better than excessive pressure."}, {"title": "Pulling the Shell Apart", "description": "Once cracked, show how to position thumbs on either side of the crack and pull the shell halves apart gently and evenly. The egg drops into the bowl.", "tip": "Demonstrate the gentle, steady pulling motion."}, {"title": "Managing Shell Pieces", "description": "Show what to do with the shell halves: place them in a compost container or trash. Check the egg for any shell fragments and remove them if needed.", "tip": "Cleanliness during food prep is essential."}, {"title": "Inviting the Child to Try", "description": "Give the child their own egg and a bowl. Stand nearby to support. Encourage: ''Tap firmly. Now pull apart gently.'' Let them work with your guidance.", "tip": "Hands-on experience is essential for learning."}, {"title": "Normalizing Imperfection", "description": "Explain that sometimes egg gets messy, sometimes there''s extra shell, sometimes they''re harder or easier. All of that''s normal and fine.", "tip": "Normalizing mistakes reduces frustration and builds resilience."}, {"title": "Completing the Task", "description": "Once cracked, if the egg is to be used, wash hands. Prepare it for cooking or eating. If it''s just practice, rinse the bowl and put it away.", "tip": "Completion and cleanup matter."}]',
  control_of_error = '["Shell fragments in the egg teach the need to check (child learns inspection)", "Egg splattered if hit too hard teaches controlled force (child learns proper pressure)", "Difficulty pulling apart teaches where to position hands (child learns technique)"]',
  direct_aims = '["Learn egg-cracking technique", "Develop hand strength and coordination", "Understand food preparation"]',
  materials = '["Fresh eggs (start with 2-3 to practice)", "Small bowl for cracking", "Another bowl for the cracked egg (if continuing to cooking)", "Towel for cleanup", "Compost or trash container for shells", "Hard surface (bowl edge, countertop edge) for tapping"]',
  parent_description = 'Cracking eggs teaches children that they can work with delicate, breakable items carefully. It''s a foundational cooking skill that builds confidence. Most importantly, it teaches that hands-on work with real materials is satisfying and empowering.',
  why_it_matters = 'This work builds confidence in kitchen skills and independence in food preparation. Children learn that they can handle delicate items successfully, and that practical kitchen work is within their capability. It''s empowering and teaches self-sufficiency.'
WHERE LOWER(name) = LOWER('Cracking Eggs');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Demonstrate simple snack preparation (sandwiches, fruit, crackers)
• Show proper hygiene and food safety
• Teach how to use simple tools and utensils
• Practice creating complete, balanced snacks
• Build independence in food preparation',
  presentation_steps = '[{"step": 1, "title": "Hand Washing and Preparation", "description": "Begin by washing hands thoroughly, demonstrating food safety.", "tip": "Explain that clean hands are essential for safe food preparation."}, {"step": 2, "title": "Simple Snack Assembly", "description": "Show how to assemble a simple snack (crackers and cheese, bread and spread, fruit).", "tip": "Choose snacks that require minimal cooking or complex preparation."}, {"step": 3, "title": "Using Tools and Utensils", "description": "Demonstrate using child-safe knives, spreaders, or other tools appropriately.", "tip": "Teach that proper tool use makes preparation easier and safer."}, {"step": 4, "title": "Arranging the Snack", "description": "Show how to arrange snack items on a plate or in a container neatly.", "tip": "Explain that attractive presentation makes snacks more appealing."}, {"step": 5, "title": "Cleanup and Eating", "description": "Demonstrate cleaning up after snack preparation and enjoying the snack.", "tip": "Show that responsibility includes cleanup as part of the activity."}]',
  control_of_error = 'A complete, edible snack shows success. Unsafe practices or incomplete preparation show need for guidance.',
  direct_aims = '["To learn simple snack preparation", "To develop food safety awareness", "To build independence in food preparation"]',
  materials = '["Age-appropriate foods for snacks", "Child-safe knives and spreaders", "Plates or containers", "Napkins", "Hand washing station", "Optional: step stool for reaching"]',
  parent_description = 'Your child is learning to prepare snacks independently. Simple snack preparation builds confidence, teaches food safety, and demonstrates that your child can meet their own nutritional needs. This practical skill supports independence and healthy eating habits.',
  why_it_matters = 'Making a snack teaches children that they can independently prepare food and meet their own needs. It demonstrates food safety and hygiene while building confidence in kitchen skills. This activity fosters independence and teaches that children''s contributions to food preparation are valued and important. Learning to prepare simple snacks builds a foundation for all future cooking skills and promotes healthy eating habits and independence in nutrition.'
WHERE LOWER(name) = LOWER('Making a Snack');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Hold thread or string with steady hand", "Guide beads one at a time onto thread", "Maintain consistent pace and focus", "Create patterns if desired", "Complete piece when desired length reached"]',
  presentation_steps = '[{"title": "Introduction & Setup", "description": "Explain: ''Today we''re threading beads onto string. This helps us develop hand control and focus. Let me show you.''", "tip": "Frame this as meditative, focused work."}, {"title": "Preparing Materials", "description": "Show how to thread string or yarn, or give a pre-threaded needle. Explain: ''The thread goes through the hole in the bead. One bead at a time.''", "tip": "For young learners, pre-threaded thick yarn works best."}, {"title": "Demonstrating the Motion", "description": "Pick up a bead. Show how to hold the string with one hand and guide the bead onto the string with the other. Move slowly and deliberately. The motion is rhythmic and calm.", "tip": "Demonstrate the exact hand position and speed."}, {"title": "Repeating the Motion", "description": "Continue threading beads one after another, at a steady, meditative pace. Show that the focus is on the motion itself, not rushing to finish.", "tip": "The meditative quality is important; slow down the pace."}, {"title": "Creating a Pattern (Optional)", "description": "Once the child understands the basic motion, show how to create a pattern: ''Red, blue, red, blue'' or ''Big, small, big, small.'' This adds complexity.", "tip": "Patterns add interest and challenge for advanced learners."}, {"title": "Inviting the Child to Practice", "description": "Provide the child with pre-threaded string and beads. Sit nearby. Observe without intervening unless help is needed. Let them find their own rhythm.", "tip": "Quiet, focused observation is powerful."}, {"title": "Completing the Work", "description": "When the child decides they''ve threaded enough, tie off the string to secure the beads. Create a bracelet, necklace, or decoration. Display or wear the finished work.", "tip": "Completion and appreciation of the finished product matter."}]',
  control_of_error = '["Thread too thick won''t fit through bead holes (child sees it and adjusts)", "Thread too loose and beads slide off (child learns tension matters)", "Beads fall off if not tied properly (child learns secure knots are needed)"]',
  direct_aims = '["Develop hand-eye coordination and fine motor control", "Practice sustained focus and concentration", "Learn hand-tool skills (threading, tying)"]',
  materials = '["Beads with large holes (pony beads, wooden beads, pasta beads)", "Thick yarn or string (pre-threaded optional)", "Needle (if not pre-threaded)", "Small container for beads", "Finished piece (bracelet, necklace, or display)"]',
  parent_description = 'Threading beads is calming, meditative work that builds fine motor skills and focus. The beauty of creating something wearable or displayable is highly motivating. Many children want to repeat this work many times.',
  why_it_matters = 'This work develops concentration, fine motor control, and the foundation for more advanced sewing skills. The meditative quality makes it calming and therapeutic. It builds the hand strength and coordination needed for writing and other precise work.'
WHERE LOWER(name) = LOWER('Threading Beads');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Thread needle with yarn", "Follow pre-punched holes in sequence", "Push needle through hole from one side", "Pull yarn all the way through smoothly", "Continue until pattern or card is complete"]',
  presentation_steps = '[{"title": "Introduction & Showing the Card", "description": "Show the sewing card with its pre-punched holes. Explain: ''This card has holes. We sew through them with yarn. Let me show you.''", "tip": "The holes make this easier than free sewing."}, {"title": "Threading the Needle", "description": "Show how to thread yarn through a large needle. Pull the yarn so it''s even on both sides. Tie a small knot at one end so the yarn doesn''t pull back through.", "tip": "Pre-threaded cards can skip this step if needed."}, {"title": "Demonstrating the First Hole", "description": "Point to the starting hole (usually marked). Show how to push the needle through from the front, then pull it from the back. Explain: ''Push through, pull through, nice and slow.''", "tip": "Model the exact motion twice."}, {"title": "Moving to the Next Hole", "description": "Show where the next hole is in the sequence (usually numbered or with arrows). Push through from the back, pull from the front. Continue the motion: push, pull, push, pull.", "tip": "The rhythm becomes meditative."}, {"title": "Maintaining Consistent Pace", "description": "Continue demonstrating, showing a steady pace without rushing. Let the child observe the emerging pattern of yarn on the card.", "tip": "Don''t rush; let the child absorb the meditative quality."}, {"title": "Inviting the Child to Practice", "description": "Give the child a sewing card and pre-threaded needle (or help them thread). Let them practice the push-pull motion. Sit nearby to support.", "tip": "Hands-on practice is essential."}, {"title": "Completing the Pattern", "description": "Continue until all holes are sewn. Tie off the yarn at the end. Step back and look at the completed pattern together.", "tip": "The finished product with completed yarn pattern is beautiful and satisfying."}]',
  control_of_error = '["Yarn tangles if not pulled all the way through (child sees it and fixes it)", "Holes skipped if not following sequence (visible in pattern, easily corrected)", "Knot too small and yarn pulls out (child learns knot needs to be bigger)"]',
  direct_aims = '["Learn sewing technique with needle and yarn", "Develop hand-eye coordination and fine motor control", "Practice following a sequence"]',
  materials = '["Sewing cards with pre-punched holes (numbered or with arrows)", "Yarn or embroidery thread (colorful, visible)", "Large needle (blunt if possible, to prevent pricks)", "Container for materials", "Flat surface for working"]',
  parent_description = 'Sewing cards are a wonderful bridge between threading beads and real sewing. They teach needle control and stitching skills in a forgiving format. The meditative quality and visible progress build both focus and pride.',
  why_it_matters = 'This work develops the fine motor skills, hand strength, and concentration needed for real sewing. It builds confidence in handling sewing tools and creates the foundation for textile work. The meditative quality makes it calming while being productive and skill-building.'
WHERE LOWER(name) = LOWER('Sewing Cards');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Hold paper firmly on flat surface", "Position punch where you want a hole", "Apply steady, firm pressure straight down", "Lift punch to remove the small paper circle", "Repeat across paper to create pattern"]',
  presentation_steps = '[{"title": "Introduction & Showing the Tool", "description": "Show the paper punch. Explain: ''This tool makes small holes in paper. We press down and it makes a perfect circle. Let me show you.''", "tip": "The simplicity and effectiveness of the tool is appealing to children."}, {"title": "Demonstrating Safe Handling", "description": "Show how to hold the punch safely: thumb and fingers on the handles, away from the hole-making area. Demonstrate the exact grip.", "tip": "Safety first; proper handling prevents pinches."}, {"title": "Positioning on Paper", "description": "Show how to position the punch on the paper where you want a hole. The paper lies flat on the table. The punch is held vertically over the spot.", "tip": "Positioning and stability matter for clean holes."}, {"title": "Applying Pressure", "description": "Show a firm, steady downward press. Explain: ''Press down firmly and straight. The hole appears. Then lift up.''", "tip": "Demonstrate the motion clearly and at regular pace."}, {"title": "Removing the Hole", "description": "Once pressed, show how to lift the punch carefully. The small paper circle might stay in the punch (collect them) or fall out.", "tip": "Show where the paper circles go (small container for collection)."}, {"title": "Creating a Pattern", "description": "Continue punching holes across the paper in a pattern or design. Show how to space them evenly or create decorative arrangements.", "tip": "The emerging pattern is visually interesting."}, {"title": "Inviting the Child to Practice", "description": "Give the child paper and the punch. Provide guidance: ''Press firmly. Straight down. Yes, like that!'' Let them find their rhythm.", "tip": "Close supervision ensures safe use of the tool."}, {"title": "Completing the Work", "description": "Once the child has made holes across the paper, look at the result together. Show the holes from both sides. Point out the pattern created.", "tip": "Reflection on the work celebrates the accomplishment."}]',
  control_of_error = '["Weak punch results in incomplete hole (child learns to press firmly)", "Angled punch results in misshapen hole (child learns to press straight down)", "Fingers in the hole area could get pinched (child learns proper hand position safety)"]',
  direct_aims = '["Learn to use a paper punch safely and effectively", "Develop hand strength and control", "Create patterned artwork"]',
  materials = '["Paper punch (hole punch)", "Paper (colored or white)", "Flat surface for working", "Container for collected paper circles (optional but nice)", "Your supervision and guidance"]',
  parent_description = 'Paper punching is a satisfying, safe introduction to using hand tools. The immediate visual result and the rhythmic nature make it appealing to children. It builds hand strength and tool confidence.',
  why_it_matters = 'This work develops hand strength, tool handling skills, and confidence. The satisfying, immediate results build motivation and pride. It''s a wonderful transition to more advanced handwork like sewing and crafting.'
WHERE LOWER(name) = LOWER('Paper Punching');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show the in-and-out needle motion
• Demonstrate even, consistent stitch spacing
• Practice on open fabric before closing stitches
• Emphasize rhythm and control
• Allow for gradual mastery through repetition',
  presentation_steps = '[{"step": 1, "title": "Introduction to Stitching", "description": "Explain that running stitch is the most basic and useful stitch in sewing.", "tip": "Show examples of fabric with running stitches on them."}, {"step": 2, "title": "Setting Up", "description": "Set up fabric in an embroidery hoop with pre-marked stitch lines or holes.", "tip": "Pre-made holes make starting stitches easier for beginners."}, {"step": 3, "title": "Demonstrating the Motion", "description": "Show the in-and-out needle motion, with the needle going under and over the fabric.", "tip": "Demonstrate that the motion should be rhythmic and even."}, {"step": 4, "title": "Creating Even Stitches", "description": "Show how to keep stitches approximately equal in length and spacing.", "tip": "Explain that consistent stitches look neat and hold fabric securely."}, {"step": 5, "title": "Completing a Line", "description": "Show how to complete a full line of stitches and tie off the thread.", "tip": "Demonstrate tying off securely so stitches don''t unravel."}]',
  control_of_error = 'Even, consistent stitches demonstrate proper technique. Uneven or loose stitches show the need for more practice or adjustment.',
  direct_aims = '["To learn the running stitch", "To develop even, consistent stitching", "To create stitches that hold fabric securely"]',
  materials = '["Pre-threaded needles (for beginners)", "Fabric squares or pre-made stitch samplers", "Embroidery hoops", "Fabric with pre-marked stitch lines or holes", "Child-appropriate scissors", "Thread in contrasting colors"]',
  parent_description = 'Your child is learning the running stitch, the foundational sewing technique. Running stitch develops hand-eye coordination and provides satisfaction through creating visible, functional stitches. This skill opens the door to sewing projects and practical clothing repair.',
  why_it_matters = 'Running stitch is the most fundamental sewing technique that teaches children how thread creates strong, durable seams. It develops hand-eye coordination, rhythm, and the ability to create consistent, functional stitches. Learning running stitch builds confidence in sewing and demonstrates that with practice and focus, children can create beautiful, functional items. This skill is essential for all future sewing work and provides a foundation for understanding how fabrics are constructed and repaired.'
WHERE LOWER(name) = LOWER('Running Stitch');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Thread needle with embroidery floss", "Understand the cross-stitch pattern (diagonal grid)", "Create an X pattern: diagonal one way, then diagonal back", "Work in rows or sections methodically", "Continue until pattern or picture is complete"]',
  presentation_steps = '[{"title": "Introduction & Showing the Technique", "description": "Explain: ''Cross-stitch is a special sewing pattern. We make tiny X marks with thread. It takes patience but creates beautiful patterns.''", "tip": "Frame this as an advanced skill the child is ready for."}, {"title": "Preparing Materials", "description": "Gather: aida cloth or gingham fabric (large squares make stitching easier), embroidery floss in one color, needle, and a simple cross-stitch pattern or chart.", "tip": "Gingham or aida cloth with clear squares is much easier than plain fabric."}, {"title": "Threading the Needle", "description": "Show how to thread embroidery floss through the needle. Tie a small knot at one end. Explain: ''Just like before, but thinner thread.''", "tip": "Using only 1-2 strands of floss is easier than the full thickness."}, {"title": "Demonstrating the First Diagonal", "description": "Start with one corner square on the fabric. Show how to push the needle through one corner of the square, then pull it through the opposite corner diagonally. Create a diagonal line.", "tip": "Work on a single, large square first so the child can see the pattern clearly."}, {"title": "Demonstrating the Second Diagonal", "description": "Now show the return diagonal: push through the opposite starting corner, pull through the opposite corner of the same square. This creates an X. Explain: ''That''s a cross-stitch!''", "tip": "The X is the satisfying visual result."}, {"title": "Working Across a Row", "description": "Show how to continue this pattern across a row of squares. Each square gets a complete X before moving to the next. Work methodically from left to right.", "tip": "Rhythm and consistency are beautiful in cross-stitch."}, {"title": "Following a Pattern", "description": "Show a simple pattern or chart (often marked with symbols or colors indicating where to stitch). Explain: ''We follow the pattern. This square gets a stitch. This one doesn''t.''", "tip": "Simple patterns with large squares work best for beginners."}, {"title": "Inviting the Child to Practice", "description": "Give the child their own materials and a simple pattern. Provide close guidance initially: ''Diagonal one way. Now the other way. Make an X.'' Let them find rhythm.", "tip": "Hands-on learning with nearby support is essential for this complex skill."}, {"title": "Recognizing Progress", "description": "As rows complete, step back and look at the emerging pattern. Show how their work creates a design. Celebrate: ''You''re creating a picture!''", "tip": "Progress is motivating; celebrate regularly."}]',
  control_of_error = '["Stitches going through wrong squares (visible in pattern; child adjusts", "Uneven tension creates loose or tight stitches (child learns through practice)", "Pattern not followed creates unexpected results (child sees deviation and corrects)"]',
  direct_aims = '["Learn cross-stitch technique and pattern following", "Develop advanced fine motor control and precision", "Understand how stitching creates pictures"]',
  materials = '["Aida cloth or gingham fabric with clear squares", "Embroidery floss in desired colors", "Embroidery needle or small needle", "Cross-stitch pattern (simple, large squares for beginners)", "Needle threader (optional but helpful)", "Embroidery hoop (optional, to hold fabric taut)", "Scissors for trimming floss"]',
  parent_description = 'Cross-stitch is an advanced handwork skill that teaches precision, pattern following, and patience. The finished work is often beautiful enough to display or gift, which builds tremendous pride. It''s therapeutic and focuses the mind powerfully.',
  why_it_matters = 'This work develops the highest level of fine motor control, concentration, and precision. It teaches that complex, beautiful things take sustained effort and focus. The meditative quality of repetitive cross-stitching is calming and therapeutic, while the finished product is deeply satisfying.'
WHERE LOWER(name) = LOWER('Cross Stitch');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to thread needle and position button
• Demonstrate sewing up and down through button holes
• Practice creating secure stitches
• Teach knotting and finishing technique
• Build confidence through completing a practical repair',
  presentation_steps = '[{"step": 1, "title": "Introduction to Button Sewing", "description": "Explain that sewing buttons is a practical skill that fixes clothing and makes things last longer.", "tip": "Show examples of buttons on clothing and what happens when they fall off."}, {"step": 2, "title": "Preparing the Materials", "description": "Gather fabric (or clothing), needle, thread, and a button with appropriately sized holes.", "tip": "Start with larger buttons with bigger holes before progressing to smaller buttons."}, {"step": 3, "title": "Demonstrating Positioning", "description": "Show how to position the button on the fabric and mark where stitches will go.", "tip": "Demonstrate that the button must be straight and secure for functionality."}, {"step": 4, "title": "Sewing the Button", "description": "Show how to pass the needle up through one hole and down through another, creating stitches.", "tip": "Demonstrate multiple stitches through each pair of holes for security."}, {"step": 5, "title": "Tying Off", "description": "Show how to tie off and knot the thread securely so stitches don''t unravel.", "tip": "Demonstrate that secure knots prevent the button from falling off."}]',
  control_of_error = 'A securely attached button demonstrates success. A loose button or inadequate stitches shows the need for more stitches or tighter knots.',
  direct_aims = '["To learn how to sew a button", "To develop practical sewing skills", "To create secure, functional buttons"]',
  materials = '["Pre-threaded needle (for beginners)", "Fabric pieces or clothing with a button location", "Buttons (start with large buttons with big holes)", "Thread in colors that match or contrast", "Optional: button with shank for variation"]',
  parent_description = 'Your child is learning to sew buttons, a practical skill that repairs and maintains clothing. Sewing buttons builds confidence in solving real problems and demonstrates that children can care for their belongings independently. This skill teaches that small efforts extend the life of items and contribute to family well-being.',
  why_it_matters = 'Sewing a button is a practical, real-world sewing application that teaches children valuable repair skills. It demonstrates that their sewing abilities can solve actual problems and that repair is often simpler than replacement. This activity builds practical independence, confidence, and understanding that small skills contribute to self-sufficiency and environmental responsibility through caring for and extending the life of possessions.'
WHERE LOWER(name) = LOWER('Sewing a Button');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Understand warp (vertical threads) and weft (horizontal threads)", "Set up loom with warp threads at even tension", "Thread shuttle or pass yarn back and forth over and under warps", "Push each row of weft down firmly to create fabric", "Continue until desired width or length is achieved"]',
  presentation_steps = '[{"title": "Introduction & Showing the Loom", "description": "Show the weaving loom. Explain: ''Weaving is how we make fabric from thread. We have threads going this way [point up-down] and threads going this way [point left-right]. Together they make cloth.''", "tip": "The concept of creating fabric from scratch fascinates children."}, {"title": "Understanding Warp & Weft", "description": "Point to the vertical threads: ''These are warp threads. They stay still.'' Point to the horizontal threads or yarn: ''This is weft. It goes over and under, back and forth.''", "tip": "Clear naming helps children understand the system."}, {"title": "Preparing the Loom", "description": "Show how warp threads are already set at even tension on the loom. Explain: ''The warp is already ready. Now we add the weft.''", "tip": "Pre-threaded looms are much better for learning."}, {"title": "Demonstrating the Over-Under Pattern", "description": "Show how to pass the shuttle or yarn through the warp threads: over the first, under the second, over the third, under the fourth, etc. Go slowly so the child sees the pattern.", "tip": "The back-and-forth motion is rhythmic and meditative."}, {"title": "Returning on the Opposite Pattern", "description": "When you reach the end, turn around and come back the opposite way: under where you went over, over where you went under. This creates the interlocking.", "tip": "Show that the pattern alternates perfectly."}, {"title": "Pushing Down the Weft", "description": "After each row, show how to push the weft down gently but firmly toward the bottom of the loom. This packs the weave and creates fabric.", "tip": "This is a satisfying step; the fabric visibly grows."}, {"title": "Continuing the Rhythm", "description": "Continue the pattern: across, return, push down. Across, return, push down. Show the meditation of the rhythm.", "tip": "Let the child observe several rows to absorb the pattern before they try."}, {"title": "Inviting the Child to Weave", "description": "Invite the child to take over. Guide them gently: ''Over, under, over, under. All the way across. Now turn around. Under, over, under, over. Back across. Now push down gently.''", "tip": "Hands-on learning with verbal guidance is essential."}, {"title": "Celebrating the Growing Fabric", "description": "As rows accumulate, step back and look together. Point out: ''You''re making cloth! Real fabric! Look at the pattern you''re creating.''", "tip": "The visible creation of fabric is deeply satisfying."}, {"title": "Completing the Weaving", "description": "When the child decides their weaving is complete, show how to secure the end yarn. They can then remove the finished piece from the loom (if it''s designed to come off) or leave it as is.", "tip": "Completion and the ability to show the finished work are important."}]',
  control_of_error = '["Pattern mistakes (going over twice in a row or under twice) result in uneven weaving\u2014visible and easily fixed", "Tension too loose creates loose fabric (child learns to push down consistently)", "Leaving weft too tight creates pulling or distortion (child learns balance through practice)"]',
  direct_aims = '["Understand the fundamentals of weaving", "Learn over-under pattern and repetition", "Develop hand coordination and rhythm"]',
  materials = '["Weaving loom (frame loom, peg loom, or simple cardboard loom)", "Warp thread or yarn (usually pre-threaded on loom)", "Weft yarn in one or more colors", "Shuttle (for larger looms) or just fingers/needle (for smaller looms)", "Comb or fork to push weft down (if shuttle doesn''t have one)"]',
  parent_description = 'Weaving is creating cloth from scratch—a profound, meditative experience. Children understand viscerally how fabric is made. The finished weaving is tangible proof of their work and can be worn, displayed, or gifted. It builds tremendous pride and accomplishment.',
  why_it_matters = 'This work teaches that beautiful, functional things are created through repetitive, focused work. The meditative rhythm of weaving builds concentration and calm. Understanding how fabric is made creates respect for textiles and appreciation for craftsmanship. The creation of something truly useful and beautiful is deeply satisfying and empowering.'
WHERE LOWER(name) = LOWER('Weaving');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up two containers with water or colored liquid
• Show how to fill the eye dropper by squeezing and releasing
• Demonstrate transferring drops from one container to another
• Emphasize fine motor control and precision
• Allow for extended exploration and experimentation',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a tray with an eye dropper, one container filled with water, and one or more empty containers.", "tip": "Add food coloring to the water to make the activity more visually interesting and easier to observe drop transfer."}, {"step": 2, "title": "Demonstrating the Eye Dropper", "description": "Show how to hold the eye dropper and explain its parts (bulb and tube).", "tip": "Let the child feel the dropper and understand how it works before using it with water."}, {"step": 3, "title": "Filling the Dropper", "description": "Demonstrate squeezing the rubber bulb to release air, placing the tube in the water, and releasing the bulb to draw water into the dropper.", "tip": "Show this step slowly and clearly, as this is the most complex part of the activity."}, {"step": 4, "title": "Transferring Drops", "description": "Position the dropper over the empty container and gently squeeze drops of colored water into it.", "tip": "Emphasize the precision and control required to release individual drops."}, {"step": 5, "title": "Repetition and Exploration", "description": "Continue the activity, filling and transferring drops multiple times.", "tip": "Allow the child to discover patterns, fill containers to different levels, and explore the color changes as drops accumulate."}]',
  control_of_error = 'The eye dropper and containers provide built-in feedback. If squeezed too hard, liquid sprays. If not squeezed enough, no water is drawn. Spilled water shows lack of control.',
  direct_aims = '["To learn to use an eye dropper", "To develop precision in liquid transfer", "To practice fine motor control with a tool"]',
  materials = '["Eye droppers (child-sized or standard)", "Multiple small containers or bowls", "Water", "Food coloring (optional, but recommended)", "A tray to contain spills", "Towels for cleanup", "Apron for water protection"]',
  parent_description = 'Your child is learning to use an eye dropper with precision and control. This fine motor activity develops finger strength and dexterity, and introduces your child to managing small quantities of liquid. It''s a foundation for future scientific observation and careful measurement.',
  why_it_matters = 'Eye dropper transfer is a sophisticated transfer activity that develops fine motor control and precision with tools. It teaches children to manage very small amounts of liquid, a skill essential for cooking, art, science, and other practical activities. The activity also builds confidence with tools and introduces concepts of measurement and volume.'
WHERE LOWER(name) = LOWER('Eye Dropper Transfer');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up a low mirror at child height
• Show how to hold and manipulate the brush
• Demonstrate gentle brushing strokes
• Practice brushing different areas of the head
• Emphasize care and gentleness with hair',
  presentation_steps = '[{"step": 1, "title": "Mirror and Setup", "description": "Position a low mirror so the child can see their face and head while brushing their hair.", "tip": "The mirror should be at eye level when the child is standing normally."}, {"step": 2, "title": "Selecting the Brush", "description": "Show the child an appropriate brush or comb for their hair type.", "tip": "Use a soft-bristled brush suitable for the child''s hair texture."}, {"step": 3, "title": "Demonstrating Grip", "description": "Show how to hold the brush with a relaxed grip that allows for gentle, controlled strokes.", "tip": "Explain that hair brushing should feel good, not be painful or rough."}, {"step": 4, "title": "Brushing Technique", "description": "Demonstrate gentle brushing strokes, starting from the tips and working toward the scalp.", "tip": "Show how to work through tangles gradually and gently."}, {"step": 5, "title": "Checking and Repeating", "description": "Show how to use the mirror to check that the entire head is brushed and neaten as needed.", "tip": "Allow the child to observe the results in the mirror."}]',
  control_of_error = 'The appearance of the hair in the mirror provides feedback. Neat, brushed hair looks groomed; tangles and messiness are visible.',
  direct_aims = '["To learn how to brush hair", "To develop independence in grooming", "To understand personal appearance and care"]',
  materials = '["Low mirror positioned at child height", "Soft-bristled brush or comb appropriate for hair type", "Optional: hair ties, clips, or decorative hair accessories"]',
  parent_description = 'Your child is learning to brush their own hair, an important grooming skill. Hair brushing develops independence in personal care and self-awareness through mirror observation. This activity teaches your child to care for their appearance as an act of self-respect.',
  why_it_matters = 'Hair brushing is a practical self-care activity that develops independence and self-awareness. Using a mirror builds self-concept and awareness of personal appearance. This activity teaches children that grooming and personal care are forms of self-respect and that maintaining a neat appearance is within their control. The daily habit of hair care supports overall hygiene and wellbeing.'
WHERE LOWER(name) = LOWER('Hair Brushing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up metal polish and polishing cloths
• Show how to apply polish to the metal surface
• Demonstrate rubbing and buffing motions
• Practice until the object shines
• Emphasize care and attention to detail',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a tray with metal polishing supplies: polish, soft cloth for application, and cloth for buffing.", "tip": "Choose appropriate metals (brass, silver, copper) that polish well and show clear results."}, {"step": 2, "title": "Introduction to Polishing", "description": "Show the child a dull metal object and explain that polishing will make it shine.", "tip": "Allow the child to feel the difference between the dull and shiny areas."}, {"step": 3, "title": "Applying the Polish", "description": "Show how to apply a small amount of polish to a soft cloth and rub it onto the metal in the direction of the grain.", "tip": "Demonstrate that the metal becomes dull with the polish coating before it shines."}, {"step": 4, "title": "Buffing to Shine", "description": "Show how to use a different, clean cloth to buff the metal with circular motions until it shines.", "tip": "Explain that buffing removes the dull coating and reveals the shine underneath."}, {"step": 5, "title": "Completing the Task", "description": "Demonstrate finishing one side and then repeating the process on other sides of the object.", "tip": "Show how the entire object becomes uniformly shiny through continued polishing."}]',
  control_of_error = 'The metal itself provides feedback. Unpolished areas appear dull; properly polished areas shine brightly. The child can see their progress.',
  direct_aims = '["To learn how to polish metal objects", "To develop arm strength and controlled motions", "To care for the classroom environment"]',
  materials = '["Metal polish (non-toxic, safe for children)", "Soft cloths (terry cloth for applying, flannel for buffing)", "Metal objects to polish (brass, silver, or copper items)", "Tray to organize materials", "Newspaper or cloth to protect surfaces"]',
  parent_description = 'Your child is learning to polish metal, a care of environment activity. Polishing develops hand strength and arm control while creating visible, satisfying results. This activity teaches your child that effort and care maintain beauty and order in the environment.',
  why_it_matters = 'Polishing metal is a practical care of environment activity that develops muscular strength and provides immediate visual feedback showing the effect of effort and care. Children experience the satisfaction of transforming a dull object into a shiny one through their own work. This activity builds concentration, demonstrates the value of maintenance, and develops an appreciation for beautiful, orderly environments.'
WHERE LOWER(name) = LOWER('Polishing Metal');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up wood polish and soft cloths
• Show how to apply polish in the direction of the grain
• Demonstrate buffing with circular motions
• Practice until the wood develops a natural sheen
• Emphasize care and respect for materials',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a tray with wood polishing supplies: polish, terry cloth for application, and poplin cloth for buffing.", "tip": "Choose attractive wooden objects that show clear results from polishing."}, {"step": 2, "title": "Understanding the Wood", "description": "Show the child the wooden object and explain that we polish to keep it beautiful and protected.", "tip": "Allow the child to feel the grain of the wood and notice its natural beauty."}, {"step": 3, "title": "Observing the Grain", "description": "Point out the grain pattern in the wood and explain that we always polish in the direction of the grain.", "tip": "Show how to follow the grain with your fingers to understand its direction."}, {"step": 4, "title": "Applying Polish", "description": "Show how to apply wood polish with a soft cloth, working in the direction of the grain.", "tip": "Demonstrate that a small amount of polish covers a large area."}, {"step": 5, "title": "Buffing to a Sheen", "description": "Show how to use a clean, smooth cloth to buff the polish until the wood develops a soft, natural sheen.", "tip": "Explain that this process protects the wood and brings out its natural beauty."}]',
  control_of_error = 'The wood develops a visible sheen when properly polished. Unpolished areas appear dull and may show dust accumulation.',
  direct_aims = '["To learn how to polish wood", "To understand and respect natural materials", "To care for the classroom environment"]',
  materials = '["Wood polish safe for children (non-toxic)", "Terry cloth for applying polish", "Smooth poplin cloth for buffing and developing sheen", "Wooden objects to polish (furniture, frames, boxes)", "Tray for organizing materials", "Newspaper or cloth to protect surfaces"]',
  parent_description = 'Your child is learning to polish wood, a care of environment activity that teaches respect for natural materials. Wood polishing develops fine motor control and an appreciation for maintaining beauty. This activity shows your child that their efforts directly contribute to keeping the environment beautiful and protected.',
  why_it_matters = 'Wood polishing is a practical care of environment activity that teaches children to respect and care for natural materials. It develops fine motor control through smooth, directional motions and builds appreciation for the beauty of wood grain and finish. This activity demonstrates that objects properly maintained remain beautiful and functional, and that care and attention extend the life and beauty of materials. Learning to work with natural materials builds a foundation for environmental awareness and respect.'
WHERE LOWER(name) = LOWER('Polishing Wood');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up washing station with water and soap
• Show how to immerse cloth and rub together
• Demonstrate gentle scrubbing and wringing
• Practice rinsing thoroughly to remove soap
• Teach proper drying and storage',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a low table with a basin of warm soapy water, a rinse basin, and clean cloths to wash.", "tip": "Use small cloths initially to avoid overwhelming the child with a large volume of water."}, {"step": 2, "title": "Selecting and Examining Cloths", "description": "Show the child a cloth that needs washing and explain that we will make it clean.", "tip": "Choose cloths that show visible dirt or stains so the child sees the transformation."}, {"step": 3, "title": "Immersing and Rubbing", "description": "Show how to immerse the cloth in soapy water and rub the fabric together to remove dirt.", "tip": "Demonstrate rubbing all areas, both sides, and corners of the cloth."}, {"step": 4, "title": "Rinsing", "description": "Show how to rinse the cloth in clear water, squeezing gently to remove soap and dirt.", "tip": "Demonstrate multiple rinses if necessary until water runs clear."}, {"step": 5, "title": "Wringing and Drying", "description": "Show how to wring out excess water and hang or lay the cloth to dry.", "tip": "Demonstrate the wringing technique using both hands in a gentle twisting motion."}]',
  control_of_error = 'Clean cloths that are bright and free of stains demonstrate success. Remaining dirt or soap shows the need for continued work.',
  direct_aims = '["To learn how to wash cloth items", "To understand care of materials", "To develop independence in cleaning tasks"]',
  materials = '["Basin with warm soapy water", "Basin with clear water for rinsing", "Small cloths to wash (aprons, dish towels, dusting cloths)", "Drying line or rack", "Clothespins if using a clothesline", "Apron for water protection"]',
  parent_description = 'Your child is learning to wash cloths, a practical care of environment skill. Cloth washing develops hand and arm strength and teaches responsibility for keeping materials clean. This activity demonstrates that effort and care maintain the cleanliness of materials we use daily.',
  why_it_matters = 'Cloth washing is a practical care of environment activity that teaches children to maintain materials and develop independence in cleaning tasks. It develops muscular strength through rubbing and wringing, provides immediate visual feedback as dirt leaves the cloth, and builds understanding that individual effort maintains a clean environment. This activity also connects to historical practices and teaches respect for materials and resources.'
WHERE LOWER(name) = LOWER('Cloth Washing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up supplies: warm soapy water, scrub brush, and towels
• Show how to scrub the entire table surface
• Demonstrate working from outer edges toward the center
• Practice rinsing and drying completely
• Emphasize thoroughness and care',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a basin with warm soapy water, a soft scrub brush, and clean towels for drying.", "tip": "Clear the table completely before beginning to wash it."}, {"step": 2, "title": "Wetting and Scrubbing", "description": "Show how to wet the brush in soapy water and begin scrubbing the table surface.", "tip": "Demonstrate working in organized, overlapping strokes to cover the entire surface."}, {"step": 3, "title": "Working from Edges to Center", "description": "Show how to work from the outer edges toward the center of the table, ensuring complete coverage.", "tip": "Explain that this systematic approach ensures no area is missed."}, {"step": 4, "title": "Rinsing the Table", "description": "Show how to use clean water (or a wet cloth) to rinse away all soap from the table.", "tip": "Demonstrate rinsing multiple times if necessary to remove all soap residue."}, {"step": 5, "title": "Drying the Table", "description": "Show how to use a clean towel to dry the table completely.", "tip": "Explain that a dry table prevents water marks and is more pleasant to use."}]',
  control_of_error = 'A clean, dry table demonstrates success. Remaining dirt, soap, or water spots show the need for continued work.',
  direct_aims = '["To learn how to scrub and clean a table", "To understand care of the classroom environment", "To develop independence in maintaining shared spaces"]',
  materials = '["Basin with warm soapy water", "Soft-bristled brush for scrubbing", "Water for rinsing (basin or pitcher with running water)", "Clean towels for drying", "Table to scrub", "Apron for water protection"]',
  parent_description = 'Your child is learning to scrub and clean a table, an important care of environment activity. Table scrubbing develops physical strength and teaches responsibility for maintaining the classroom. This meaningful work demonstrates that your child''s efforts directly contribute to a clean, pleasant environment.',
  why_it_matters = 'Table scrubbing is a fundamental care of environment activity that teaches practical skills and builds responsibility for shared spaces. It develops arm and hand strength through repetitive motions, provides clear visual feedback showing the results of effort, and builds confidence through completing a meaningful task. This activity demonstrates that systematic, thorough work maintains a clean, pleasant environment for everyone.'
WHERE LOWER(name) = LOWER('Scrubbing a Table');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to hold the food and peeler properly
• Demonstrate controlled peeling strokes
• Practice peeling various fruits and vegetables
• Emphasize safe technique and preventing waste
• Allow for mastery through repetition',
  presentation_steps = '[{"step": 1, "title": "Introduction and Safety", "description": "Show the child appropriate foods to peel (banana, mandarin, hard-boiled egg, carrot) and a child-safe peeler.", "tip": "Start with soft fruits (banana) before progressing to vegetables requiring more pressure."}, {"step": 2, "title": "Demonstrating the Grip", "description": "Show how to hold the fruit or vegetable firmly in one hand and the peeler in the other.", "tip": "Explain that the hand holding the food keeps fingers out of the way of the peeler."}, {"step": 3, "title": "Peeling Technique", "description": "Demonstrate slow, controlled peeling strokes, always moving away from the body.", "tip": "Show that gentle, even pressure works better than hard scraping."}, {"step": 4, "title": "Completing the Process", "description": "Show how to peel until the entire surface is removed and the food is ready to use.", "tip": "Demonstrate checking for spots that need additional peeling."}, {"step": 5, "title": "Cleanup and Disposal", "description": "Show how to dispose of peels properly and clean the work surface.", "tip": "Explain that scraps can go in the compost or trash."}]',
  control_of_error = 'Successfully peeled food is ready to eat. Remaining skin or inefficient peeling shows the need for more work.',
  direct_aims = '["To learn how to peel fruits and vegetables", "To develop fine motor control with tools", "To prepare food for eating"]',
  materials = '["Child-safe peeler (vegetable peeler or apple peeler)", "Appropriate foods to peel (banana, mandarin, carrot, hard-boiled egg)", "Cutting board or work surface", "Trash or compost bin", "Towels for cleanup"]',
  parent_description = 'Your child is learning to peel fruits and vegetables, a foundational food preparation skill. Peeling develops hand strength and coordination while teaching practical meal preparation. This activity builds independence and the satisfaction of contributing to family meals.',
  why_it_matters = 'Peeling is a fundamental food preparation activity that develops fine motor control and practical independence. It teaches children that they can participate in preparing their own food and contributes to the development of healthy eating habits. This activity also demonstrates the connection between raw ingredients and prepared food, and builds confidence in handling food safely.'
WHERE LOWER(name) = LOWER('Peeling');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show proper knife grip and food holding technique
• Demonstrate safe cutting on a cutting board
• Practice with soft foods (banana, bread) before harder foods
• Emphasize safety and control
• Build skills gradually through varied practice',
  presentation_steps = '[{"step": 1, "title": "Safety Introduction", "description": "Establish clear safety rules about knife use. Show appropriate, child-safe knives (blunt or serrated blades).", "tip": "Start with very soft foods that require minimal cutting pressure."}, {"step": 2, "title": "Demonstrating Knife Grip", "description": "Show how to hold the knife firmly but not tensely, with the handle in the palm.", "tip": "Explain that the grip should be comfortable and allow for control."}, {"step": 3, "title": "Food Holding Technique", "description": "Show how to hold the food with fingers curled inward, away from the blade path.", "tip": "Demonstrate that the \"claw\" hand position protects fingers from the knife."}, {"step": 4, "title": "Cutting Technique", "description": "Show slow, controlled cutting motions using a gentle sawing or slicing motion.", "tip": "Demonstrate cutting one piece at a time with full attention and focus."}, {"step": 5, "title": "Safe Completion", "description": "Show how to place the knife down carefully and clean the work surface.", "tip": "Emphasize that knives are always placed with the blade pointing away when not in use."}]',
  control_of_error = 'Successfully cut pieces demonstrate proper technique. Uneven cuts or difficulty with cutting shows the need for practice or a different food.',
  direct_aims = '["To learn to cut and slice food safely", "To develop control with sharp tools", "To prepare food for cooking or eating"]',
  materials = '["Child-safe knife (blunt-serrated or acrylic blade initially)", "Cutting board (wood or plastic)", "Soft foods to cut (banana, bread, cucumber, avocado)", "Progress to harder foods (carrot, apple with core removed)", "Towels for cleanup"]',
  parent_description = 'Your child is learning to cut and slice food safely. Cutting develops hand strength, coordination, and respect for tools. This activity builds confidence and independence in food preparation while establishing safe habits with sharp implements.',
  why_it_matters = 'Cutting and slicing is a practical food preparation activity that teaches children to use sharp tools safely and develop control and focus. Learning proper cutting technique builds confidence with increasingly complex tools and demonstrates that careful attention and safe habits allow for successful use of potentially dangerous items. This activity is foundational for all future cooking and kitchen work.'
WHERE LOWER(name) = LOWER('Cutting/Slicing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to greet a person with eye contact and smile
• Demonstrate saying a pleasant greeting
• Practice respectful tone of voice
• Emphasize genuine, warm interactions
• Model appropriate greetings for different contexts',
  presentation_steps = '[{"step": 1, "title": "Introduction to Greetings", "description": "Explain that greetings are how we show respect and friendliness when we meet someone.", "tip": "Give examples of different greetings (Good morning, Hello, How are you)."}, {"step": 2, "title": "Demonstrating Eye Contact", "description": "Show how to look at the person''s face while greeting them.", "tip": "Explain that looking at someone shows you are paying attention and respect them."}, {"step": 3, "title": "Demonstrating a Smile", "description": "Show how a warm smile makes a greeting more friendly and welcoming.", "tip": "Explain that smiles show that we are happy to see someone."}, {"step": 4, "title": "Speaking the Greeting", "description": "Model saying a pleasant greeting in a warm, respectful tone of voice.", "tip": "Demonstrate different greetings for different times of day (morning, afternoon, evening)."}, {"step": 5, "title": "Practicing with the Child", "description": "Invite the child to greet you, and then greet others in the classroom.", "tip": "Provide gentle guidance and positive reinforcement."}]',
  control_of_error = 'Positive responses from others show successful greetings. Lack of engagement shows the need for adjustment.',
  direct_aims = '["To learn to greet others respectfully", "To develop social awareness and communication", "To understand the importance of courtesy"]',
  materials = '["A safe, calm classroom environment", "Willing adults and children to practice greetings with", "Optional: mirror to observe own greetings"]',
  parent_description = 'Your child is learning to greet others with respect and warmth. Greeting teaches social skills and builds confidence in interactions. This foundational courtesy skill helps your child develop positive relationships throughout their life.',
  why_it_matters = 'Greeting is a fundamental grace and courtesy lesson that teaches children to interact respectfully with others. Learning to greet with eye contact, smile, and warm tone builds confidence in social situations and demonstrates that courtesy creates positive connections. This skill is foundational for all future social and professional interactions.'
WHERE LOWER(name) = LOWER('Greeting');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to wait quietly rather than interrupt
• Teach the signal for getting attention politely (raising hand)
• Demonstrate saying "excuse me" respectfully
• Practice waiting for a response before speaking
• Emphasize respect for others'' work and conversation',
  presentation_steps = '[{"step": 1, "title": "Understanding the Problem", "description": "Explain that interrupting stops others from focusing and is disrespectful.", "tip": "Give examples of interrupting and how it affects others."}, {"step": 2, "title": "Demonstrating Waiting Quietly", "description": "Show how to wait quietly without talking, touching, or fidgeting.", "tip": "Explain that waiting respectfully shows we respect others'' time and work."}, {"step": 3, "title": "Teaching the Polite Signal", "description": "Show how to raise hand gently to get attention, or stand nearby without interrupting.", "tip": "Establish a classroom signal for needing attention."}, {"step": 4, "title": "Demonstrating \"Excuse Me\"", "description": "Show how to say \"excuse me\" in a quiet, respectful tone and wait for a response.", "tip": "Role-play scenarios where the child needs to get an adult''s attention."}, {"step": 5, "title": "Practicing Patience", "description": "Praise the child when they wait patiently rather than interrupting.", "tip": "Acknowledge how respectfully waiting shows maturity and kindness."}]',
  control_of_error = 'Adults responding positively to patient waiting provides feedback. Lack of response to interrupting shows it''s not effective.',
  direct_aims = '["To learn not to interrupt others", "To develop patience and self-control", "To understand respect for others'' focus"]',
  materials = '["Calm classroom environment", "Adults available for role-playing", "Clear classroom norms about getting attention"]',
  parent_description = 'Your child is learning not to interrupt, a crucial social skill. Learning to wait patiently and respect others'' focus shows maturity and builds respectful relationships. This lesson teaches your child that consideration for others'' needs is important.',
  why_it_matters = 'Learning not to interrupt is a fundamental grace and courtesy lesson that teaches children to respect others'' time and focus. It develops impulse control and builds empathy by helping children understand how interruptions affect others. This skill is essential for successful collaboration, learning, and all future relationships.'
WHERE LOWER(name) = LOWER('Interrupting');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to observe mats with work on them
• Demonstrate walking carefully around the work space
• Teach respect for others'' workspace and concentration
• Practice walking with awareness of others
• Emphasize that everyone''s work deserves respect',
  presentation_steps = '[{"step": 1, "title": "Understanding Personal Space", "description": "Explain that when someone is working on a mat, that is their special workspace.", "tip": "Show how mats define personal work areas in the classroom."}, {"step": 2, "title": "Demonstrating Awareness", "description": "Show how to notice when someone is working and plan a path around their workspace.", "tip": "Explain that walking around shows respect for their work and concentration."}, {"step": 3, "title": "Walking Carefully", "description": "Demonstrate walking slowly and carefully, watching feet to avoid stepping near the mat.", "tip": "Show that quiet, mindful walking doesn''t disrupt the person working."}, {"step": 4, "title": "Taking Alternative Routes", "description": "Show how to plan a path around occupied mats, even if it means a longer route.", "tip": "Emphasize that the slight inconvenience respects the other person''s work."}, {"step": 5, "title": "Appreciating Others'' Work", "description": "Show how to observe others'' work with quiet respect, without interrupting or interfering.", "tip": "Praise children for respecting others'' workspace and concentration."}]',
  control_of_error = 'No disruption or interference shows successful mat walking. Stepping on mats or distracting workers shows the need for more practice.',
  direct_aims = '["To learn to respect others'' workspace", "To develop awareness of others while moving", "To understand the importance of concentration"]',
  materials = '["Work mats in classroom", "Children working on various activities", "Calm, orderly classroom environment", "Clear pathways around mats"]',
  parent_description = 'Your child is learning to respect others'' workspace by walking carefully around mats. This lesson teaches awareness of others and builds a respectful classroom community. Walking around mats shows your child that everyone''s work deserves respect and focus.',
  why_it_matters = 'Walking around mats is a fundamental grace and courtesy lesson that teaches children to respect others'' space and concentration. It develops awareness of others'' needs and builds a peaceful, orderly environment where everyone can focus. This skill demonstrates that individual actions affect the community and that respectful behavior creates a positive environment for everyone.'
WHERE LOWER(name) = LOWER('Walking Around Mats');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show proper needle holding and thread manipulation
• Demonstrate threading the needle eye precisely
• Practice steadiness and fine motor control
• Use needle threader if needed for assistance
• Build confidence through successful threading',
  presentation_steps = '[{"step": 1, "title": "Introduction to Sewing", "description": "Explain that sewing is a useful skill for making and repairing clothing.", "tip": "Show examples of sewn items and explain that it starts with threading a needle."}, {"step": 2, "title": "Selecting Supplies", "description": "Choose an appropriately sized needle and thread for the child''s hand size.", "tip": "Use larger needles and thicker thread initially for easier threading."}, {"step": 3, "title": "Demonstrating Threading", "description": "Show how to hold the needle steady in one hand and guide the thread through the eye.", "tip": "Demonstrate slowly and clearly so the child can see the threading motion."}, {"step": 4, "title": "Using a Needle Threader", "description": "If threading is too difficult, show how to use a needle threader as a helpful tool.", "tip": "Explain that using tools to help is smart and practical."}, {"step": 5, "title": "Practicing Repeatedly", "description": "Have the child practice threading multiple times, celebrating successes.", "tip": "Acknowledge that threading takes practice but becomes easier with repetition."}]',
  control_of_error = 'Successfully threaded needle shows the child has learned the skill. Difficulty threading shows the need for practice or a needle threader.',
  direct_aims = '["To learn how to thread a needle", "To develop fine motor precision", "To prepare for sewing activities"]',
  materials = '["Child-sized or larger needles with large eyes", "Colored thread (easier to see)", "Needle threader (for support if needed)", "Needle storage (pin cushion or safe container)", "Embroidery hoop for practicing sewing"]',
  parent_description = 'Your child is learning to thread a needle, the first step toward sewing independence. Threading develops fine motor precision and patience. This practical skill opens the door to sewing projects and repairs that your child can eventually do independently.',
  why_it_matters = 'Threading a needle is the foundational sewing skill that teaches children how thread and needle work together. It develops the fine motor precision and patience necessary for all future sewing work. Learning to thread successfully builds confidence and demonstrates that challenging tasks become easier with practice and persistence. This skill is essential preparation for all sewing activities.'
WHERE LOWER(name) = LOWER('Threading a Needle');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Demonstrate starting from one end and rolling tightly
• Show how to hold the rolled mat in both hands
• Practice smooth, controlled unrolling
• Emphasize order and care in handling
• Allow for repetition to develop coordination',
  presentation_steps = '[{"step": 1, "title": "Observation and Introduction", "description": "Show the child a flat mat and explain that we will learn to roll and unroll it properly.", "tip": "A child-sized mat (2-3 feet wide, 3-4 feet long) works best for beginners."}, {"step": 2, "title": "Starting the Roll", "description": "Demonstrate beginning at one end and rolling the mat tightly toward the other end.", "tip": "Show that starting neatly makes the entire roll tight and organized."}, {"step": 3, "title": "Completing the Roll", "description": "Show how to continue rolling, keeping the mat edges aligned and the roll tight.", "tip": "Demonstrate that a tight roll stays together and is easy to carry."}, {"step": 4, "title": "Unrolling the Mat", "description": "Show the reverse process: carefully unrolling the mat and laying it flat.", "tip": "Demonstrate that unrolling slowly prevents crumpling and keeps the mat neat."}, {"step": 5, "title": "Practicing the Sequence", "description": "Have the child practice rolling and unrolling multiple times.", "tip": "Celebrate successful rolls and unrolls, noting how skills improve with practice."}]',
  control_of_error = 'A tightly rolled mat that unrolls smoothly demonstrates proper technique. A loose or wrinkled mat shows the need for adjustment.',
  direct_aims = '["To learn how to roll and unroll a mat", "To develop coordination and control", "To create order and organization"]',
  materials = '["Child-sized work mat", "Clear floor space", "Storage location for mat", "Optional: music or rhythm to guide rolling"]',
  parent_description = 'Your child is learning to roll and unroll a mat, a key foundation skill for floor work. This activity develops coordination and introduces the concept of sequence and order. It''s one of the first practical life activities a child learns in the Montessori classroom.',
  why_it_matters = 'Rolling and unrolling a mat teaches children about sequence, order, and caring for materials. It develops gross and fine motor control while building the foundation for all floor work activities. This activity is traditionally one of the first practical life lessons, setting the tone for how children interact with materials and spaces throughout their Montessori journey.'
WHERE LOWER(name) = LOWER('Rolling and Unrolling a Mat');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to hold the book properly
• Demonstrate gentle opening of the cover
• Practice opening to specific pages
• Show how to close the book carefully
• Emphasize respect and care for books',
  presentation_steps = '[{"step": 1, "title": "Introduction to Books", "description": "Present a beautifully illustrated book and explain that we will learn to handle it with care.", "tip": "Use a sturdy board book or picture book with thick pages."}, {"step": 2, "title": "Proper Hand Placement", "description": "Show how to hold the book with both hands, supporting it from underneath.", "tip": "Demonstrate that gentle support prevents damage to the binding."}, {"step": 3, "title": "Opening the Cover", "description": "Show how to slowly and carefully open the front cover, allowing it to lie flat.", "tip": "Demonstrate respect for the binding by opening gently and gradually."}, {"step": 4, "title": "Opening to Pages", "description": "Show how to turn pages from front to back, opening the book fully.", "tip": "Explain that we turn pages to see the pictures and words inside."}, {"step": 5, "title": "Closing the Book", "description": "Demonstrate gently closing pages and then the cover, completing the activity.", "tip": "Show that closing is done with the same care as opening."}]',
  control_of_error = 'A book opened carefully that closes without damage shows proper handling. Torn pages or damaged binding shows rough handling.',
  direct_aims = '["To learn how to open and close a book properly", "To develop respect for books", "To prepare for reading activities"]',
  materials = '["Board books or picture books with thick pages", "Beautifully illustrated books", "Books that are age-appropriate and engaging", "A calm space for exploring books"]',
  parent_description = 'Your child is learning to open and close books carefully. This foundational skill teaches respect for reading materials and prepares your child for all future learning. Proper book handling ensures books last and demonstrates care for the tools of learning.',
  why_it_matters = 'Opening and closing a book properly teaches children to respect reading materials from the very beginning. It develops gentle hand control and fosters a positive relationship with books. This skill demonstrates that reading and learning are valued activities worth careful, respectful handling. Early respect for books often leads to a lifelong love of reading and learning.'
WHERE LOWER(name) = LOWER('Opening and Closing a Book');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Demonstrate putting on clothing items independently
• Show how to remove clothing and place in appropriate locations
• Practice with different clothing types
• Emphasize the sequence and organization
• Build confidence through successful dressing',
  presentation_steps = '[{"step": 1, "title": "Selecting Appropriate Clothing", "description": "Begin with easy-to-manage clothing (pull-on pants, simple shirts, slip-on shoes).", "tip": "Avoid complicated fastenings initially; focus on removing and donning items."}, {"step": 2, "title": "Demonstrating Undressing", "description": "Show how to remove clothing items one at a time and place them in designated locations.", "tip": "Demonstrate that dirty clothes go in one place, clean clothes in another."}, {"step": 3, "title": "Demonstrating Dressing", "description": "Show how to put on clothing items in a logical sequence (underwear, pants, shirt, shoes).", "tip": "Explain the sequence so the child understands the order."}, {"step": 4, "title": "Managing Difficult Items", "description": "Show how to manage trickier items (pulling sleeves, getting feet through pant legs).", "tip": "Demonstrate techniques that make dressing easier (step into pants, push arms through sleeves)."}, {"step": 5, "title": "Checking and Practicing", "description": "Have the child practice with adult support as needed.", "tip": "Celebrate successes and provide gentle guidance for challenging moments."}]',
  control_of_error = 'Successfully dressed child with appropriate clothing shows success. Backward or incorrect clothing shows the need for more practice.',
  direct_aims = '["To learn to dress independently", "To develop sequencing and planning", "To build self-care independence"]',
  materials = '["Child-sized, easy-to-manage clothing", "Mirror at child height", "Clothing storage at child height", "Optional: picture cards showing dressing sequence"]',
  parent_description = 'Your child is developing independence in dressing, a crucial self-care skill. Learning to dress independently builds confidence, self-esteem, and practical life skills. This activity demonstrates that your child is capable of caring for themselves.',
  why_it_matters = 'Self-dressing is a fundamental independence skill that teaches children to manage their own bodies and prepare for daily activities. It develops body awareness, sequencing skills, and problem-solving while building self-esteem and confidence. Learning to dress independently is a major milestone that demonstrates growing competence and self-reliance.'
WHERE LOWER(name) = LOWER('Self-Dressing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up containers with dry material (beans, rice, lentils)
• Show how to hold the pitcher and pour steadily
• Demonstrate pouring with control to avoid spills
• Practice transferring from one container to another
• Emphasize precision and limiting waste',
  presentation_steps = '[{"step": 1, "title": "Preparation and Setup", "description": "Set up a tray with two containers and dry material (beans, rice, or lentils).", "tip": "Use a small pitcher or cup that is easy for small hands to manage."}, {"step": 2, "title": "Demonstrating the Grip", "description": "Show how to hold the pitcher with a firm grip, supporting from underneath.", "tip": "Explain that a good grip prevents spilling and provides control."}, {"step": 3, "title": "Pouring Technique", "description": "Demonstrate pouring slowly and steadily, keeping the pitcher over the receiving container.", "tip": "Show that slow, controlled pouring prevents splashing and waste."}, {"step": 4, "title": "Controlling the Flow", "description": "Show how to tilt the pitcher to control the amount of material being poured.", "tip": "Demonstrate that slight tilting prevents overflow."}, {"step": 5, "title": "Repeating the Activity", "description": "Show how the activity continues: fill, pour, refill, pour again.", "tip": "Explain that this activity can continue indefinitely with the same material."}]',
  control_of_error = 'Clean, spill-free pouring demonstrates proper technique. Spilled material shows insufficient control.',
  direct_aims = '["To learn to pour dry material with control", "To develop hand-eye coordination", "To practice precise movements"]',
  materials = '["Two containers (bowls or cups)", "Dry material (beans, rice, lentils, dried pasta)", "Small pitcher or pouring vessel", "A tray to contain spills", "Optional: funnel to aid pouring"]',
  parent_description = 'Your child is learning to pour dry materials with control. Dry pouring develops hand-eye coordination and precision before introducing the complexity of liquid pouring. This foundational activity teaches control and prepares your child for cooking and other practical tasks.',
  why_it_matters = 'Dry pouring is an essential preliminary transfer activity that teaches children precise, controlled hand movements. It develops hand-eye coordination and demonstrates cause and effect with gravity. Learning to pour without spilling builds confidence and establishes the foundation for all future pouring activities, including wet pouring and cooking.'
WHERE LOWER(name) = LOWER('Dry Pouring');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set up containers with water and pouring pitcher
• Show proper pouring grip and technique
• Demonstrate controlled pouring into receiving container
• Practice filling to different levels
• Emphasize precision and preventing spills',
  presentation_steps = '[{"step": 1, "title": "Setup and Safety", "description": "Set up a water table or low surface with water, pitchers, and containers.", "tip": "Ensure water is room temperature and the area is waterproof. Provide towels for cleanup."}, {"step": 2, "title": "Prerequisites", "description": "Ensure child has mastered dry pouring before introducing water.", "tip": "Dry pouring skills transfer to water pouring but water requires more precise control."}, {"step": 3, "title": "Demonstrating the Technique", "description": "Show slow, controlled pouring of water into a receiving container.", "tip": "Demonstrate that water requires even more careful control than dry materials."}, {"step": 4, "title": "Controlling Spillage", "description": "Show how to pour carefully over the receiving container to minimize splashing.", "tip": "Explain that some splashing is normal, but we try to minimize it through control."}, {"step": 5, "title": "Cleanup and Repetition", "description": "Show how to clean up spills and repeat the activity.", "tip": "Demonstrate that the activity continues until all containers are used or child chooses to stop."}]',
  control_of_error = 'Water level in containers shows success. Spilled water demonstrates the need for more precise control.',
  direct_aims = '["To learn to pour water with control", "To develop precise hand-eye coordination", "To understand liquid transfer"]',
  materials = '["Water table or low, waterproof surface", "Small pitchers for pouring", "Various containers (cups, bowls, small pitchers)", "Water (room temperature)", "Towels and sponges for cleanup", "Apron for water protection", "Optional: food coloring to make water more visible"]',
  parent_description = 'Your child is learning to pour water with precise control. Water pouring is more challenging than dry pouring and requires focus and coordination. This activity develops the motor skills essential for cooking, food preparation, and other practical life activities.',
  why_it_matters = 'Water pouring is a sophisticated transfer activity that teaches children to control liquids, which are more challenging than solids. It develops the precise motor control and hand-eye coordination needed for cooking and other practical kitchen tasks. Successfully pouring water builds confidence and demonstrates that mastery comes through practice and focused effort.'
WHERE LOWER(name) = LOWER('Water Pouring');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to remove clothing items one at a time
• Demonstrate proper placement of dirty clothes
• Practice with various clothing types
• Emphasize organization and care
• Build independence in clothing management',
  presentation_steps = '[{"step": 1, "title": "Preparation", "description": "Set up a designated area for dirty clothes and a basket or hamper.", "tip": "Use picture labels to show where clothes should go."}, {"step": 2, "title": "Demonstrating Removal", "description": "Show how to remove clothing items one at a time (shirt, pants, socks, shoes).", "tip": "Demonstrate slow, careful removal to avoid pulling hair or twisting limbs."}, {"step": 3, "title": "Placing Dirty Clothes", "description": "Show how to place removed clothes in the dirty clothes basket.", "tip": "Explain that dirty clothes go together so they can be washed."}, {"step": 4, "title": "Complete Undressing", "description": "Demonstrate removing all clothing items and placing them appropriately.", "tip": "Show that we keep order in the process just as with dressing."}, {"step": 5, "title": "Practicing Independently", "description": "Have the child practice undressing with guidance as needed.", "tip": "Celebrate successful undressing and proper placement of clothes."}]',
  control_of_error = 'Properly undressed child with clothes placed correctly shows success. Clothes left on floor or in wrong place shows need for guidance.',
  direct_aims = '["To learn to undress independently", "To develop organization and care for clothing", "To build self-care independence"]',
  materials = '["Designated area for undressing (bathroom, bedroom)", "Dirty clothes basket or hamper", "Optional: picture labels for clothing placement", "Mirror at child height"]',
  parent_description = 'Your child is learning to undress independently and manage their clothing. Undressing teaches organization, self-care, and independence. This practical life skill develops responsibility for personal belongings.',
  why_it_matters = 'Undressing is a fundamental self-care skill that builds independence and organization. Learning to remove clothing and properly place it develops responsibility for belongings and introduces laundry concepts. This activity teaches that managing one''s clothing is an important part of self-care and home management.'
WHERE LOWER(name) = LOWER('Undressing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to prepare bathwater safely
• Demonstrate washing body systematically
• Practice using soap and washcloth
• Teach drying and putting on clean clothes
• Emphasize water safety and independence',
  presentation_steps = '[{"step": 1, "title": "Water Preparation", "description": "Show how to fill the bath with appropriate water (warm, not hot) and depth.", "tip": "Adult supervision is essential; child learns by observing and helping."}, {"step": 2, "title": "Getting in the Bath", "description": "Show how to safely enter the bath, using a step stool if needed.", "tip": "Demonstrate care in stepping over the edge and sitting carefully."}, {"step": 3, "title": "Washing the Body", "description": "Show how to wash systematically: face, arms, torso, legs, feet.", "tip": "Demonstrate using soap and washcloth to clean all areas."}, {"step": 4, "title": "Rinsing", "description": "Show how to rinse away all soap using water from the tub or shower.", "tip": "Explain that rinsing removes dirt and soap, leaving skin clean."}, {"step": 5, "title": "Exiting and Drying", "description": "Demonstrate how to carefully exit the bath and dry thoroughly with a towel.", "tip": "Show that drying completely prevents chilling and discomfort."}]',
  control_of_error = 'Clean, dry skin and child''s feeling of comfort demonstrate success. Remaining soap or dirt shows incomplete washing.',
  direct_aims = '["To learn proper bathing techniques", "To develop independence in personal hygiene", "To understand the importance of cleanliness"]',
  materials = '["Bathtub or child-sized bath", "Step stool for accessing tub", "Warm water (temperature controlled by adult)", "Gentle soap and washcloth", "Towels for drying", "Clean clothes for after bathing", "Adult supervision at all times"]',
  parent_description = 'Your child is learning to bathe, an essential daily hygiene activity. Bathing teaches cleanliness, safety, and independence while building comfort with water. This practical life skill supports health and wellness.',
  why_it_matters = 'Bathing is a fundamental self-care activity that teaches children to maintain personal hygiene and develop independence in daily routines. Learning proper bathing techniques, combined with water safety, creates healthy habits that protect children''s wellbeing. This activity also builds confidence in managing self-care tasks independently while developing an understanding that regular cleanliness supports health and comfort.'
WHERE LOWER(name) = LOWER('Bathing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to set a table with dishes and utensils
• Demonstrate serving food to oneself and others
• Teach how to pass dishes appropriately
• Practice clearing dishes after meals
• Emphasize care and consideration',
  presentation_steps = '[{"step": 1, "title": "Preparing the Table", "description": "Show how to place placemats, plates, cups, napkins, and utensils correctly.", "tip": "Use a simple place setting appropriate for young children."}, {"step": 2, "title": "Bringing Food to Table", "description": "Demonstrate carrying food and placing it where it is accessible.", "tip": "Show careful, controlled movement to prevent spills."}, {"step": 3, "title": "Serving Food", "description": "Show how to use serving utensils to place food on a plate or pass dishes.", "tip": "Demonstrate that serving is done thoughtfully, considering others'' needs."}, {"step": 4, "title": "Eating Together", "description": "Show how to enjoy the meal together, with proper table manners.", "tip": "Emphasize that meals are times for family or community connection."}, {"step": 5, "title": "Clearing the Table", "description": "Demonstrate clearing dishes, scraping food, and washing up.", "tip": "Show that caring for the meal space is everyone''s responsibility."}]',
  control_of_error = 'A properly set table with served meals and clean-up afterward shows success. Disorder or incomplete service shows need for guidance.',
  direct_aims = '["To learn meal preparation and service skills", "To develop independence in food preparation", "To understand how to care for eating spaces"]',
  materials = '["Child-sized table and chairs", "Dishes, cups, utensils (child-appropriate)", "Napkins and placemats", "Food to serve", "Serving utensils", "Towels for cleanup"]',
  parent_description = 'Your child is learning to prepare and serve meals, practical skills that contribute to family life. Meal preparation and serving develop independence, responsibility, and an understanding that feeding the family is a shared responsibility.',
  why_it_matters = 'Meal preparation and serving teaches children practical independence and builds understanding of their role in family and community. Learning to set a table, serve food, and clear dishes demonstrates that each person contributes meaningfully to shared meals. This activity builds responsibility, confidence, and understanding that meals are important times for connection and nourishment.'
WHERE LOWER(name) = LOWER('Meal Preparation and Serving');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to say "please" when asking for something
• Demonstrate saying "thank you" when receiving
• Teach grateful tone and facial expression
• Practice in various situations
• Model courteous language consistently',
  presentation_steps = '[{"step": 1, "title": "Introduction to Courtesy Words", "description": "Explain that \"please\" and \"thank you\" show respect and gratitude.", "tip": "Give examples of when to use these words in daily life."}, {"step": 2, "title": "Demonstrating \"Please\"", "description": "Show how to ask for something politely using the word \"please.\"", "tip": "Demonstrate that asking with please is more effective than demanding."}, {"step": 3, "title": "Demonstrating \"Thank You\"", "description": "Show how to express gratitude when someone gives or helps.", "tip": "Explain that saying thank you shows appreciation."}, {"step": 4, "title": "Using Tone and Expression", "description": "Demonstrate that genuine tone and kind expression make these words more meaningful.", "tip": "Show that how we say something matters as much as the words."}, {"step": 5, "title": "Practicing Together", "description": "Role-play situations where please and thank you are used.", "tip": "Praise the child for using these courteous words naturally."}]',
  control_of_error = 'Positive responses from others show that please and thank you are effective. Lack of response might indicate need for more genuine or appropriate use.',
  direct_aims = '["To learn to say please and thank you", "To develop gratitude and respect", "To understand courtesy in communication"]',
  materials = '["Willing adults and children for role-playing", "Situations where these words are naturally used", "Calm classroom environment", "Optional: picture cards showing please and thank you"]',
  parent_description = 'Your child is learning to say please and thank you, fundamental courtesy words that show respect and gratitude. These simple words open doors and create positive relationships. Teaching your child these words is teaching valuable lessons about respect and appreciation.',
  why_it_matters = 'Learning to say please and thank you teaches children to express gratitude and make respectful requests. These foundational courtesy words demonstrate that appreciation and respect create positive interactions. Learning to use these words genuinely builds empathy and understanding that courtesy affects how others respond to us, creating a foundation for all positive relationships.'
WHERE LOWER(name) = LOWER('Saying Thank You and Please');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to water plants gently
• Demonstrate caring for classroom animals
• Teach responsibility and observation
• Practice consistent care routines
• Build understanding that living things need care',
  presentation_steps = '[{"step": 1, "title": "Understanding Living Needs", "description": "Explain that plants and animals need water, food, and care to survive.", "tip": "Use observations to help the child understand what plants/animals need."}, {"step": 2, "title": "Watering Plants", "description": "Show how to pour water at the base of plants without splashing leaves.", "tip": "Demonstrate that we give plants enough water but don''t drown them."}, {"step": 3, "title": "Observing and Recording", "description": "Show how to observe plants'' growth and note changes.", "tip": "Use pictures or charts to track plant growth."}, {"step": 4, "title": "Caring for Classroom Animals", "description": "If present, show how to feed, water, and observe classroom pets safely.", "tip": "Teach gentle touch and respect for animals."}, {"step": 5, "title": "Creating Routines", "description": "Establish daily care routines so children remember to care for living things.", "tip": "Use charts or schedules to support regular care."}]',
  control_of_error = 'Healthy plants and animals demonstrate good care. Wilting plants or sick animals show insufficient care.',
  direct_aims = '["To learn how to care for plants and animals", "To develop responsibility and observation", "To understand what living things need"]',
  materials = '["Classroom plants suitable for children to care for", "Watering can or cup", "Optional: classroom animals (fish, insects, small mammals)", "Food and water containers for animals", "Observation charts or journals", "Gentle handling tools if needed"]',
  parent_description = 'Your child is learning to care for living things, developing responsibility and empathy. Caring for plants and animals teaches that we depend on nature and that our actions affect other living beings. This activity builds a foundation for environmental awareness and respect.',
  why_it_matters = 'Caring for living things teaches children responsibility, observation, and empathy. It develops understanding that all living things have needs and that we play a role in meeting those needs. This activity connects children to nature and builds awareness of their role in the ecosystem, fostering environmental consciousness and a sense of stewardship that lasts a lifetime.'
WHERE LOWER(name) = LOWER('Care of Living Things');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show proper placement of plates and bowls
• Demonstrate utensil, cup, and napkin placement
• Practice creating a complete place setting
• Teach that order creates welcoming meals
• Build understanding of preparation and care',
  presentation_steps = '[{"step": 1, "title": "Introduction to Table Setting", "description": "Explain that a properly set table shows care and welcomes people to eat.", "tip": "Show a picture or example of a set table."}, {"step": 2, "title": "Placing the Placemat", "description": "Demonstrate positioning a placemat to define each person''s space.", "tip": "Explain that placemats create order and protect the table."}, {"step": 3, "title": "Placing Dishes and Utensils", "description": "Show how to place a plate, bowl, cup, utensils, and napkin in correct positions.", "tip": "Use a simple setting appropriate for young children."}, {"step": 4, "title": "Creating the Complete Setting", "description": "Demonstrate setting a complete place that is ready for a meal.", "tip": "Show that everything needed is in place for comfortable eating."}, {"step": 5, "title": "Reviewing and Practicing", "description": "Have the child practice setting places independently with guidance.", "tip": "Celebrate successful, complete place settings."}]',
  control_of_error = 'A complete place setting with all items properly positioned shows success. Missing items or incorrect placement shows need for guidance.',
  direct_aims = '["To learn how to set a table", "To develop organization skills", "To prepare welcoming meals"]',
  materials = '["Child-sized dishes and utensils", "Placemats", "Napkins", "Cups or bowls", "A table for practicing", "Optional: picture guide for place setting"]',
  parent_description = 'Your child is learning to set a table, a practical skill that prepares welcoming meals. Table setting teaches organization, care, and the importance of preparation. This activity shows your child''s ability to contribute meaningfully to family meals.',
  why_it_matters = 'Setting a table teaches children about order, organization, and the care that goes into preparing spaces for shared meals. It demonstrates that preparation and attention to detail create welcoming environments. This activity builds pride in contributing to family life and shows that small gestures of care and organization demonstrate respect and hospitality.'
WHERE LOWER(name) = LOWER('Setting a Table');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to recognize when help is needed
• Demonstrate asking politely and respectfully
• Teach appropriate tone and language
• Practice in various situations
• Model asking for help yourself',
  presentation_steps = '[{"step": 1, "title": "Introduction and Modeling", "description": "Explain that asking for help is a sign of wisdom, not weakness.", "tip": "Give examples of when adults ask for help and how it''s done respectfully."}, {"step": 2, "title": "Recognizing the Need", "description": "Teach the child to recognize situations where help would be beneficial.", "tip": "Help identify when something is too difficult or when two hands are needed."}, {"step": 3, "title": "Polite Language", "description": "Demonstrate how to ask using polite language and respectful tone.", "tip": "Model phrases like ''Can you help me please?'' or ''I need help with...''"}, {"step": 4, "title": "Receiving Help", "description": "Show how to accept help gracefully and thank the helper.", "tip": "Explain that gratitude for help strengthens relationships."}, {"step": 5, "title": "Practicing Together", "description": "Role-play scenarios where help is asked for and provided.", "tip": "Praise the child for asking respectfully and accepting help."}]',
  control_of_error = 'Positive responses from helpers show that asking respectfully works. Refusals might indicate need for better timing or approach.',
  direct_aims = '["To learn to ask for help appropriately", "To develop respectful communication", "To understand that help is available"]',
  materials = '["Willing adults and children for role-playing", "Situations where help might be needed", "Patient, supportive environment", "Optional: picture cards showing asking for help"]',
  parent_description = 'Your child is learning to ask for help politely. This important social skill teaches that asking for help is acceptable and builds strong relationships. Teaching your child to seek help when needed fosters confidence and community connections.',
  why_it_matters = 'Learning to ask for help appropriately teaches children that seeking assistance is a sign of wisdom, not weakness. It builds communication skills and demonstrates that communities are built on mutual support and interdependence. This lesson creates confidence in asking for what is needed and fosters relationships based on trust, respect, and reciprocal support.'
WHERE LOWER(name) = LOWER('Asking for Help Politely');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Show how to sort dirty clothes
• Demonstrate placing clothes in washer
• Teach loading the dryer
• Practice folding clean clothes
• Build complete laundry independence',
  presentation_steps = '[{"step": 1, "title": "Sorting Laundry", "description": "Show how to sort clothes into piles (dark, light, delicate) before washing.", "tip": "Explain that colors don''t bleed if sorted properly."}, {"step": 2, "title": "Loading the Washer", "description": "Demonstrate placing clothes in the washer and adding soap.", "tip": "Show age-appropriate responsibilities with washer controls."}, {"step": 3, "title": "Moving to Dryer", "description": "Show how to transfer wet clothes to the dryer carefully.", "tip": "Explain that some clothes air dry instead of using the dryer."}, {"step": 4, "title": "Folding Clean Clothes", "description": "Demonstrate folding different clothing items and placing them in storage.", "tip": "Start with simple items (socks, towels) before progressing to complex pieces."}, {"step": 5, "title": "Complete Laundry Management", "description": "Show the full cycle from dirty clothes to folded storage.", "tip": "Praise the child''s contribution to family clothing care."}]',
  control_of_error = 'Clean, properly folded clothes in storage demonstrates success. Poorly sorted or inadequately cleaned clothes shows need for more practice.',
  direct_aims = '["To learn complete laundry management", "To develop independence in clothing care", "To understand how families function together"]',
  materials = '["Washing machine (or access to washing facilities)", "Clothesline or dryer", "Detergent (age-appropriate amounts)", "Laundry baskets", "Clothespins if line drying", "Optional: sorting baskets by color"]',
  parent_description = 'Your child is learning laundry management, a critical self-care and household skill. Laundry teaches responsibility for clothing and contributes meaningfully to family functioning. This practical skill builds independence and demonstrates the child''s ability to manage significant household tasks.',
  why_it_matters = 'Laundry is a comprehensive practical life activity that teaches children complete responsibility for clothing care from dirty to clean to storage. It develops independence and demonstrates that children can manage substantial household tasks. This activity builds pride in contributing to family functioning and creates habits of responsibility and cleanliness that support independence throughout life. Learning laundry skills shows that children are capable of managing complex, multi-step tasks essential to daily living.'
WHERE LOWER(name) = LOWER('Laundry');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Four progressive blocks isolate different sensory dimensions (height, diameter, both variables)
• Block 1: Height variation with constant diameter develops thickness discrimination
• Block 2: Diameter variation with constant height prepares for width understanding
• Block 3: Inverse relationship between diameter and height challenges discrimination
• Block 4: Both dimensions decrease, introducing complexity and refined comparison skills',
  presentation_steps = '[{"step": 1, "title": "Invitation and Preparation", "description": "Invite the child to join the lesson on Cylinder Blocks. Show the child how to carefully pick up and carry one block by the ends using both hands.", "tip": "Use respectful language and model slow, deliberate movement."}, {"step": 2, "title": "Positioning the Block", "description": "Sit to the right of the child. Orient Block 1 so the thickest (tallest) cylinder is on the left side of the block.", "tip": "This left-to-right orientation aligns with reading direction."}, {"step": 3, "title": "Removing Cylinders", "description": "Using a three-fingered grasp on the knob (thumb, index, middle finger), gently remove cylinders starting from the left.", "tip": "Remove all cylinders before the child attempts replacement."}, {"step": 4, "title": "Inviting the Child''s Turn", "description": "Offer the child an invitation to remove the cylinders using the same technique.", "tip": "Focus on the child''s concentration and confidence."}, {"step": 5, "title": "Replacing Cylinders", "description": "Demonstrate replacing cylinders by starting with one extreme, hovering over holes, and gently lowering.", "tip": "Move slowly and deliberately."}, {"step": 6, "title": "Child Replaces Cylinders", "description": "Invite the child to replace all cylinders into Block 1 independently.", "tip": "Do not intervene unless asked."}, {"step": 7, "title": "Vocabulary Introduction", "description": "Once worked extensively, use three-period lesson for vocabulary: thick/thin, wide/narrow, tall/short.", "tip": "Introduce language only after sensorial familiarity."}]',
  control_of_error = 'Cylinders don''t fit or one left over',
  direct_aims = '["Visual discrimination of diameter", "Fine motor - pencil grip"]',
  materials = '["Cylinder Block 1 with 10 cylinders"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Cylinder Block 1');

