UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Pairs of wooden cylinders filled with different materials creating distinct sounds
• Develops auditory discrimination from loud to quiet and fine listening skills
• Supports concentration, memory, and auditory processing development
• Introduced around age 3.5-4 years in a quiet environment
• Control of error: matching pairs produce identical sounds when shaken correctly',
  presentation_steps = '[{"step": 1, "title": "Environment Setup", "description": "Prepare quiet, peaceful environment. Invite gently.", "tip": "Auditory activities require minimal background noise."}, {"step": 2, "title": "Introduction", "description": "Begin with loudest and quietest pairs only.", "tip": "Start with extreme contrasts."}, {"step": 3, "title": "Listening Technique", "description": "Hold cylinder by ends, shake gently near ear. Model focused listening.", "tip": "Set expectation for auditory attention."}, {"step": 4, "title": "Child Listens", "description": "Give cylinder to child to shake and listen.", "tip": "Child listens intently before matching."}, {"step": 5, "title": "Matching Game", "description": "Mix cylinders, child finds matching pairs by sound.", "tip": "Develops systematic problem-solving."}, {"step": 6, "title": "Gradation", "description": "Arrange cylinders from loudest to quietest.", "tip": "Creates sound gradient parallel to visual gradients."}]',
  control_of_error = 'Sounds don''t match',
  direct_aims = '["Auditory discrimination", "Vocabulary: loud/soft, louder/softer"]',
  materials = '["Sound Boxes (2 matching sets of 6 cylinders)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Sound Boxes (Sound Cylinders)');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Set of bells producing musical scale tones for auditory discrimination
• Two sets: brown for grading, white for matching
• Develops ability to distinguish pitch differences
• Prepares for music education and ear training
• Struck with wooden mallet for clear tone',
  presentation_steps = '[{"step": 1, "title": "Environment", "description": "Create quiet environment. Focus on listening.", "tip": "Auditory work needs quiet."}, {"step": 2, "title": "Striking Technique", "description": "Hold mallet correctly. Strike bell base once, listen.", "tip": "Let sound ring fully."}, {"step": 3, "title": "Matching Introduction", "description": "Strike one brown bell, one white bell. ''Same or different?''", "tip": "Start with obvious contrasts."}, {"step": 4, "title": "Finding Pairs", "description": "Match white bells to brown bells by sound.", "tip": "One pair at a time."}, {"step": 5, "title": "Grading", "description": "Arrange bells from lowest to highest pitch.", "tip": "Musical scale emerges."}, {"step": 6, "title": "Vocabulary", "description": "Introduce high/low, higher/lower, music note names.", "tip": "After auditory familiarity."}]',
  control_of_error = 'Pitches don''t match',
