  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Montessori Bells');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Paired bottles containing different scents for olfactory matching
• Child matches identical scents without seeing contents
• Develops sense of smell and olfactory discrimination
• Uses natural scents: vanilla, mint, lavender, lemon, etc.
• Prepares for science, cooking, and environmental awareness',
  presentation_steps = '[{"step": 1, "title": "Introduction", "description": "Present smelling bottles. ''Let''s use our noses to discover.''", "tip": "Natural, pleasant scents."}, {"step": 2, "title": "Smelling Technique", "description": "Open cap, hold near nose, waft gently. ''Breathe softly.''", "tip": "Don''t sniff too hard."}, {"step": 3, "title": "First Scent", "description": "Smell one bottle. ''What do you notice?''", "tip": "Describe the experience."}, {"step": 4, "title": "Finding Match", "description": "Find the bottle that smells the same.", "tip": "Compare carefully."}, {"step": 5, "title": "All Pairs", "description": "Match all pairs by scent.", "tip": "Rest nose between scents."}, {"step": 6, "title": "Identification", "description": "Name scents: vanilla, lemon, lavender, etc.", "tip": "After matching mastery."}]',
  control_of_error = 'Scents don''t match',
  direct_aims = '["Olfactory discrimination", "Scent identification"]',
  materials = '["Smelling Bottles (2 matching sets of 6 bottles)", "Various scent materials"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Smelling Bottles');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Dropper bottles containing different taste solutions
• Four basic tastes: sweet, salty, sour, bitter
• Develops gustatory sense and taste discrimination
• One drop on tongue is sufficient for identification
• Connects to cooking, nutrition, and science',
  presentation_steps = '[{"step": 1, "title": "Preparation", "description": "Prepare: water for rinsing, small cups or spoons.", "tip": "Clean palate between tastes."}, {"step": 2, "title": "First Taste", "description": "Place one drop of sweet solution on tongue. ''How does it taste?''", "tip": "Start with pleasant tastes."}, {"step": 3, "title": "Vocabulary", "description": "Name the taste: ''This is sweet.''", "tip": "Clear identification."}, {"step": 4, "title": "Contrast", "description": "Try sour after sweet. Notice difference.", "tip": "Rinse mouth between."}, {"step": 5, "title": "Four Tastes", "description": "Introduce all four: sweet, salty, sour, bitter.", "tip": "Bitter may be unpleasant."}, {"step": 6, "title": "Matching", "description": "Match pairs of taste bottles.", "tip": "After vocabulary learned."}]',
  control_of_error = 'Taste identification incorrect',
  direct_aims = '["Gustatory discrimination", "Vocabulary: sweet, sour, salty, bitter"]',
  materials = '["Tasting containers", "Dropper", "Solutions: sweet, sour, salty, bitter"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Tasting Bottles/Cups');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Non-transparent cloth bag with hand opening for tactile exploration without vision
• Contains 8-10 familiar objects (eraser, dice, cork, spoon, marble, pencil, etc.)
• Develops stereognostic sense: ability to identify objects through touch alone
• Presented around age 3-5 years, supporting fine motor and cognitive development
• Progression: matching pairs, identifying by description, complex discrimination',
  presentation_steps = '[{"step": 1, "title": "Introduction", "description": "Prepare bag with 8-10 familiar objects. Show the bag, explain exploration by touch.", "tip": "Start with very familiar objects."}, {"step": 2, "title": "Exploring the Opening", "description": "Guide child''s hand to opening, feel what''s inside without removing.", "tip": "Builds comfort before identification."}, {"step": 3, "title": "Demonstration", "description": "Find an object, describe its properties, remove and reveal.", "tip": "Narrate thinking process."}, {"step": 4, "title": "Child''s First Experience", "description": "Invite child to feel, describe, and guess object.", "tip": "Encourage tactile exploration and verbal description."}, {"step": 5, "title": "Matching Pairs", "description": "Place two identical objects inside; child finds matching pair by touch.", "tip": "Develops tactile memory."}, {"step": 6, "title": "Finding by Description", "description": "Describe object without naming, child finds by touch.", "tip": "Develops listening and tactile discrimination."}]',
  control_of_error = 'Object identification incorrect',
  direct_aims = '["Stereognostic sense development", "Mental visualization"]',
  materials = '["Mystery Bag (cloth bag)", "Various familiar objects"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Mystery Bag');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Present a mystery bag filled with varied objects
• Child feels objects inside bag without looking
• Identify and name each object through touch alone
• Sort objects by material or type
• Build confidence in tactile recognition',
  presentation_steps = '[{"step": 1, "title": "Invitation and Mystique", "description": "Invite the child with intrigue: ''I have a special bag filled with mystery objects. I''d like to play a guessing game with you. You get to feel inside the bag and guess what things are\u2014without looking!'' Show the child the bag, creating anticipation and interest.", "tip": "The element of mystery and play makes this stereognostic work engaging and fun."}, {"step": 2, "title": "Exploring the Objects", "description": "First, with eyes open, show the child all the objects before they go into the bag. Present each one: ''This is a feather. How does it feel? This is a key. How does that feel?'' Invite the child to handle and examine each object, building familiarity.", "tip": "Visual and tactile exploration before the challenge builds confidence and sensory memory."}, {"step": 3, "title": "Preparing for Blindfolded Work", "description": "Place all objects into the mystery bag. Say: ''Now that you''ve felt all these objects, let''s see if you can recognize them just by feeling them\u2014without looking.'' Offer a blindfold or suggest closing eyes: ''We''ll cover your eyes so you can really focus on how things feel.''", "tip": "Framing it as a focusing technique rather than a restriction makes children more comfortable."}, {"step": 4, "title": "First Identification", "description": "Guide the child''s hand into the bag: ''Pick one object. Feel it carefully. Feel the whole thing\u2014the shape, the texture, the weight. Can you tell me what it is?'' Give them time to explore before answering.", "tip": "Allow extended exploration time; rushing prevents full sensory analysis and enjoyment."}, {"step": 5, "title": "Naming and Description", "description": "Once the child identifies an object, have them name it and describe how it feels: ''That''s a key! How does it feel?'' (Child might say: ''Hard and cold and bumpy.'') Celebrate the identification: ''You knew it was a key just by feeling it!''", "tip": "Verbal description of tactile sensations builds sensory vocabulary and confidence."}, {"step": 6, "title": "Removing and Continuing", "description": "The child removes the identified object from the bag. Set it aside. Return to the bag for another object: ''Now find another mystery object and tell me what it is.'' Continue until all objects are identified and removed.", "tip": "Removing objects as they''re identified keeps the bag from becoming overwhelming."}, {"step": 7, "title": "Verification and Sorting", "description": "Once all objects are identified, the child can remove the blindfold and verify. Optional: organize objects by categories (soft things, hard things, metal things, natural things) to extend the learning.", "tip": "Sorting by type builds classification skills while celebrating successful identification."}]',
  control_of_error = 'The child''s sensory perception provides self-correction. When the child identifies an object and removes it from the bag, they can verify their identification when they remove the blindfold. Incorrect identifications become obvious when the child sees the object they were holding.',
  direct_aims = '["Develop and refine the stereognostic sense", "Build the ability to recognize objects through tactile exploration alone", "Develop detailed tactile discrimination", "Build sensory memory and association"]',
  materials = '["Cloth bag (large enough for hand to reach inside comfortably)", "8-12 varied objects such as: feather, key, small rock, marble, pencil, wooden block, piece of fabric, ribbon, small bell, cork, pinecone, sponge", "Soft blindfold or fabric for eye covering", "Work surface for organizing sorted objects"]',
  parent_description = 'When children feel objects in a bag without looking, they''re developing their stereognostic sense—the ability to recognize things by touch alone. This is a fun game that builds real sensory skill. As children carefully feel each object, they''re learning to use their hands to ''see'' and identify things. This kind of sensory development is important for all kinds of learning and helps children feel more confident in their ability to understand the world around them.',
  why_it_matters = 'The stereognostic sense is a sophisticated sensory ability that integrates touch, proprioception, and sensory memory. It underlies practical life skills (getting dressed, eating, writing) and academic learning (reading tactile materials, performing manipulative tasks). Sorting Objects Stereognostically develops this sense through playful exploration. The variety of objects and the need to distinguish between similar items build fine tactile discrimination. This work also demonstrates the Montessori principle that learning is most effective when joyful and intrinsically motivating—children love mystery bag activities, making them ideal for sensory development.'
WHERE LOWER(name) = LOWER('Sorting Objects Stereognostically');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Ten red and blue graduated rods representing quantities 1-10
• First concrete introduction to quantity and the decimal system
• Color alternation isolates the unit concept within larger quantities
• Prepares for addition, subtraction, and number operations
• Combined with number cards to associate numeral with quantity',
  presentation_steps = '[{"step": 1, "title": "Preparation", "description": "Invite child to the Number Rods lesson. Carry rods one at a time to mat, placing randomly.", "tip": "Rods are heavy - model careful two-handed carrying."}, {"step": 2, "title": "Building the Stair", "description": "Arrange rods from longest (10) to shortest (1) like a staircase, aligned at left edge.", "tip": "Left alignment emphasizes the difference in length."}, {"step": 3, "title": "Counting Introduction", "description": "Touch each alternating section of the 10-rod saying ''One, two, three...'' up to ten.", "tip": "Touch precisely at each color change."}, {"step": 4, "title": "Isolation of Quantities", "description": "Take the 1-rod, count ''One.'' Take the 2-rod, count ''One, two.''", "tip": "Start with rods 1-3, then expand."}, {"step": 5, "title": "Three-Period Lesson", "description": "Period 1: ''This is one.'' Period 2: ''Show me two.'' Period 3: ''What is this?''", "tip": "Introduce only 2-3 rods at a time."}, {"step": 6, "title": "Child Exploration", "description": "Invite child to count rods independently and build the stair.", "tip": "Allow repetition for mastery."}]',
  control_of_error = 'Rods don''t align correctly',
  direct_aims = '["Understanding quantity 1-10", "Number names", "Visual representation of quantity"]',
  materials = '["Number Rods (10 rods from 10cm to 1m)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Number Rods');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Tactile numerals 0-9 cut from sandpaper mounted on wooden boards
• Multi-sensory approach: visual, tactile, and kinesthetic learning combined
• Teaches correct numeral formation through muscle memory
• Prepares hand for writing while learning number symbols
• Three-period lesson format for vocabulary introduction',
  presentation_steps = '[{"step": 1, "title": "Preparation", "description": "Select 2-3 contrasting numerals (e.g., 1, 2, 3). Place on mat.", "tip": "Start with numerals that look different."}, {"step": 2, "title": "Tracing Demonstration", "description": "Trace numeral with two fingers in correct formation while saying the name.", "tip": "Use writing fingers (index and middle)."}, {"step": 3, "title": "Child Traces", "description": "Invite child to trace while you say the name together.", "tip": "Guide hand gently if needed."}, {"step": 4, "title": "Period 1: Naming", "description": "Trace and say ''This is one'' for each numeral.", "tip": "Repeat tracing multiple times."}, {"step": 5, "title": "Period 2: Recognition", "description": "Mix numerals. Say ''Show me the two.'' Child finds and traces.", "tip": "Always have child trace when identifying."}, {"step": 6, "title": "Period 3: Recall", "description": "Point to numeral. Ask ''What is this?'' Child traces and names.", "tip": "Return to Period 2 if struggling."}]',
  control_of_error = 'Texture provides tactile feedback',
  direct_aims = '["Numeral recognition", "Numeral formation", "Association of symbol to quantity"]',
  materials = '["Sandpaper Numerals 0-9"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Sandpaper Numerals');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• First material associating quantities with written numbers
• Red and blue striped rods from 1-10 in increasing length
• Child counts and matches numerals to quantities
• Builds foundation for decimal system understanding
• Prepares for advanced arithmetic work',
  presentation_steps = '[{"step": 1, "title": "Introduction to the Material", "description": "Invite the child to work with the Number Rods. Bring the rods and numeral cards to a long mat or table. Arrange the rods in mixed order.", "tip": "Use a calm, gentle tone and allow the child time to observe the materials before beginning."}, {"step": 2, "title": "Presenting Quantity", "description": "Take the rod with one segment (red-blue unit) and say ''This is one.'' Count aloud as you touch each colored segment. Place the numeral card ''1'' next to it. Continue with rod 2, emphasizing the count.", "tip": "Make counting clear and deliberate, touching each segment as you count."}, {"step": 3, "title": "Three-Period Lesson", "description": "Present three consecutive rods (e.g., 3, 4, 5) using the three-period lesson format: First show and name, then ask ''Show me 4,'' then ask ''What is this?''", "tip": "Only work with three rods at a time to avoid overwhelming the child."}, {"step": 4, "title": "Ordering Activity", "description": "Ask the child to build the stair with the rods in correct order from 1-10, with the shortest rod on top. This creates a visual representation of quantity progression.", "tip": "The stair should look like an ascending staircase with clear height progression."}, {"step": 5, "title": "Numeral Matching", "description": "Have the child place corresponding numeral cards next to each rod. Verify that the child understands the relationship between quantity and symbol.", "tip": "Allow independent work; observe for understanding without correcting immediately."}]',
  control_of_error = 'The rods themselves provide control—if arranged correctly, they form a perfect stair. Numeral cards can be checked against known quantities.',
  direct_aims = '["Associate quantity with numeral representation", "Understand number sequence 1-10", "Develop counting accuracy", "Prepare for decimal system understanding"]',
  materials = '["Number Rods (wooden rods painted red and blue, 1-10)", "Numeral Cards (1-10)", "Long work mat or table"]',
  parent_description = 'Number Rods with Numerals introduce your child to the connection between quantity and written numbers. It''s the first Montessori material that helps children see that the written numeral ''5'' means a specific, countable quantity. The rods'' increasing length creates a visual representation of numerical progression, making abstract numbers concrete and meaningful.',
  why_it_matters = 'This work is foundational for all future math learning. It bridges sensorial understanding of quantity with abstract numeral representation, establishing the cognitive framework needed for the decimal system, place value, and all arithmetic operations.'
WHERE LOWER(name) = LOWER('Number Rods with Numerals');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Two partitioned boxes with compartments labeled 0-9
• Child places correct number of spindles (dowels) in each compartment
• Introduces concept of zero and quantity-number matching
• Bundling spindles develops muscular impression
• Built-in control of error through total spindle count',
  presentation_steps = '[{"step": 1, "title": "Material Familiarization", "description": "Invite the child to sit with the Spindle Boxes. Show both boxes and point out the numbered compartments (0-9). Explain that today you''ll fill boxes with small wooden sticks called spindles.", "tip": "Speak clearly about the purpose: matching the number to the correct quantity of spindles."}, {"step": 2, "title": "Introducing Zero", "description": "Start with the ''0'' compartment. Say ''This is zero. Zero means no spindles.'' Show the empty compartment and emphasize that nothing goes in this space.", "tip": "This is crucial for understanding zero as ''nothing'' rather than just an empty space."}, {"step": 3, "title": "Working with One to Nine", "description": "For each number 1-9, count out the correct number of spindles aloud. Place them in the corresponding compartment. Say ''Three means three spindles,'' as you place exactly three spindles in compartment 3.", "tip": "Count deliberately and slowly, touching each spindle as you count."}, {"step": 4, "title": "Bundling Spindles", "description": "Once the child places spindles in each compartment, introduce rubber bands. Bind pairs of spindles together in compartment 2, threesomes in compartment 3, etc. This gives kinesthetic impression of grouping.", "tip": "This bundling activity develops muscle memory and fine motor control."}, {"step": 5, "title": "Independent Work and Verification", "description": "Invite the child to repeat the activity independently. After completion, count the total spindles used. Explain that if they have spindles left over, they made an error somewhere.", "tip": "The total number of spindles (45 total) provides the control of error."}]',
  control_of_error = 'The total number of spindles in the set equals 45 (sum of 1-9). If the child finishes with remaining spindles, they know an error occurred. Child can recount to find and correct mistakes.',
  direct_aims = '["Associate numerals 0-9 with corresponding quantities", "Understand the concept of zero", "Develop accurate counting skills", "Connect quantity to number symbol"]',
  materials = '["Spindle Boxes (two boxes, each with five compartments labeled 0-4 and 5-9)", "Wooden spindles (45 total: 0 for compartment 0, 1 for compartment 1, etc.)", "Rubber bands for bundling"]',
  parent_description = 'Spindle Boxes help your child develop a muscular impression of numbers 0-9. By physically placing spindles into compartments and bundling them, your child''s hands and mind work together to understand quantity. The built-in error correction teaches independence—if spindles remain at the end, your child can find and fix the mistake themselves.',
  why_it_matters = 'This work solidifies number concept understanding and introduces the crucial concept of zero. It transitions from abstract numeral recognition to active engagement with quantity, preparing children for the decimal system and arithmetic operations where zero plays an essential role.'
WHERE LOWER(name) = LOWER('Spindle Boxes');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Number cards 1-10 and 55 identical counters (usually red circles)
• Introduces odd and even numbers through visual pattern
• Final consolidation of the 1-10 sequence before decimal system
• Counters arranged in pairs reveal which numbers have ''lonely'' counters
• Prepares for division concepts through equal grouping',
  presentation_steps = '[{"step": 1, "title": "Card Arrangement", "description": "Child places number cards 1-10 in order from left to right.", "tip": "Verify sequence before continuing."}, {"step": 2, "title": "Placing Counters", "description": "Under ''1'', place one counter. Under ''2'', place two counters in a column.", "tip": "Counters placed in pairs vertically."}, {"step": 3, "title": "Pattern Emergence", "description": "Continue placing counters in paired columns under each number.", "tip": "Some numbers have ''extra'' counters."}, {"step": 4, "title": "Odd and Even Discovery", "description": "Point out numbers where counters pair evenly vs. those with a ''lonely'' counter.", "tip": "Let child discover the pattern."}, {"step": 5, "title": "Vocabulary Introduction", "description": "Introduce ''odd'' (lonely counter) and ''even'' (all paired) terminology.", "tip": "Finger can walk between paired counters but not odd."}, {"step": 6, "title": "Verification", "description": "Child identifies odd and even numbers independently.", "tip": "Test with random number selection."}]',
  control_of_error = 'Wrong number of counters',
  direct_aims = '["Quantity-symbol association", "Odd and even concept", "Counting and arranging"]',
  materials = '["Numeral cards 1-10", "55 counters (red discs)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Cards and Counters');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Game where child fetches quantities from memory to match cards
• Cards with numerals 1-10 on table; objects across room
• Child draws card, remembers quantity, fetches that many objects
• Develops number memory and one-to-one correspondence
• Fun game format reinforces quantity understanding',
  presentation_steps = '[{"step": 1, "title": "Setup", "description": "Place numeral cards face down on table. Objects across room.", "tip": "Distance adds challenge."}, {"step": 2, "title": "Draw Card", "description": "Child draws card, reads numeral silently.", "tip": "No saying aloud yet."}, {"step": 3, "title": "Remember", "description": "Child places card face down, walks to objects.", "tip": "Memory challenge begins."}, {"step": 4, "title": "Fetch", "description": "Child counts out remembered quantity of objects.", "tip": "Must remember number."}, {"step": 5, "title": "Return and Check", "description": "Bring objects back, turn over card, verify match.", "tip": "Self-correction moment."}, {"step": 6, "title": "Continue Game", "description": "Continue with other cards until all matched.", "tip": "Game format is engaging."}]',
  control_of_error = 'Incorrect quantity fetched',
  direct_aims = '["Number memory", "Counting accuracy", "Following directions"]',
  materials = '["Numeral cards", "Small objects to fetch"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Memory Game of Numbers');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Matching cards with quantity dots to numeral cards
• Self-correcting puzzle pieces fit only with correct pairings
• Reinforces one-to-one correspondence
• Engaging extension activity for number concepts
• Develops visual discrimination and pattern recognition',
  presentation_steps = '[{"step": 1, "title": "Material Introduction", "description": "Invite the child to work with the Number Puzzles. Show one puzzle with 10 matching cards. Point out that each card has two parts: one side shows dots (quantity), the other shows a numeral.", "tip": "Start with just one number (e.g., 1-5) rather than the full 1-10 set."}, {"step": 2, "title": "Demonstration", "description": "Show a card with three dots and a card with the numeral ''3'' separately. Say ''This card shows three dots. This card shows the number three.'' Count the dots aloud to verify.", "tip": "Emphasize the connection between the visual dots and the abstract numeral."}, {"step": 3, "title": "Puzzle Matching", "description": "Take the puzzle pieces from the card with three dots. Show how the pieces fit together only with the corresponding numeral card. Try to fit incorrect cards together to demonstrate the control of error\u2014they don''t fit.", "tip": "Let the child discover the self-correcting nature through exploration."}, {"step": 4, "title": "Guided Practice", "description": "Have the child count the dots on one puzzle card. Help them find the matching numeral card and fit the pieces together. Repeat with 2-3 different numbers.", "tip": "Allow the child to count aloud; don''t provide the answer."}, {"step": 5, "title": "Independent Work", "description": "Invite the child to complete all puzzle matches independently. Circulate to observe without interfering unless the child asks for help.", "tip": "Independent error correction is valuable; resist the urge to point out mistakes immediately."}]',
  control_of_error = 'The puzzle pieces have uniquely shaped edges that fit together only when the correct quantity and numeral are paired. An incorrect pairing won''t fit, providing immediate feedback.',
  direct_aims = '["Associate quantity (dots) with numeral symbols", "Practice one-to-one correspondence", "Develop visual discrimination skills", "Reinforce number recognition 1-10"]',
  materials = '["Number Puzzle cards (typically 1-10, with dot side and numeral side)", "Wooden box or storage container", "Cards with uniquely shaped edges for error control"]',
  parent_description = 'Number Puzzles make learning to match quantities with numerals fun and engaging. The self-correcting nature means your child can work independently, discovering when pieces don''t fit and trying again. This builds problem-solving skills and confidence without needing an adult to say ''that''s wrong.''',
  why_it_matters = 'This work reinforces foundational number concepts in an engaging format. It''s particularly valuable as an extension activity after introducing Number Rods and Spindle Boxes, allowing children to apply their learning in a playful, puzzle-based context that maintains their motivation and interest.'
WHERE LOWER(name) = LOWER('Number Puzzles and Games');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Nine colored bead bars representing quantities 1-9
• Each bar uniquely colored for visual differentiation
• Arranged in stair formation for visual quantity progression
• Combines with numeral cards for quantity-symbol association
• Sensorial foundation for understanding number relationships',
  presentation_steps = '[{"step": 1, "title": "Introduction to the Material", "description": "Invite the child to work with the Short Bead Stair. Show all nine bead bars and numeral cards. Say ''Today we''re going to work with bead bars. Each bar has a different number of beads.''", "tip": "Allow the child to handle and explore the bars before beginning formal presentation."}, {"step": 2, "title": "Building the Stair", "description": "Take the bead bar with one bead (unit) and place it in the upper left corner of the mat. Place the 2-bar below it, then the 3-bar, continuing until all nine bars form a stair. The shortest bar (unit) should be at the top.", "tip": "Build slowly and deliberately, talking through each placement."}, {"step": 3, "title": "Naming the Bars", "description": "Point to each bar starting from the unit. Say ''This is one. This is two. This is three,'' and so on. Touch the beads as you count to create the sensorial connection.", "tip": "Use a clear, calm voice and maintain steady eye contact with the child."}, {"step": 4, "title": "Matching Numerals", "description": "Introduce numeral cards one at a time. Place the card ''1'' to the right of the unit bar. Continue with cards 2-9. Have the child help place each card.", "tip": "This connects the abstract numeral with the concrete, visual representation."}, {"step": 5, "title": "Three-Period Lesson", "description": "Present three bars using the three-period lesson: Name (show and say ''This is three''), Recognition (ask ''Show me five''), and Recall (ask ''What is this?'' while pointing to a bar).", "tip": "Work with only three bars at a time to avoid overwhelming the child."}, {"step": 6, "title": "Independent Stair Building", "description": "Invite the child to disassemble the stair, mix the bars, and rebuild the stair independently. This consolidates understanding of quantity progression.", "tip": "Self-correcting: if the stair is built correctly, bars will form a perfect descending stair."}]',
  control_of_error = 'The stair''s geometric shape provides visual control. If built correctly, the stair forms a perfect descending line. Missing or misplaced bars disrupt the pattern immediately.',
  direct_aims = '["Understand number sequence 1-9", "Associate quantity with numeral representation", "Develop sensorial impression of quantity relationships", "Build foundation for arithmetic operations"]',
  materials = '["Short Bead Stair (nine colored bead bars, 1-9 beads each)", "Numeral Cards (1-9)", "Long work mat"]',
  parent_description = 'The Short Bead Stair is your child''s first Montessori math material. Through colorful beads and increasing lengths, your child develops a sensorial understanding of numbers 1-9. The visual progression of the stair creates a tangible representation of how numbers grow, providing a foundation for all future mathematical learning.',
  why_it_matters = 'This material is essential because it bridges the sensorial learning your child has been doing with the Pink Tower and Red Rods with abstract number understanding. The bead stair makes quantity visible and tangible, allowing children to internalize number relationships that will later support all arithmetic operations and the decimal system.'
WHERE LOWER(name) = LOWER('Short Bead Stair');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Concrete representation of the decimal system: unit beads, ten bars, hundred squares, thousand cubes
• Golden color isolates the concept of quantity from numeral
• Each category is exactly 10x the previous, building place value understanding
• Sensorial experience of quantity through weight and size differences
• Foundation for all four mathematical operations',
  presentation_steps = '[{"step": 1, "title": "Introduction to Unit", "description": "Present single golden bead. ''This is one unit.'' Let child feel the weight.", "tip": "Emphasize ''unit'' not just ''one.''"}, {"step": 2, "title": "Introduction to Ten", "description": "Present ten bar. ''This is one ten.'' Count beads on the bar.", "tip": "Show that 10 units = 1 ten."}, {"step": 3, "title": "Introduction to Hundred", "description": "Present hundred square. ''This is one hundred.'' Count ten bars.", "tip": "Show that 10 tens = 1 hundred."}, {"step": 4, "title": "Introduction to Thousand", "description": "Present thousand cube. ''This is one thousand.'' Feel the weight.", "tip": "Dramatic size and weight difference."}, {"step": 5, "title": "Three-Period Lesson", "description": "Use three-period lesson for unit, ten, hundred, thousand.", "tip": "Child should identify by name and find by request."}, {"step": 6, "title": "Quantity Games", "description": "Play fetching games: ''Bring me 3 hundreds and 2 units.''", "tip": "Builds familiarity with categories."}]',
  control_of_error = 'Incorrect naming',
  direct_aims = '["Understanding place value hierarchy", "Vocabulary: unit, ten, hundred, thousand"]',
  materials = '["Golden Bead Material (units, ten-bars, hundred-squares, thousand-cubes)"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Introduction to Golden Beads');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduction to decimal system using concrete golden beads
• Unit beads, ten bars, hundred squares, thousand cube represent place value
• Child manipulates materials to understand quantity relationships
• Foundation for all decimal system work
• Concrete before abstract progression',
  presentation_steps = '[{"step": 1, "title": "Material Arrangement and Introduction", "description": "Set up the Presentation Tray with golden bead materials arranged from largest (thousand cube) on left to smallest (unit) on right. Invite the child and say ''Today we''re going to explore special beads that help us understand large numbers.''", "tip": "Handle materials respectfully and carefully to show the child how to treat the valuable resources."}, {"step": 2, "title": "Introducing the Unit Bead", "description": "Hold up a single unit bead. Say ''This is one bead. We also call it a unit.'' Pass it to the child to hold and feel. Ask ''Can you hold the unit?'' to ensure they understand the terminology.", "tip": "Allow sensorial exploration; let the child''s hands experience the material."}, {"step": 3, "title": "Introducing the Ten Bar", "description": "Show a ten bar (ten unit beads linked together). Say ''This is ten.'' Count the beads together, touching each one: ''One, two, three... ten.'' Place the ten bar next to the unit and say ''Look, ten is much bigger than one.''", "tip": "Create clear visual and tactile contrasts between quantities."}, {"step": 4, "title": "Comparing Unit and Ten", "description": "Take the ten bar and the unit bead. Ask ''How many units fit in this ten?'' Count aloud while moving the unit along the length of the ten bar, demonstrating that 10 units equal 1 ten.", "tip": "This is crucial for understanding place value relationships."}, {"step": 5, "title": "Introducing the Hundred Square", "description": "Show the hundred square (ten tens linked together in a grid). Say ''This is one hundred.'' Count ten horizontal bars aloud. Explain ''We have ten tens, which makes one hundred.''", "tip": "The square''s visual structure makes the concept clear."}, {"step": 6, "title": "Introducing the Thousand Cube", "description": "Show the thousand cube (ten hundred squares stacked). Say ''This is one thousand.'' Count the hundred squares stacked together aloud. Explain ''Ten hundreds make one thousand.''", "tip": "The cube''s impressive size helps children appreciate the magnitude of 1,000."}, {"step": 7, "title": "Place Value Relationship Summary", "description": "Arrange all four quantities in order from left to right: thousand cube, hundred square, ten bar, unit bead. Review: ''One unit, ten units make one ten, ten tens make one hundred, ten hundreds make one thousand.''", "tip": "Repeat this relationship multiple times as it''s foundational to all decimal system work."}]',
  control_of_error = 'The materials themselves provide control. The relationships are fixed: 10 units always equal 1 ten, 10 tens always equal 1 hundred, 10 hundreds always equal 1 thousand.',
  direct_aims = '["Understand the decimal system structure (base 10)", "Recognize place value hierarchy", "Learn decimal nomenclature (unit, ten, hundred, thousand)", "Grasp the relationship between place values"]',
  materials = '["Golden Bead Presentation Tray", "Unit beads (individual beads)", "Ten bars (10 beads linked together)", "Hundred squares (10 ten-bars linked together in grid)", "Thousand cube (10 hundred squares stacked)", "Tray for transport"]',
  parent_description = 'The Golden Bead Tray Exercises introduce your child to the decimal system using materials they can hold and count. By physically handling beads representing units, tens, hundreds, and thousands, your child develops a concrete understanding of how our number system is organized. This hands-on approach makes abstract place value concepts tangible and meaningful.',
  why_it_matters = 'This is the cornerstone of Montessori mathematics. The golden beads make the base-10 system visible and manipulable, allowing children to truly understand (not just memorize) how our decimal number system works. All subsequent arithmetic operations and advanced math concepts build on this foundation.'
WHERE LOWER(name) = LOWER('Golden Bead Tray Exercises');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduces written symbols for decimal place values
• Cards show numerals for units, tens, hundreds, thousands
• Child reads large numbers through symbol association
• Three-period lesson format with material already familiar from beads
• Bridges concrete quantity to abstract numeral representation',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Setup", "description": "Ensure child has already worked with Golden Bead Tray Exercises and understands unit, ten, hundred, and thousand quantities. Bring all numeral cards to the mat. Say ''Now we''re going to learn how to write these numbers.''", "tip": "This work follows golden bead introduction, moving from concrete to abstract."}, {"step": 2, "title": "Three-Period Lesson - Introduction (Units)", "description": "Hold up the unit numeral card (1). Say ''This is one. You already know about one bead. This card shows you how to write one.'' Place the card on the mat and pass it to the child.", "tip": "Start with units before progressing to tens, hundreds, thousands."}, {"step": 3, "title": "Three-Period Lesson - Introduction (Tens)", "description": "Hold up the ten card (10). Say ''This is ten. You know about the ten bar. This is how we write ten.'' Trace the numerals with your finger. Emphasize that it takes two symbols (1 and 0) to write ten.", "tip": "The zero is crucial\u2014it''s a placeholder, not ''nothing'' in this context."}, {"step": 4, "title": "Three-Period Lesson - Introduction (Hundreds and Thousands)", "description": "Present hundred card (100) and thousand card (1000) in the same way. Emphasize the increasing number of zeros and explain ''More zeros means a bigger number.''", "tip": "Use the golden bead quantities as references: ''This is how we write the hundred square.''"}, {"step": 5, "title": "Three-Period Lesson - Recognition", "description": "Mix the cards. Ask the child ''Show me ten,'' ''Show me hundred,'' ''Show me one thousand.'' Watch carefully to ensure correct identification.", "tip": "Repeat until the child confidently identifies each card."}, {"step": 6, "title": "Three-Period Lesson - Recall", "description": "Point to individual cards and ask ''What is this?'' Encourage the child to answer aloud. Praise correct responses warmly.", "tip": "If the child hesitates, don''t provide the answer; wait patiently."}, {"step": 7, "title": "Connection to Golden Beads", "description": "Place the golden beads next to their corresponding numeral cards. Reinforce ''The numeral card shows how to write this number. The beads show how many there really are.''", "tip": "This concrete-abstract connection is essential for deep understanding."}]',
  control_of_error = 'The child''s prior knowledge of golden bead quantities serves as control. Confusion indicates need for more bead work before progressing.',
  direct_aims = '["Recognize numeral symbols for 1, 10, 100, 1000", "Understand place value nomenclature in written form", "Associate quantity with written number", "Prepare for reading and writing larger numbers"]',
  materials = '["Large Numeral Cards (1, 10, 100, 1000)", "Golden Beads (for comparison)", "Long work mat"]',
  parent_description = 'Large Numeral Cards help your child connect the golden bead quantities they already understand with written numbers. After handling beads representing one, ten, one hundred, and one thousand, your child learns how to write these numbers using symbols. This bridges the gap between concrete materials and abstract math.',
  why_it_matters = 'This work solidifies place value understanding by connecting concrete quantity (golden beads) with abstract representation (written numerals). This bridge is essential for understanding expanded notation, reading large numbers, and performing operations across place values.'
WHERE LOWER(name) = LOWER('Large Numeral Cards');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Child selects golden beads to build specific quantities from teacher direction
• ''Give me 324'' - child gathers 3 hundreds, 2 tens, 4 units
• Deepens understanding of place value composition
• Requires child to understand relationships between place values
• Bridges naming numbers to manipulating quantities',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Material Setup", "description": "Child should have worked with Golden Bead Tray Exercises and understand place values. Set up golden beads in organized sections on a large mat. Say ''Today I''m going to ask you to collect beads for me.''", "tip": "Ensure adequate space for the child to move and gather beads comfortably."}, {"step": 2, "title": "Simple Request (One Place Value)", "description": "Ask for a small quantity in a single place value: ''Please bring me three.'' Child selects three unit beads. Say ''Yes! Three units.'' Place them together visibly.", "tip": "Use clear, direct language and wait patiently for the child to complete the task."}, {"step": 3, "title": "Two-Place Request", "description": "Ask for a quantity using two place values: ''Please bring me twenty-three.'' Child should gather 2 tens and 3 units. Praise and confirm: ''Yes, two tens and three units makes twenty-three.''", "tip": "If the child brings incorrect quantity, ask ''Let''s count together'' rather than correcting."}, {"step": 4, "title": "Three-Place Request", "description": "Ask for a three-digit quantity: ''Please bring me one hundred and forty-five.'' Child gathers 1 hundred square, 4 tens, 5 units. Verify together: ''One hundred, forty, five.''", "tip": "The child''s understanding of decimal composition is becoming more sophisticated."}, {"step": 5, "title": "Four-Place Request", "description": "Ask for four-digit quantities: ''Please bring me two thousand, three hundred, sixty-seven.'' Child gathers appropriate beads from each place value category.", "tip": "This work requires significant cognitive organization and concentration."}, {"step": 6, "title": "Reverse: Child Gives Quantity and Teacher Reads", "description": "Reverse roles. Ask the child to gather beads for a quantity, then you count and name what they selected: ''I see one thousand, two hundreds, thirty-four!''", "tip": "This variation deepens place value understanding from a different perspective."}, {"step": 7, "title": "Observation and Recording", "description": "Observe how the child selects and organizes beads. Note their understanding of relationships and ability to translate spoken numbers into physical quantities.", "tip": "This activity reveals deep understanding or misconceptions needing clarification."}]',
  control_of_error = 'The correctness of the gathered quantity serves as control. Teacher can recount and verify accuracy, or child can check their work by counting.',
  direct_aims = '["Decompose numbers into place value components", "Understand that numbers are composed of specific quantities in each place", "Strengthen place value vocabulary and understanding", "Connect spoken numbers with physical quantities"]',
  materials = '["Golden Beads (units, tens, hundreds, thousands)", "Large work mat", "Organized layout with space for gathering"]',
  parent_description = 'Formation of Quantity asks your child to listen to a number and gather the correct beads to represent it. This activity deepens understanding of place value by requiring your child to decompose numbers into their component parts—ones, tens, hundreds, and thousands. It''s the bridge between understanding place value theory and using it in real math.',
  why_it_matters = 'This work transitions children from passive understanding (observing and naming quantities) to active mathematical thinking (composing and decomposing numbers). This active engagement is crucial for developing flexible number sense and preparing for arithmetic operations where place value decomposition is essential.'
WHERE LOWER(name) = LOWER('Formation of Quantity');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Child writes numerals to represent gathered golden bead quantities
• ''Here are beads for 324'' - child writes ''324'' using numeral cards or pencil
• Transitions from physical quantity to written representation
• Strengthens understanding of place value notation
• Critical bridge from concrete to abstract mathematics',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Setup", "description": "Child should have completed Formation of Quantity work and understand large numeral cards. Gather golden beads in a specific quantity (e.g., 2 hundreds, 3 tens, 4 units). Say ''You''ve gathered these beads. Now let''s write the number.''", "tip": "Start with quantities under 1000 before progressing to four-place numbers."}, {"step": 2, "title": "Counting the Gathered Quantity", "description": "Point to each bead group. Count together: ''Two hundreds, three tens, four units.'' Say ''We have two hundred thirty-four.''", "tip": "Emphasize the connection between grouped quantities and the composite number name."}, {"step": 3, "title": "Using Numeral Cards", "description": "Show numeral cards for each place value (2, 3, 4). Place the card 2 (hundred) first, then card 3 (ten), then card 4 (unit) side by side. Say ''When we write them together, it makes 234.''", "tip": "If using cards with place value labels, the positioning reinforces place value understanding."}, {"step": 4, "title": "Writing the Number", "description": "If child is ready, model writing the number on paper or a whiteboard. Write ''two hundred thirty-four'' in words, then write the numeral ''234'' below it. Say ''When we write it this way, with just numbers, it says two hundred thirty-four.''", "tip": "Connect both written forms to reinforce that numerals and words represent the same quantity."}, {"step": 5, "title": "Reverse Practice", "description": "Show the child a number (written or with numeral cards), and have them gather the corresponding golden beads. This strengthens the bidirectional understanding.", "tip": "Alternating between formation of quantity and formation of symbol deepens understanding."}, {"step": 6, "title": "Independent Work", "description": "Prepare cards with numbers (2-4 digits). Child reads each number and writes it using numeral cards or records it in a notebook. You observe and provide feedback.", "tip": "Independence builds confidence in translating quantities to symbols."}, {"step": 7, "title": "Error Correction", "description": "If child writes incorrect numeral for gathered beads, count together again and guide re-writing. Say ''Let''s count the hundreds again. We have... so the first digit is...''", "tip": "Guide the child back to the quantities rather than correcting the written form directly."}]',
  control_of_error = 'The gathered golden beads serve as control. If the child writes the wrong numeral, they can recount the beads to verify accuracy.',
  direct_aims = '["Associate place value components with positional notation", "Write numerals representing place value quantities", "Understand the correspondence between physical and symbolic representation", "Develop place value awareness through writing"]',
  materials = '["Golden Beads (units, tens, hundreds, thousands)", "Large Numeral Cards (1-9 for each place value)", "Pencil and paper or whiteboard", "Work mat"]',
  parent_description = 'Formation of Symbol teaches your child to write numbers that represent specific quantities of beads. After gathering beads for two hundred thirty-four, your child learns to write ''234''. This connects the abstract written number to the concrete quantity they''ve physically organized, making mathematical notation meaningful.',
  why_it_matters = 'This work is crucial for developing true place value understanding. Many children learn to ''read'' numerals without understanding they represent specific quantities in specific positions. By connecting writing numerals to physical bead quantities, children develop deep comprehension of how our positional notation system works—essential for all future mathematics.'
WHERE LOWER(name) = LOWER('Formation of Symbol');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Combines golden beads (quantity) with numeral cards and large numeral cards (symbol)
• Child matches gathered beads to their written representations
• Strengthens place value understanding through multiple representations
• Bridges formation of quantity and formation of symbol
• Consolidates decimal system knowledge',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Setup", "description": "Child should have completed both Formation of Quantity and Formation of Symbol. Prepare the workspace with golden beads organized and large numeral cards visible. Say ''Today we''ll use beads and numbers together to see how they match.''", "tip": "This is a consolidation activity, so child should be confident in previous work."}, {"step": 2, "title": "Presentation of Materials", "description": "Show how golden beads, large numeral cards, and individual numeral cards all represent the same quantity. Arrange 3 hundreds, 2 tens, 4 units on the mat. Place large numeral cards for 300, 20, 4 next to each group. Say ''These beads and these numbers show the same amount.''", "tip": "Organize materials with clear spatial relationships showing place value."}, {"step": 3, "title": "Overlapping Numeral Cards", "description": "Take individual numeral cards (3, 2, 4) and overlap them on the mat (thousands place, tens place, units place). Say ''When we write them like this, the three is worth three hundred, the two is worth twenty, the four is worth four.'' This shows how position changes value.", "tip": "The overlapping technique emphasizes that each digit''s value depends on its position."}, {"step": 4, "title": "Reading the Complete Number", "description": "Point to the overlapped numerals and say ''When we put them together like this, it says three hundred twenty-four'' or 324. Have the child repeat the full number.", "tip": "Emphasize the connection to the gathered beads representing the same quantity."}, {"step": 5, "title": "Matching Beads to Symbols", "description": "Show a number in overlapped cards (e.g., 456). Have the child gather the corresponding golden beads (4 hundreds, 5 tens, 6 units). Verify: ''Yes, four hundred fifty-six.''", "tip": "This reverse work strengthens bidirectional understanding."}, {"step": 6, "title": "Multiple Representations", "description": "Show how the same quantity can be represented in different ways: gathered beads, individual numeral cards, overlapped numeral cards, and written numeral. Emphasize they''re all the same number.", "tip": "Multiple representations deepen abstract number understanding."}, {"step": 7, "title": "Independent Matching Activity", "description": "Provide prepared sets: gather beads and overlapped numeral cards representing 4-5 different quantities. Have the child match gathered bead sets to their numeral representations.", "tip": "Self-correction can come from recounting beads or having prepared answer keys."}]',
  control_of_error = 'The golden beads provide concrete control. Recounting beads verifies accuracy of numeral representations.',
  direct_aims = '["Understand equivalence between quantity and symbol representations", "Strengthen place value understanding through multiple representations", "Associate numerical notation with physical quantities", "Develop abstract number sense"]',
  materials = '["Golden Beads (units, tens, hundreds, thousands)", "Large Numeral Cards (1, 10, 100, 1000)", "Individual Numeral Cards (1-9, 10-90 by tens, 100-900 by hundreds, 1000-9000 by thousands)", "Work mat with space for organized arrangement"]',
  parent_description = 'Association of Quantity and Symbol combines all your child''s place value learning. By working with gathered beads alongside numeral cards, your child sees that the same quantity can be shown in different ways—physical beads, individual number cards, and written numerals. This flexibility in representation is foundational for all advanced mathematical thinking.',
  why_it_matters = 'This consolidation work solidifies place value understanding by showing that quantity and symbol are inseparable. Children who truly understand that 324 is the same as 3 hundreds, 2 tens, and 4 units—and can represent this multiple ways—have developed deep, flexible mathematical thinking that supports all future learning. Without this understanding, arithmetic operations become rote memorization rather than logical thinking.'
WHERE LOWER(name) = LOWER('Association of Quantity and Symbol');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Child exchanges 10 smaller units for 1 larger unit using golden beads
• 10 units become 1 ten, 10 tens become 1 hundred, etc.
• Reinforces place value relationships and value conversions
• Prepares understanding for carrying/regrouping in operations
• Integrates quantity and symbol work',
  presentation_steps = '[{"step": 1, "title": "Game Setup and Introduction", "description": "Invite the child to play a ''game'' with the golden beads. Create a ''bank'' area with organized quantities. Say ''In this game, we can trade small quantities for bigger ones, like trading coins at a bank.''", "tip": "Frame this as an engaging game rather than a formal lesson to maintain motivation."}, {"step": 2, "title": "Gathering a Mixed Quantity", "description": "Have the child gather a random assortment of golden beads from the bank (e.g., 8 units, 7 tens, 3 hundreds). Place them on a large mat. Say ''You''ve gathered these beads. Now we''re going to organize them.''", "tip": "Use quantities that will naturally result in opportunities to exchange."}, {"step": 3, "title": "Organizing by Place Value", "description": "Arrange gathered beads in columns: thousands on left, then hundreds, tens, units on right. Say ''Let''s count each group and put them in order from biggest to smallest values.''", "tip": "Organization makes exchanges clear and logical."}, {"step": 4, "title": "First Exchange - Units to Tens", "description": "Count the units. When you reach 10 units, say ''We have ten ones! We can trade these for one ten.'' Exchange 10 units for 1 ten bar from the bank. Place the ten with the other tens.", "tip": "Emphasize that the value doesn''t change; we''re just using a different form."}, {"step": 5, "title": "Subsequent Exchanges", "description": "Continue exchanging: 10 tens become 1 hundred, 10 hundreds become 1 thousand. After each exchange, pause and count to verify the total value hasn''t changed.", "tip": "Children often need to recount after each exchange to believe the value is equivalent."}, {"step": 6, "title": "Reading the Final Quantity", "description": "After all exchanges, read the final organized quantity: ''We started with a mixed pile, and now we have one thousand, three hundreds, five tens, and eight units. Let''s write that number.'' Child helps write 1358.", "tip": "This connection to numeral writing reinforces why exchange/regrouping matters."}, {"step": 7, "title": "Numeral Card Overlap Technique", "description": "Demonstrate overlapping numeral cards (1, 3, 5, 8) to show how the final number is written, connecting gathered beads to symbol representation.", "tip": "Multiple repetitions of this activity cement understanding of place value relationships."}]',
  control_of_error = 'The total value of beads remains constant throughout. Recounting verifies that exchanges maintain equivalent value.',
  direct_aims = '["Understand place value relationship (10 of one unit equals 1 of next)", "Practice exchanging smaller quantities for larger equivalents", "Recognize when exchanges are possible (when 10 or more of a unit exist)", "Prepare for carrying and regrouping in arithmetic"]',
  materials = '["Golden Beads (complete set with units, tens, hundreds, thousands)", "Large work mat", "Golden Bead Bank (organized supply)", "Numeral Cards (optional, for writing final quantity)"]',
  parent_description = 'The Exchange Game teaches your child that our number system is organized around groups of ten. When your child has gathered ten unit beads, they trade them for one ten bar from the ''bank.'' This simple action—exchanging ten ones for one ten—is the key concept behind carrying in addition and borrowing in subtraction. Playing this game repeatedly builds deep understanding of how regrouping works.',
  why_it_matters = 'This work is essential for understanding regrouping in all arithmetic operations. Children who physically exchange beads and see that the value remains the same understand carrying in addition not as a mysterious rule to follow, but as a logical consequence of place value organization. This conceptual understanding prevents arithmetic from becoming rote procedure.'
WHERE LOWER(name) = LOWER('Exchange Game (Change Game)');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• First operation with golden bead material
• Two addends formed separately, then combined
• Introduces exchange: 10 units = 1 ten, etc.
• Concrete experience of addition with regrouping
• Foundation for all addition understanding',
  presentation_steps = '[{"step": 1, "title": "Problem Setup", "description": "Present addition problem. Two children form addends with beads.", "tip": "Group work engaging."}, {"step": 2, "title": "Addend Formation", "description": "Each child creates their number with golden beads and cards.", "tip": "Careful counting."}, {"step": 3, "title": "Combining", "description": "''Now we put them together.'' Combine by category.", "tip": "Units with units, etc."}, {"step": 4, "title": "Counting", "description": "Count each category. If 10 or more, exchange.", "tip": "Exchange is key concept."}, {"step": 5, "title": "Exchange", "description": "10 units \u2192 1 ten, 10 tens \u2192 1 hundred, etc.", "tip": "Carry to next category."}, {"step": 6, "title": "Result", "description": "Form final number with cards. Read result.", "tip": "Record on paper."}]',
  control_of_error = 'Sum doesn''t match calculation',
  direct_aims = '["Concrete understanding of addition", "Process of combining quantities"]',
  materials = '["Golden Bead Material", "Large Numeral Cards", "Problem cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Golden Bead Addition');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Subtraction using golden bead material with exchanging
• Minuend formed; subtrahend taken away
• Exchange needed when category has insufficient quantity
• Concrete experience of subtraction with borrowing
• Foundation for subtraction understanding',
  presentation_steps = '[{"step": 1, "title": "Problem Setup", "description": "Present subtraction problem. Form minuend with beads.", "tip": "Larger number formed completely."}, {"step": 2, "title": "Taking Away", "description": "''We need to take away...'' Start removing from units.", "tip": "One category at a time."}, {"step": 3, "title": "Exchange When Needed", "description": "If not enough units, exchange 1 ten for 10 units.", "tip": "Borrowing concrete."}, {"step": 4, "title": "Continue Removal", "description": "Remove from each category, exchanging as needed.", "tip": "Work through all categories."}, {"step": 5, "title": "Count Remainder", "description": "Count what remains in each category.", "tip": "This is the answer."}, {"step": 6, "title": "Record", "description": "Form answer with cards. Record on paper.", "tip": "Complete the process."}]',
  control_of_error = 'Difference doesn''t match calculation',
  direct_aims = '["Concrete understanding of subtraction", "Process of taking away"]',
  materials = '["Golden Bead Material", "Large Numeral Cards", "Problem cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Golden Bead Subtraction');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Child multiplies using golden beads with place value columns
• ''Three groups of twenty-three'' - child builds 3 separate groups
• Transitions from concrete understanding to abstract algorithms
• Reinforces place value through regrouping/exchanging
• Prepares for memorization work with multiplication board',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Setup", "description": "Child should understand golden beads thoroughly and have completed Exchange Game. Present a multiplication problem: 3 \u00d7 24. Say ''We''re going to make three groups of twenty-four.''", "tip": "Start with single-digit multipliers and two-digit multiplicands before progressing."}, {"step": 2, "title": "Building the First Group", "description": "Gather 2 tens and 4 units. Say ''This is one group of twenty-four.'' Place it on the mat. Have the child help gather materials.", "tip": "Using child''s language: ''Can you help me find the beads for twenty-four?''"}, {"step": 3, "title": "Building Remaining Groups", "description": "Build a second group of 24 (2 tens, 4 units) next to the first. Say ''This is another group of twenty-four. We have two groups now.'' Build a third group. Say ''This is three groups of twenty-four.''", "tip": "Keep groups visually separate to maintain clarity."}, {"step": 4, "title": "Combining and Organizing", "description": "Gather all three groups together. Say ''Now let''s put all the beads together. We have all our units together, all our tens together.'' Organize into place value columns.", "tip": "This organization prepares for the exchange step."}, {"step": 5, "title": "Counting and Exchanging", "description": "Count the units: ''We have twelve units! We can exchange ten of these for one more ten.'' Make the exchange. Now count tens and continue exchanging as needed.", "tip": "Each exchange reinforces place value understanding in a meaningful context."}, {"step": 6, "title": "Reading the Final Result", "description": "After all exchanges, read the result: ''Three groups of twenty-four equals seventy-two.'' Write the number: 3 \u00d7 24 = 72.", "tip": "The visual, tactile experience helps the child understand what the numeral represents."}, {"step": 7, "title": "Multiple Problems", "description": "Repeat with 2 \u00d7 15, 4 \u00d7 12, and other suitable problems. Have child gather increasingly more material as confidence builds.", "tip": "Repetition with varied problems builds fluency and flexibility."}]',
  control_of_error = 'The gathered beads and counted groups provide control. Recounting verifies accuracy.',
  direct_aims = '["Understand multiplication as ''groups of'' rather than abstract operation", "Perform multiplication with concrete materials", "Practice place value regrouping in meaningful context", "Prepare for memorization and abstract multiplication"]',
  materials = '["Golden Beads (units, tens, hundreds, thousands)", "Large work mat with space for multiple groups", "Pencil and paper for recording equations"]',
  parent_description = 'Golden Bead Multiplication shows your child that multiplication means ''making groups of.'' When your child makes three separate groups of twenty-four and then combines them, they physically see that 3 × 24 = 72. The exchanges that happen when combining the groups—trading ten units for a ten bar—show why larger numbers result. This concrete understanding prevents multiplication from being a mysterious procedure and makes the numbers meaningful.',
  why_it_matters = 'This work is foundational for all multiplication understanding. Children who have physically made groups of items and combined them understand multiplication conceptually, not just procedurally. This understanding prepares them for the Multiplication Board (memorization), Bead Board (efficiency), and eventually abstract multiplication algorithms.'
WHERE LOWER(name) = LOWER('Golden Bead Multiplication');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Child divides golden beads into equal groups
• ''Share sixty-eight beads equally among four people'' 
• Uses concrete materials to understand division as distribution
• Inverse operation of multiplication
• Foundation for abstract division algorithms',
  presentation_steps = '[{"step": 1, "title": "Prerequisites and Setup", "description": "Child should understand multiplication with beads and be comfortable with place values. Use a concrete frame: ''Imagine we have toys to share equally among friends. Let''s use beads to show this.'' Start with 48 \u00f7 4.", "tip": "Use division problems that divide evenly initially; no remainders."}, {"step": 2, "title": "Gathering the Dividend", "description": "Gather 48 beads: 4 tens and 8 units. Say ''We have forty-eight beads to share. We''re going to divide them equally among four people.''", "tip": "Use the word ''dividend'' naturally: ''This is our dividend, the amount we''re dividing.''"}, {"step": 3, "title": "Creating Groups for Division", "description": "Create four spaces on the mat, representing four people. Say ''Each person will get an equal share. Let''s give out the beads one at a time to each person.''", "tip": "Dealing out beads (one to each person, repeating) demonstrates fair distribution."}, {"step": 4, "title": "Distributing the Tens", "description": "Start with the tens. Say ''Let''s see if we can give tens to each person.'' Distribute the 4 tens: one to each person. Say ''Each person got one ten.''", "tip": "This demonstrates how tens are divided before dealing with units."}, {"step": 5, "title": "Distributing the Units", "description": "Deal out the units one at a time to each person in turn. Say ''One, two, three, four\u2014everyone gets one. One, two, three, four\u2014everyone gets another one. One, two, three, four\u2014everyone gets one more.''", "tip": "The dealing process shows division''s meaning: fair distribution."}, {"step": 6, "title": "Verification and Counting", "description": "Count what each person received: ''Each person has one ten and two units. That''s twelve.'' Verify all people received equal amounts.", "tip": "Recount to confirm: 12 + 12 + 12 + 12 = 48."}, {"step": 7, "title": "Recording the Problem", "description": "Write the division problem: ''48 \u00f7 4 = 12'' or ''4)48''. Say ''Forty-eight divided into four equal groups is twelve in each group.''", "tip": "Connect the concrete action to the abstract notation."}, {"step": 8, "title": "Multiple Problems", "description": "Repeat with 63 \u00f7 3, 84 \u00f7 6, and other suitable problems, gradually progressing to three-digit dividends.", "tip": "Each problem deepens understanding; celebrate the pattern recognition."}]',
  control_of_error = 'The gathered beads and counted shares provide control. Adding the individual shares should equal the original dividend.',
  direct_aims = '["Understand division as distribution into equal groups", "Perform division with concrete materials", "Connect division to multiplication as inverse operation", "Prepare for abstract division algorithms"]',
  materials = '["Golden Beads (units, tens, hundreds)", "Large work mat with space for multiple division groups", "Cards or objects to represent ''people'' receiving shares", "Pencil and paper for recording equations"]',
  parent_description = 'Golden Bead Division shows your child that division means sharing fairly. When your child divides forty-eight beads equally among four people, getting twelve for each person, they physically understand what 48 ÷ 4 = 12 means. This concrete foundation prevents division from becoming a confusing, abstract procedure and builds real understanding of fair sharing and equal groups.',
  why_it_matters = 'This work is foundational for all division understanding. Children who have physically divided quantities into equal groups understand division conceptually, not just as a procedure to memorize. This understanding supports the Division Board (memorization), long division algorithms, and later algebraic thinking about equal groups and fair distribution.'
WHERE LOWER(name) = LOWER('Golden Bead Division');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Seguin boards for teaching teen numbers 11-19 (Board 1) and counting teens (Board 2)
• Wooden boards with printed ''10'' slots where unit cards 1-9 slide to form teens
• Shows that teen numbers are ''10 and some more'' - addressing irregular naming
• Connected with golden bead ten bar and unit beads for concrete understanding
