  presentation_steps = '[{"step": 1, "title": "Board 1 Setup", "description": "Place Seguin Board 1 on mat. Set out unit cards 1-9 and corresponding bead bars.", "tip": "Use golden bead materials alongside."}, {"step": 2, "title": "Building 11", "description": "Place ten bar, add one unit bead. Slide ''1'' card over zero in ''10'' to make ''11.''", "tip": "Say ''ten and one makes eleven.''"}, {"step": 3, "title": "Building 12-19", "description": "Continue pattern: ten bar + units, slide card to form numeral.", "tip": "Emphasize ''10 and __'' pattern."}, {"step": 4, "title": "Three-Period Lesson", "description": "Use three-period lesson for teen number names.", "tip": "Teen names are irregular - extra practice needed."}, {"step": 5, "title": "Board 2: Counting", "description": "Board 2 has fixed teen numerals. Child counts golden beads to match.", "tip": "Reinforces quantity-symbol connection."}, {"step": 6, "title": "Bead Bar Association", "description": "Child matches colored bead bars to teen numbers on Board 2.", "tip": "Colored bead bars represent 1-9."}]',
  control_of_error = 'Beads don''t match numeral',
  direct_aims = '["Understanding teen numbers", "Vocabulary: eleven through nineteen"]',
  materials = '["Teen Board 1", "Golden ten-bar", "Colored bead bars 1-9"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Teen Board 1 (Seguin Board A)');

