UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Wooden board with 100 squares and numbered tiles 1-100
• Child sequences numbers and discovers patterns
• Develops understanding of number order and place value
• Visual representation of hundred quantity
• Foundation for understanding linear sequence',
  presentation_steps = '[{"step": 1, "title": "Material Introduction", "description": "Show the Hundred Board with all tiles. Say ''This board shows all numbers from 1 to 100.''", "tip": "Initially show completed board so child sees full scope."}, {"step": 2, "title": "Observing Patterns", "description": "Point out patterns: ''Column 1 has 1, 11, 21, 31... Column 10 has 10, 20, 30... Each row adds ten!''", "tip": "Pattern observation builds mathematical thinking."}, {"step": 3, "title": "Removing and Re-sequencing", "description": "Remove all tiles and mix them. Invite child to place back in order 1-100.", "tip": "Active work embeds sequence kinesthetically and visually."}, {"step": 4, "title": "Self-Correction", "description": "Each space has a printed number showing correct sequence. Board itself provides control.", "tip": "Self-correction builds independence and confidence."}, {"step": 5, "title": "Progressive Challenges", "description": "Present partial ranges to re-order (51-75, 41-60, etc.). Deepens number relationship understanding.", "tip": "Chunking into ranges prevents overwhelm."}]',
  control_of_error = 'Printed board shows correct sequence; misplacement is immediately visible.',
  direct_aims = '["Sequence 1-100 accurately", "Understand number order and relationship", "Recognize place value patterns visually"]',
  materials = '["Hundred Board (wooden with 100 squares)", "Numbered tiles 1-100", "Storage container"]',
  parent_description = 'The Hundred Board helps your child understand number order 1-100 and discover patterns. Placing tiles reveals that tens organize in columns and ones repeat in rows—visual patterns that build deep place value understanding.',
  why_it_matters = 'Number sequence and patterns are foundational for mental math and place value operations. The Hundred Board makes abstract relationships visible, allowing children to discover patterns—more powerful than being told.'
WHERE LOWER(name) = LOWER('Hundred Board');

