  control_of_error = 'Incorrect classification',
  direct_aims = '["Understanding nouns name things", "Noun identification"]',
  materials = '["Noun lesson materials", "Black triangle symbol", "Objects and labels"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Introduction to the Noun');

