  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Compound Words');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Word elements that modify meaning (un-, re-, -ful, -less)
• Teach common prefixes and suffixes explicitly
• Show how affixes change word meaning and function
• Practice building new words with affixes
• Connect to reading and writing skills',
  presentation_steps = '[{"step": 1, "title": "Introduce Prefix Concept", "description": "Show word pairs: happy/unhappy, do/redo, do/undo. Discuss: ''What part is the same?'' (happy, do) ''What part is added?'' (un-, re-) ''Does it change the meaning?'' Introduce the idea that affixes change words.", "tip": "Start with very common, high-utility prefixes (un-, re-, pre-) that children will encounter in reading."}, {"step": 2, "title": "Teach Individual Prefixes", "description": "Present one prefix with multiple examples: un-(undo, unhappy, unfair, unzip). Use consistent language: ''un- means not.'' Show how the prefix changes meaning. Have children practice making new words with the prefix.", "tip": "Knowing the meaning of the prefix helps children decode new words. ''Un- means not, so unlucky means...''"}, {"step": 3, "title": "Introduce Suffixes", "description": "Show word pairs showing suffix addition: walk/walker, slow/slowly, beauty/beautiful. Discuss: ''What part is the same?'' ''What''s added?'' ''How does it change meaning or function?'' Introduce suffix concept.", "tip": "Suffixes often change parts of speech: walk (verb) \u2192 walker (noun), slow (adjective) \u2192 slowly (adverb)."}, {"step": 4, "title": "Teach Individual Suffixes", "description": "Present suffixes one at a time with multiple examples: -ful (careful, helpful, beautiful, wonderful). Teach the meaning: ''-ful means full of.'' Show how it changes meaning and part of speech.", "tip": "Some suffixes are straightforward (-er = one who); others are more complex. Start with the clearest."}, {"step": 5, "title": "Practice Word Building", "description": "Provide activities where children build words using affixes: add prefixes to root words, add suffixes, combine multiple affixes. Sort words by which prefix or suffix they contain. Apply to reading and writing.", "tip": "Kinesthetic letter manipulation (movable alphabet) helps children experience how affixes combine with roots."}]',
  control_of_error = 'Accuracy of meaning when affix is applied; child''s ability to build new words correctly; application to reading and writing; understanding of what each affix means',
  direct_aims = '["Learn common prefixes and suffixes", "Understand how affixes modify meaning", "Develop ability to decode unfamiliar words", "Build vocabulary efficiently"]',
  materials = '["Prefix and suffix cards", "Root word cards for combining with affixes", "Word building cards with affixes and roots on separate pieces", "Word lists organized by prefix/suffix", "Charts showing common affixes and their meanings", "Movable alphabet letters for manipulation"]',
  parent_description = 'Prefixes and Suffixes are word parts that modify meaning. Prefixes (un-, re-, pre-) attach to the beginning of words; suffixes (-ful, -less, -ing, -ly) attach to the end. Teaching these word elements explicitly empowers children to decode unfamiliar words. Learning that ''un-'' means not helps children understand that unkind means not kind, unhappy means not happy. Learning that ''-ful'' means full of helps them understand that careful means full of care. By recognizing these parts in new words, children can decode them independently and expand vocabulary significantly.',
  why_it_matters = 'Prefixes and suffixes are powerful tools for word learning and reading fluency. A child who knows common affixes can decode hundreds of words they''ve never seen before. Etymology and morphological awareness (understanding word structure) directly supports reading comprehension and vocabulary growth. Research shows that explicit instruction in affixes significantly improves reading comprehension and spelling. Perhaps most importantly, understanding affixes teaches children that language is systematic—words are built from parts that follow rules, not random collections of letters.'
WHERE LOWER(name) = LOWER('Prefixes and Suffixes');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Synonyms: words with similar meanings (big/large, happy/joyful)
• Antonyms: words with opposite meanings (big/small, happy/sad)
• Teach through comparison and categorization
• Use in reading to expand vocabulary and comprehension
• Support writing through word choice variety',
  presentation_steps = '[{"step": 1, "title": "Introduce Synonym Concept", "description": "Show word pairs with similar meanings: big/large, small/tiny, happy/joyful, run/sprint. Discuss: ''Do these words mean the same thing?'' ''Are they exactly the same, or slightly different?'' Introduce idea that different words can have similar meanings.", "tip": "Even synonyms have nuances. Big and large mean similar things but have different feel/usage. This sophistication is valuable."}, {"step": 2, "title": "Present Synonym Groups", "description": "Show groups of similar words: happy/joyful/glad/delighted. Discuss how they''re similar (all positive emotions) and different (delighted is more intense than happy). Show range of synonyms from simple to sophisticated.", "tip": "This helps children see vocabulary nuance. Not all synonyms are perfectly interchangeable; context matters."}, {"step": 3, "title": "Introduce Antonym Concept", "description": "Show word pairs with opposite meanings: big/small, happy/sad, hot/cold, go/stop. Discuss: ''These words have opposite meanings.'' Introduce term ''antonym.'' Explore multiple pairs.", "tip": "Some antonyms are perfect opposites (on/off); others are just opposite ends of a spectrum (big/small)."}, {"step": 4, "title": "Create Synonym and Antonym Connections", "description": "Use visual organizers: one word in center, synonyms around it, antonyms on opposite side. Or sort cards into synonym groups. Activities help children see relationships visually.", "tip": "Visual organization makes abstract relationships concrete and memorable."}, {"step": 5, "title": "Apply to Reading and Writing", "description": "In reading: ''This character is very sad. What''s the antonym?'' In writing: ''You used ''happy'' twice. What''s a synonym you could use instead?'' Connect concepts to actual language use.", "tip": "Real application motivates learning. Children see why understanding synonyms and antonyms matters for communication."}]',
  control_of_error = 'Accuracy of synonym/antonym identification; ability to explain similarity/difference; application to reading and writing; quality of word choice improvements',
  direct_aims = '["Learn synonyms and antonyms", "Understand word relationships and meanings", "Expand vocabulary through related words", "Improve writing through varied word choices"]',
  materials = '["Synonym and antonym card sets", "Visual organizers (word webs, comparison charts)", "Word sort activities organized by synonym/antonym relationships", "Thesaurus for exploring word relationships", "Books highlighted with synonym and antonym examples", "Writing samples for analysis and improvement"]',
  parent_description = 'Synonyms are words with similar meanings; antonyms are words with opposite meanings. Children learn that ''big'' and ''large'' are synonyms—similar but not identical. They learn that ''big'' and ''small'' are antonyms—opposite meanings. Understanding these relationships helps children understand texts (using context and word relationships to infer meaning), expand vocabulary, and vary word choices in writing. Rather than always using the same word (happy, happy, happy), children learn alternatives (joyful, delighted, glad) that add sophistication and precision. This work is playful and visual, often involving sorting, categorizing, and building connections between words.',
  why_it_matters = 'Understanding word relationships is central to vocabulary growth and reading comprehension. When children understand that words have synonyms and antonyms, they recognize larger patterns in language. In reading, knowing an antonym can help infer meaning: ''The opposite of cloudy is clear, so clearing up means getting clearer.'' In writing, choosing among synonyms helps express subtle meanings and add variety. Most importantly, working with synonyms and antonyms teaches children to think about language analytically—to see words not as isolated items but as parts of an interconnected system. This metalinguistic awareness supports all future language learning.'
WHERE LOWER(name) = LOWER('Synonyms and Antonyms');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Word relationship studies for vocabulary development
• Synonyms: same/similar meaning (big/large)
• Antonyms: opposite meaning (big/small)
• Homonyms: same sound, different meaning (bear/bare)
• Develops precise word choice and vocabulary',
  presentation_steps = '[{"step": 1, "title": "Synonym Introduction", "description": "''Big and large mean the same. They''re synonyms.''", "tip": "Same meaning words."}, {"step": 2, "title": "Synonym Matching", "description": "Match synonym pairs: happy/glad, fast/quick.", "tip": "Card matching."}, {"step": 3, "title": "Antonym Introduction", "description": "''Hot and cold are opposites. They''re antonyms.''", "tip": "Opposite meaning."}, {"step": 4, "title": "Antonym Matching", "description": "Match antonym pairs: up/down, day/night.", "tip": "Opposite pairs."}, {"step": 5, "title": "Homonym Introduction", "description": "''Bear (animal) and bare (no clothes) sound the same.''", "tip": "Same sound, different spelling/meaning."}, {"step": 6, "title": "Practice", "description": "Sort, match, use in context.", "tip": "Multiple activities."}]',
  control_of_error = 'Wrong meaning for context',
  direct_aims = '["Understanding multiple meanings", "Spelling awareness"]',
  materials = '["Homonym cards with pictures"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Homonyms');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• First globe presentation showing Earth as blue (water) and sandpaper (land)
• Tactile experience of Earth''s surface: rough land, smooth water
• Introduces concept that we live on a sphere with more water than land
• Sandpaper texture isolates land concept from color
• Prepares for Continents Globe and detailed geography',
  presentation_steps = '[{"step": 1, "title": "Introduction", "description": "Carry globe carefully to table. ''This is our Earth, where we all live.''", "tip": "Create sense of wonder."}, {"step": 2, "title": "Tactile Exploration", "description": "Guide child''s hand over smooth blue areas. ''This is water - feel how smooth.''", "tip": "Close eyes for sensory focus."}, {"step": 3, "title": "Land Discovery", "description": "Guide hand to sandpaper areas. ''This is land - feel the roughness.''", "tip": "Contrast sensation with water."}, {"step": 4, "title": "Comparison", "description": "''There is more water or more land?'' Child observes and responds.", "tip": "Visual and tactile observation."}, {"step": 5, "title": "Vocabulary", "description": "Use three-period lesson: water, land. Then ''ocean'' and ''continent.''", "tip": "Build vocabulary gradually."}, {"step": 6, "title": "Spinning Exploration", "description": "Gently spin globe. ''Our Earth turns slowly all the time.''", "tip": "Introduce Earth''s rotation."}]',
  control_of_error = 'Texture difference',
  direct_aims = '["Land/water distinction"]',
  materials = '["Sandpaper Globe"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Globe - Land and Water');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Second globe with continents color-coded (same colors as puzzle maps)
• Each continent has distinct color: Africa-green, Europe-red, Asia-yellow, etc.
• Builds on land/water globe with specific continent identification
• Connects to puzzle maps, flags, and cultural studies
• Three-period lesson for continent names',
  presentation_steps = '[{"step": 1, "title": "Connection", "description": "Review land/water globe. ''Now I''ll show you the names of the land areas.''", "tip": "Build on prior knowledge."}, {"step": 2, "title": "Continent Introduction", "description": "Point to each colored area. ''This land is called Africa. It''s colored green.''", "tip": "Use consistent color references."}, {"step": 3, "title": "Three-Period Lesson", "description": "Introduce 2-3 continents at a time using three-period format.", "tip": "Start with child''s home continent."}, {"step": 4, "title": "Spinning Location", "description": "Spin globe. Stop randomly. ''Which continent is under my finger?''", "tip": "Game format reinforces learning."}, {"step": 5, "title": "Personal Connection", "description": "''We live on this continent.'' Mark with small figure if possible.", "tip": "Concrete personal connection."}, {"step": 6, "title": "Extension", "description": "Connect to puzzle maps with same colors.", "tip": "Color coding creates consistency."}]',
  control_of_error = 'The puzzle-fit nature of continent pieces; visual matching of colors to control cards; teacher observation during three-period lesson',
  direct_aims = '["Continent names"]',
  materials = '["Colored Globe"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Globe - Continents');

UPDATE montree_classroom_curriculum_works SET
