  control_of_error = 'Pieces don''t fit',
  direct_aims = '["Continent shapes"]',
  materials = '["World Puzzle Map"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Puzzle Map - World');

