UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Present clock hands and their functions
• Teach hour and minute hand movements
• Practice on-the-hour times first
• Progress to 15-minute intervals
• Extend to 5-minute and 1-minute increments',
  presentation_steps = '[{"step": 1, "title": "Introduce Clock Parts", "description": "Show a teaching clock and identify parts: hour hand (shorter), minute hand (longer), numbers 1-12, center point. Discuss function of each part.", "tip": "Use a large, clear teaching clock with removable hands"}, {"step": 2, "title": "Teach Hour Hand", "description": "Focus on hour hand movement first. Demonstrate how it moves slowly from one number to the next during an hour. Practice setting times on the hour (1:00, 2:00, etc.).", "tip": "Connect to daily activities: lunch is at 12:00, dismissal is at 3:00"}, {"step": 3, "title": "Introduce Minute Hand", "description": "Show how minute hand moves faster, completing full circle in 60 minutes. Teach that 12 = :00 minutes, 3 = :15 minutes, 6 = :30 minutes, 9 = :45 minutes.", "tip": "Use fraction circles to show minute increments visually"}, {"step": 4, "title": "Practice Reading Times", "description": "Set various times on teaching clock. Have child read the time. Start with on-the-hour, progress to half-hour, quarter-hour, then 5-minute intervals.", "tip": "Use daily schedule to anchor time reading to real events"}, {"step": 5, "title": "Extend to Digital Time", "description": "Show connection between analog clock and digital display. Practice converting between formats.", "tip": "Some children find digital easier; connect both formats to same concept"}]',
  control_of_error = 'Correct hour and minute positioning; teacher verification; self-checking with clock faces; daily schedule reference',
  direct_aims = '["Learn to read time on an analog clock", "Understand hour and minute hand functions", "Develop temporal awareness and punctuality"]',
  materials = '["Large teaching clock with movable hands", "Individual practice clocks for each child", "Clock cards showing various times", "Digital clock for comparison", "Daily schedule visuals with times"]',
  parent_description = 'Clock Work teaches children to read time on analog and digital clocks. Beginning with simple on-the-hour readings, children progress through 30-minute, 15-minute, 5-minute, and finally individual-minute increments. This work is foundational for independence and scheduling in later years.',
  why_it_matters = 'Being able to tell time is essential for independence, punctuality, and participation in community life. This work develops mathematical concepts (fractions, skip counting) while building practical life skills. Understanding time prepares children for elementary schedules and activities.'
WHERE LOWER(name) = LOWER('Clock Work');

UPDATE montree_classroom_curriculum_works SET
