  materials = '["Timeline of Life"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Timeline of Life');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study of universal human needs: food, shelter, clothing, transportation, defense, culture, religion
• Explores how different cultures meet same needs in different ways
• Includes picture cards, sorting activities, and cultural comparisons
• Develops understanding of human universals and cultural diversity
• Foundation for peace education and cultural appreciation',
  presentation_steps = '[{"step": 1, "title": "Introduction", "description": "''All people everywhere need certain things to live.''", "tip": "Connect to child''s own needs."}, {"step": 2, "title": "Needs Identification", "description": "Present each fundamental need: food, clothing, shelter.", "tip": "Start with physical needs."}, {"step": 3, "title": "Personal Connection", "description": "''How do we meet this need?'' Discuss child''s experience.", "tip": "Local and concrete first."}, {"step": 4, "title": "Cultural Comparison", "description": "Show how other cultures meet same need differently.", "tip": "Pictures, artifacts, stories."}, {"step": 5, "title": "Sorting Activity", "description": "Sort pictures by which need they represent.", "tip": "Multiple cultures per need."}, {"step": 6, "title": "Discussion", "description": "''Why might people meet this need differently?''", "tip": "Geography, climate, resources."}]',
  control_of_error = 'Classification',
  direct_aims = '["Understanding needs"]',
  materials = '["Needs charts"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Fundamental Needs of Humans');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Collect and examine real objects from nature
• Define characteristics of living things
• Sort objects into living/non-living categories
• Discuss what living things need to survive
• Observe ongoing changes in living things',
  presentation_steps = '[{"step": 1, "title": "Gather Natural Materials", "description": "Collect diverse objects: plants, animals, rocks, water, wood, soil. Display all materials attractively for observation.", "tip": "Include items children can touch and examine closely"}, {"step": 2, "title": "Define Living Characteristics", "description": "Discuss: living things breathe, eat, grow, move, respond to environment, and reproduce. Non-living things don''t do these things.", "tip": "Use concrete examples: ''Does this plant breathe? Yes, through its leaves.''"}, {"step": 3, "title": "Three-Period Lesson", "description": "Period 1: Name items as living or non-living. Period 2: Have child identify items you point to. Period 3: Child recalls classification independently.", "tip": "Use consistent language and clear examples"}, {"step": 4, "title": "Sorting Activity", "description": "Child sorts mixed objects into two circles or trays: living and non-living. Discuss reasoning for each placement.", "tip": "Include some tricky items like seeds (living) or branches (once-living)"}, {"step": 5, "title": "Ongoing Observation", "description": "Keep some living things in the classroom and observe changes over time. Water plants, feed animals, observe growth.", "tip": "This concrete experience is the foundation for understanding life"}]',
  control_of_error = 'Correct sorting into living/non-living categories; discussion of characteristics; observation of actual objects',
  direct_aims = '["Understand the fundamental distinction between living and non-living", "Develop observational skills", "Build vocabulary related to life"]',
  materials = '["Living things: plants, insects, small animals", "Non-living objects: rocks, water, sand, metal", "Once-living items: branches, leaves, shells", "Sorting circles or baskets", "Magnifying glasses for observation"]',
  parent_description = 'Living vs. Non-Living is the foundational classification work in Montessori botany. Through hands-on exploration of real objects from nature, children learn to identify characteristics that distinguish living things from non-living objects. This work develops observational skills and respect for nature while preparing for more detailed biological studies.',
  why_it_matters = 'This fundamental classification establishes the basis for all future biological learning. Understanding what makes something alive is essential for studying plants, animals, and ecosystems. This work fosters respect and care for living things and encourages scientific observation.'
WHERE LOWER(name) = LOWER('Living vs Non-Living');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Present five key differences between plants and animals
• Use 3-part cards to sort plants and animals
• Discuss how each type of living thing survives
• Observe specific examples and their characteristics
• Create classification chart showing distinctions',
  presentation_steps = '[{"step": 1, "title": "Prepare Comparison Materials", "description": "Gather variety of plants and animals (or pictures) representing diverse species. Arrange attractively for comparison and observation.", "tip": "Include both common and unusual examples to broaden perspective"}, {"step": 3, "title": "Three-Period Lesson with Cards", "description": "Show cards of various plants and animals. Period 1: Name as plant or animal. Period 2: Have child identify items. Period 3: Child names independently.", "tip": "Use clear, colorful cards with distinct plant and animal images"}, {"step": 4, "title": "Sorting and Classification", "description": "Child sorts mixed cards into plant and animal categories. Discuss characteristics that helped identify each.", "tip": "Include some tricky items to develop critical thinking"}, {"step": 5, "title": "Create Classification Chart", "description": "Together, create a visual chart showing plant characteristics on one side and animal characteristics on the other.", "tip": "Display permanently as reference for future learning"}]',
  control_of_error = 'Correct sorting into plant/animal categories; matching pictures to examples; teacher verification; reference chart',
  direct_aims = '["Understand major differences between plant and animal kingdoms", "Classify living things accurately", "Develop understanding of plant and animal needs"]',
  materials = '["Pictures of diverse plants (flowers, trees, vegetables, mosses)", "Pictures of diverse animals (mammals, insects, birds, fish)", "3-part cards with plant and animal images", "Real specimens for observation", "Chart paper for creating classification chart"]',
  parent_description = 'Plant vs. Animal teaches children the fundamental differences between two major kingdoms of living things. Through observation and classification work, children learn that plants are rooted producers of their own food, while animals are mobile consumers who must eat. This distinction is essential for understanding ecosystems and biodiversity.',
  why_it_matters = 'Understanding the plant-animal distinction is foundational for all biological studies. This classification forms the basis for understanding food chains, ecosystems, and interdependence in nature. The work develops critical thinking and scientific observation skills.'
WHERE LOWER(name) = LOWER('Plant vs Animal');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Puzzle map and nomenclature cards showing plant parts (root, stem, leaf, flower, fruit, seed)
