UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Grow beans or seeds in water to observe root growth
• Identify primary root (taproot) and secondary roots
• Examine root hairs under magnifying glass
• Use 3-part nomenclature cards
• Discuss root functions and absorbing nutrients',
  presentation_steps = '[{"step": 1, "title": "Grow Seeds to Observe Roots", "description": "Plant seeds in jars with water so roots are visible. Observe daily as roots emerge and grow. Allow children to observe the natural development.", "tip": "Bean seeds grow quickly and show dramatic root development"}, {"step": 2, "title": "Introduce Root Terminology", "description": "Show diagrams and real roots. Name parts: primary root (taproot), secondary roots, root hairs, root cap. Discuss function of each part.", "tip": "Use both diagrams and actual specimens for clarity"}, {"step": 3, "title": "Three-Period Lesson", "description": "Use 3-part cards showing root parts. Period 1: Name parts. Period 2: Child identifies parts you point to. Period 3: Child recalls names independently.", "tip": "Include both labeled and unlabeled diagrams"}, {"step": 4, "title": "Observe Root Hairs", "description": "Use magnifying glass to examine root hairs (thin projections where water absorption occurs). Discuss their importance.", "tip": "This sensorial observation makes abstract functions concrete"}, {"step": 5, "title": "Research Root Functions", "description": "Investigate and record how roots absorb water and nutrients, anchor the plant, and store food in some species.", "tip": "Connect to child''s own plant care experiences"}]',
  control_of_error = 'Correct identification of root parts; accurate nomenclature card placement; teacher verification',
  direct_aims = '["Learn the parts of a root and their functions", "Understand how roots absorb water and nutrients", "Develop vocabulary related to plant structure"]',
  materials = '["Seeds (beans, radish, carrot)", "Glass jars for water growing", "Root diagrams and pictures", "3-part nomenclature cards", "Magnifying glasses", "Root specimens if available"]',
  parent_description = 'Parts of a Root teaches children the structure and function of plant roots through hands-on observation. By growing seeds in water and observing roots develop, children see that roots grow downward and absorb nutrients. This work connects abstract botanical concepts to concrete observation.',
  why_it_matters = 'Understanding roots is essential for plant care and plant biology. Observing root growth helps children understand that plants are living, growing organisms with specific needs. This work develops scientific observation skills and fosters care for plants.'
WHERE LOWER(name) = LOWER('Parts of a Root');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Soak seeds to soften seed coat
• Carefully dissect seeds to observe internal parts
• Identify seed coat, stored food, embryo, and first leaf
• Use 3-part cards for nomenclature
• Compare different types of seeds',
  presentation_steps = '[{"step": 1, "title": "Prepare Seeds for Dissection", "description": "Soak large seeds (beans, peanuts) in water overnight. This softens the seed coat, making dissection easier and safer for small hands.", "tip": "Kidney beans and peanuts are ideal for dissection work"}, {"step": 2, "title": "Introduce Seed Terminology", "description": "Show diagram of seed parts: seed coat (protective outer layer), stored food (energy for growth), embryo (tiny plant), and first leaf (cotyledon).", "tip": "Use actual seeds alongside diagrams for clarity"}, {"step": 3, "title": "Carefully Dissect Seeds", "description": "Gently remove seed coat. Identify and separate stored food and embryo. Observe the tiny plant waiting inside. Handle with care and respect.", "tip": "This is a sacred moment of observation; encourage wonder"}, {"step": 4, "title": "Three-Period Lesson with Cards", "description": "Use 3-part cards showing seed parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names parts independently.", "tip": "Include both internal and external views"}, {"step": 5, "title": "Compare Different Seeds", "description": "Examine seeds from various plants. Compare sizes, colors, and structures. Discuss how different seeds survive different conditions.", "tip": "Diversity of seeds demonstrates adaptation"}]',
  control_of_error = 'Correct identification of seed parts; intact embryo; accurate nomenclature placement',
  direct_aims = '["Understand seed structure and parts", "Learn how seeds prepare for growth", "Develop careful observational skills"]',
  materials = '["Large seeds: beans, peanuts, sunflower seeds", "Dishes for soaking", "Seed diagrams and pictures", "3-part nomenclature cards", "Magnifying glasses", "Paper for documenting observations"]',
  parent_description = 'Parts of a Seed introduces children to seed structure through careful dissection work. By opening seeds and observing the tiny plant waiting inside, along with its stored food and protective coat, children understand how seeds work. This concrete exploration prepares them for planting and growing seeds.',
  why_it_matters = 'Understanding seed structure is foundational for plant biology and reproduction. This work develops careful observation, fine motor control, and wonder at nature''s design. Dissecting seeds creates emotional connection to plants and motivation for growing them.'
WHERE LOWER(name) = LOWER('Parts of a Seed');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Sequential cards or objects showing complete life cycle stages
• Butterfly: egg → caterpillar → chrysalis → butterfly
• Frog: egg → tadpole → froglet → adult frog
• Plant: seed → sprout → seedling → mature plant → flower → fruit → seed
• Introduces concepts of metamorphosis and life stages',
  presentation_steps = '[{"step": 1, "title": "Introduction", "description": "''I''ll show you how a butterfly grows and changes.''", "tip": "Start with most familiar cycle."}, {"step": 2, "title": "First Stage", "description": "Present egg. ''A butterfly starts as a tiny egg.''", "tip": "Use models or quality pictures."}, {"step": 3, "title": "Sequential Presentation", "description": "Present each stage in order, describing changes.", "tip": "Dramatic transformation emphasis."}, {"step": 4, "title": "Vocabulary", "description": "Introduce terms: metamorphosis, larva, pupa, etc.", "tip": "Scientific vocabulary."}, {"step": 5, "title": "Sequencing", "description": "Mix stages. Child arranges in correct order.", "tip": "Check logic of sequence."}, {"step": 6, "title": "Comparison", "description": "Compare life cycles across organisms.", "tip": "Notice patterns and differences."}]',
  control_of_error = 'Stages order',
  direct_aims = '["Life cycle stages"]',
  materials = '["Life cycle cards", "Planting materials"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Plant Life Cycle');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Plant seeds and observe germination process
• Test conditions affecting plant growth
• Conduct simple plant experiments (light, water, soil)
• Record observations over time
• Draw conclusions about plant needs',
  presentation_steps = '[{"step": 1, "title": "Plant Seeds for Observation", "description": "Plant seeds in clear containers or jars where roots and shoots are visible. Water appropriately and place in light. Observe daily growth.", "tip": "Clear containers allow continuous observation without disturbing plants"}, {"step": 2, "title": "Set Up Comparative Experiments", "description": "Plant identical seeds in different conditions: with/without light, with/without water, in different soil types. Keep variables isolated.", "tip": "This develops scientific thinking about variables and controls"}, {"step": 3, "title": "Record Daily Observations", "description": "Create a chart to record plant growth: height, leaves, color, etc. Sketch plants or measure growth. Write descriptions of changes.", "tip": "Recording data develops scientific literacy and observation"}, {"step": 4, "title": "Make Predictions", "description": "Before planting, discuss: What will happen? What do plants need? Make predictions. Then compare predictions to actual results.", "tip": "Prediction develops critical thinking and hypothesis formation"}, {"step": 5, "title": "Draw Conclusions", "description": "After 5-7 days (typical germination time), compare results. What did we learn? Which conditions helped plants grow? Why?", "tip": "Connect conclusions to real-world gardening and plant care"}]',
  control_of_error = 'Comparison of experimental conditions; data collection; teacher discussion of results',
  direct_aims = '["Understand plant growth requirements", "Learn the scientific method through experimentation", "Develop observational and data recording skills"]',
  materials = '["Seeds (fast-growing like beans or radishes)", "Clear containers or jars", "Soil and sand", "Water", "Grow lights or sunny location", "Observation chart paper", "Rulers for measuring", "Colored pencils for recording"]',
  parent_description = 'Botany Experiments engages children in the scientific method through plant observation and simple experimentation. By planting seeds, testing different conditions, and recording observations, children discover what plants need to grow. This hands-on science work builds scientific thinking and responsibility.',
  why_it_matters = 'Hands-on experimentation is core to Montessori science. This work develops scientific method skills, observation abilities, and understanding of plant biology. The direct experience of growing plants fosters responsibility and emotional connection to nature while teaching critical thinking.'
WHERE LOWER(name) = LOWER('Botany Experiments');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduce the concept of backbone
• Present examples of vertebrates with backbones
• Show examples of invertebrates without backbones
• Use 3-part cards for classification
• Create Venn diagram showing characteristics',
  presentation_steps = '[{"step": 1, "title": "Introduce the Backbone", "description": "Discuss the spine/backbone in human body. Have children feel their own spine. Explain that vertebrates have backbones, invertebrates don''t.", "tip": "Kinesthetic experience (feeling own spine) makes concept concrete"}, {"step": 2, "title": "Present Vertebrate Examples", "description": "Show pictures and discuss vertebrates: fish, amphibians, reptiles, birds, mammals. Discuss their backbones and body structures.", "tip": "Include diverse examples: humans, whales, snakes, eagles"}, {"step": 3, "title": "Present Invertebrate Examples", "description": "Show invertebrates: insects, spiders, worms, mollusks, echinoderms. Discuss their lack of backbones and how they move/survive.", "tip": "Note that invertebrates make up 90% of all animals"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use animal pictures. Period 1: Name as vertebrate or invertebrate. Period 2: Child identifies. Period 3: Child recalls classification.", "tip": "Include some tricky examples to develop critical thinking"}, {"step": 5, "title": "Create Classification Chart", "description": "Make a visual chart comparing vertebrates and invertebrates. Display permanently as reference.", "tip": "Include both examples and characteristics"}]',
  control_of_error = 'Correct classification as vertebrate/invertebrate; accurate characteristics; teacher verification',
  direct_aims = '["Understand major animal classification system", "Learn about vertebrate and invertebrate characteristics", "Classify animals accurately"]',
  materials = '["Animal pictures (vertebrates and invertebrates)", "3-part nomenclature cards", "Real animals or specimens if available", "Skeleton model or diagram", "Chart paper for classification chart"]',
  parent_description = 'Vertebrate vs. Invertebrate is a foundational animal classification lesson. By understanding that vertebrates have backbones and invertebrates don''t, children can organize vast animal diversity. This work prepares for studying the five classes of vertebrates and various invertebrate groups.',
  why_it_matters = 'This classification system provides essential scaffolding for biological organization. Understanding the vertebrate-invertebrate distinction helps children comprehend animal diversity and adapt their learning. The work develops classification thinking used in all sciences.'
WHERE LOWER(name) = LOWER('Vertebrate vs Invertebrate');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduce five classes: mammals, birds, reptiles, amphibians, fish
• Present characteristics of each class
• Use 3-part cards for each vertebrate type
• Classify animals into correct groups
• Study representative animals from each class',
  presentation_steps = '[{"step": 1, "title": "Introduce the Five Classes", "description": "Present all five vertebrate classes: fish (water, gills, scales), amphibians (live both water and land, metamorphosis), reptiles (dry skin, eggs, cold-blooded), birds (feathers, eggs, warm-blooded), mammals (fur, nurse young, warm-blooded).", "tip": "Present all five together to show complete system"}, {"step": 2, "title": "Present Characteristics", "description": "Discuss unique features of each class. Fish: gills, fins, scales. Amphibians: metamorphosis, moist skin. Reptiles: dry scales, eggs. Birds: feathers, wings, eggs. Mammals: fur, live birth, nursing.", "tip": "Use real examples and pictures to illustrate characteristics"}, {"step": 3, "title": "Three-Period Lesson", "description": "Show pictures of animals. Period 1: Name the class each belongs to. Period 2: Child identifies class. Period 3: Child recalls classification independently.", "tip": "Include diverse examples of each class"}, {"step": 4, "title": "Sorting and Classification", "description": "Present mixed animal cards. Child sorts into five groups representing the five classes. Discuss reasons for classifications.", "tip": "This activity develops observation and classification skills"}, {"step": 5, "title": "Deep Dive into Each Class", "description": "Study each class in depth over time: typical examples, habitats, diets, life cycles, special adaptations.", "tip": "This becomes the foundation for studying individual animals"}]',
  control_of_error = 'Correct classification into five classes; accurate characteristics; teacher verification; reference cards',
  direct_aims = '["Understand the five classes of vertebrates", "Learn characteristics of each vertebrate class", "Classify animals into correct groups"]',
  materials = '["Pictures of animals from each class", "3-part nomenclature cards for each class", "Characteristic cards describing each class", "Real animal specimens or models if available", "Classification chart for display"]',
  parent_description = 'Five Classes of Vertebrates teaches children an essential classification system for organizing animal diversity. By learning that vertebrates are divided into fish, amphibians, reptiles, birds, and mammals—each with distinct characteristics—children can understand and discuss animals more precisely.',
  why_it_matters = 'This classification system is fundamental to zoological thinking. Understanding the five classes provides scaffolding for deeper animal studies and helps children organize vast diversity. The work develops scientific classification and observation skills used throughout biology.'
WHERE LOWER(name) = LOWER('Five Classes of Vertebrates');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study fish anatomy using real or detailed pictures
• Identify external parts: fins, gills, scales, eyes
• Discuss function of each part
• Use 3-part cards for nomenclature
• Discuss how fish parts help them survive in water',
  presentation_steps = '[{"step": 1, "title": "Introduce Fish Body Plan", "description": "Show a real fish or detailed picture. Discuss how fish are shaped for water life. Identify major regions: head, body, tail.", "tip": "If possible, observe a living fish to see parts in action"}, {"step": 2, "title": "Present External Parts", "description": "Identify and name external parts: fins (dorsal, caudal, pectoral, pelvic, anal), gills, scales, eyes, nostrils, mouth, lateral line.", "tip": "Use diagrams with named parts highlighted in color"}, {"step": 3, "title": "Discuss Function of Parts", "description": "For each part, discuss its purpose: fins for movement, gills for breathing, scales for protection, eyes for seeing, lateral line for sensing water movement.", "tip": "Watch fish moving to see how they use different parts"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards showing fish parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names independently.", "tip": "Include both labeled and outline drawings"}, {"step": 5, "title": "Study Fish Adaptations", "description": "Discuss how fish parts are adaptations for water life. Why do they need gills? Why fins? Why streamlined shape?", "tip": "Connect parts to function in fish''s habitat"}]',
  control_of_error = 'Correct identification and naming of parts; accurate placement of nomenclature cards; teacher verification',
  direct_aims = '["Learn the external parts of a fish", "Understand function of fish parts", "Develop vocabulary for fish anatomy"]',
  materials = '["Real fish or detailed pictures/diagrams", "3-part nomenclature cards of fish parts", "Chart showing fish external anatomy", "Optional: aquarium fish to observe", "Colored pencils for labeling"]',
  parent_description = 'Parts of a Fish introduces children to fish anatomy and adaptations. Through observation of real fish or detailed diagrams, children learn external parts and their functions. This work helps children understand how fish are perfectly designed for their aquatic environment.',
  why_it_matters = 'Understanding fish anatomy prepares for studying aquatic life, fish behavior, and marine ecosystems. This work develops observation skills and helps children appreciate the design of living creatures adapted to specific environments.'
WHERE LOWER(name) = LOWER('Parts of a Fish');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study frog anatomy using real frogs or detailed pictures
• Identify external parts: head, eyes, mouth, legs, webbed feet, tympanum
• Use 3-part cards for nomenclature
• Discuss adaptations for water and land life
• Learn about metamorphosis connection',
  presentation_steps = '[{"step": 1, "title": "Introduce Frog Body Plan", "description": "Show a frog or detailed picture. Discuss unique features: webbed feet, bumpy skin, legs suited for jumping. Explain frogs are amphibians living on land and water.", "tip": "Visit a pond if possible to observe live frogs"}, {"step": 2, "title": "Identify External Parts", "description": "Name parts: head, eyes, nostrils, mouth, tympanum (ear drum), forelegs, hind legs, webbed feet, dorsolateral folds, skin.", "tip": "Use diagrams showing all parts clearly labeled"}, {"step": 3, "title": "Discuss Function of Parts", "description": "For each part, discuss purpose: eyes for seeing, nostrils for breathing, webbed feet for swimming, powerful legs for jumping, smooth skin for absorption.", "tip": "Connect parts to how frogs live in two habitats"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards showing frog parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names independently.", "tip": "Include both labeled and outline versions"}, {"step": 5, "title": "Connect to Life Cycle", "description": "Discuss how frog parts change through metamorphosis: tadpole to frog. Gills become lungs, tail disappears, legs develop.", "tip": "This connects to animal life cycle studies"}]',
  control_of_error = 'Correct identification of frog parts; accurate nomenclature; teacher verification',
  direct_aims = '["Learn the external parts of a frog", "Understand amphibian adaptations", "Develop vocabulary for frog anatomy"]',
  materials = '["Real frog or detailed pictures/diagrams", "3-part nomenclature cards of frog parts", "Frog anatomy chart", "Magnifying glass for detailed observation", "Optional: tadpoles to observe life cycle"]',
  parent_description = 'Parts of a Frog teaches children about amphibian anatomy and adaptations. By studying frog parts and understanding how they function in water and on land, children learn about organisms adapted to two environments. This work prepares for studying amphibian life cycles and metamorphosis.',
  why_it_matters = 'Understanding frog anatomy introduces amphibian biology and life cycle concepts. This work develops observation skills and fosters appreciation for creatures adapted to specific environmental niches. The connection to tadpole metamorphosis is particularly fascinating for children.'
WHERE LOWER(name) = LOWER('Parts of a Frog');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study turtle anatomy including shell structure
• Identify external parts: head, carapace, plastron, legs, claws, tail
• Use 3-part cards for nomenclature
• Discuss shell protection and function
• Compare aquatic and land turtle adaptations',
  presentation_steps = '[{"step": 1, "title": "Introduce Turtle Body Plan", "description": "Show a turtle or detailed picture. Highlight distinctive features: protective shell (carapace and plastron), scaly skin, legs, claws. Discuss reptile characteristics.", "tip": "If possible, handle or observe a real turtle to see shell texture"}, {"step": 2, "title": "Identify External Parts", "description": "Name parts: head, eyes, nostrils, jaw/beak, carapace (upper shell), plastron (lower shell), legs, clawed feet, tail, scutes (shell segments).", "tip": "Use diagrams clearly showing all shell and body parts"}, {"step": 3, "title": "Discuss Shell Function", "description": "Explain carapace and plastron protect organs, muscles, and skeleton. Discuss how turtles retract into shell for safety. Show scute patterns.", "tip": "Connect shell structure to protection needs"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards of turtle parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names independently.", "tip": "Include both dorsal and ventral (top and bottom) views"}, {"step": 5, "title": "Compare Turtle Types", "description": "Study aquatic vs. land turtles. Aquatic: flippers, streamlined shell. Land: strong legs, domed shell. Discuss adaptations for each habitat.", "tip": "This shows how same animal adapts to different environments"}]',
  control_of_error = 'Correct identification of turtle parts; accurate nomenclature placement; teacher verification',
  direct_aims = '["Learn the external parts of a turtle", "Understand reptile adaptations", "Develop vocabulary for turtle anatomy"]',
  materials = '["Real turtle or detailed pictures/diagrams", "3-part nomenclature cards of turtle parts", "Turtle anatomy chart", "Empty turtle shell if available", "Colored pencils for labeling work"]',
  parent_description = 'Parts of a Turtle teaches children about reptile anatomy and protective adaptations. By studying the turtle''s shell structure and other body parts, children understand how the shell provides protection while maintaining mobility. This work prepares for studying reptile diversity.',
  why_it_matters = 'Understanding turtle anatomy introduces children to reptiles and defensive adaptations. The shell is a fascinating evolutionary innovation that children find engaging. This work develops observation skills and appreciation for different animal strategies.'
WHERE LOWER(name) = LOWER('Parts of a Turtle');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study bird anatomy using real birds or detailed pictures
• Identify external parts: head, wings, tail, feet, beak
• Discuss feather types and flight
• Use 3-part cards for nomenclature
• Connect parts to bird adaptations for flight',
  presentation_steps = '[{"step": 1, "title": "Introduce Bird Body Plan", "description": "Show a bird or picture. Discuss unique features: feathers, wings, hollow bones, beak. Explain adaptations for flight and life in air.", "tip": "Observe live birds at a window or outdoor bird feeder"}, {"step": 2, "title": "Identify External Parts", "description": "Name parts: head, eyes, nostrils, beak, neck, wings, coverts, torso, tail feathers, legs, feet, claws.", "tip": "Use detailed diagrams labeling all external parts"}, {"step": 3, "title": "Study Feathers", "description": "Examine real feathers. Discuss types: contour feathers (body), flight feathers (wings and tail), down feathers (insulation). Show feather structure.", "tip": "Children love examining feathers closely"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards showing bird parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names independently.", "tip": "Include both labeled diagrams and blank outlines"}, {"step": 5, "title": "Connect to Flight", "description": "Discuss how bird parts enable flight: wings for lift, hollow bones for light weight, streamlined shape, tail for steering.", "tip": "This connects anatomy directly to function"}]',
  control_of_error = 'Correct identification of bird parts; accurate nomenclature; teacher verification of feather types',
  direct_aims = '["Learn external parts of a bird", "Understand bird adaptations for flight", "Develop vocabulary for avian anatomy"]',
  materials = '["Real bird, taxidermy specimen, or detailed pictures", "Real feathers for examination", "3-part nomenclature cards of bird parts", "Bird anatomy chart", "Magnifying glass for feather observation", "Optional: bird field guide"]',
  parent_description = 'Parts of a Bird introduces children to avian anatomy and the adaptations that enable flight. By studying feathers, wings, and skeletal features, children understand how birds are uniquely designed for life in the air. This work prepares for studying bird diversity, behavior, and migration.',
  why_it_matters = 'Understanding bird anatomy unlocks appreciation for the remarkable evolution of flight. This work develops observation skills and fosters wonder at nature''s engineering. Birds are accessible to observe, making this work particularly engaging for children.'
WHERE LOWER(name) = LOWER('Parts of a Bird');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study horse anatomy including distinctive features
• Identify parts: head, neck, mane, body, tail, legs, hooves
• Use 3-part cards for nomenclature
• Discuss horse adaptations for running
• Compare to other mammals',
  presentation_steps = '[{"step": 1, "title": "Introduce Horse Body Plan", "description": "Show a picture or video of a horse. Discuss distinctive features: long neck and head, flowing mane and tail, powerful legs, single hooves. Explain mammal characteristics.", "tip": "Visit a horse farm or stables if possible for live observation"}, {"step": 2, "title": "Identify External Parts", "description": "Name parts: head, muzzle, forelock, ears, eyes, mane, neck, withers, back, torso, croup, tail, forelegs, hind legs, hooves, chest, shoulders.", "tip": "Use large, clear diagrams showing all parts labeled"}, {"step": 3, "title": "Discuss Specialized Features", "description": "Explain: hooves for running on hard ground, powerful legs for speed, long neck for reaching food, mane and tail for communication and fly swatting.", "tip": "Connect each part to its function in horse''s life"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards showing horse parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child names independently.", "tip": "Include side view and front view diagrams"}, {"step": 5, "title": "Study Hooves and Legs", "description": "Focus on hoof structure, leg bone and muscle development. Explain how these adaptations enable horses'' running and jumping abilities.", "tip": "This is particularly interesting to children"}]',
  control_of_error = 'Correct identification of horse parts; accurate nomenclature placement; teacher verification',
  direct_aims = '["Learn external parts of a horse", "Understand mammalian adaptations", "Develop vocabulary for equine anatomy"]',
  materials = '["Pictures of horses, videos, or field trip", "3-part nomenclature cards of horse parts", "Horse anatomy chart", "Real hoof if available", "Optional: horse hair or mane for tactile experience"]',
  parent_description = 'Parts of a Horse teaches children about mammalian anatomy through study of a familiar and beloved animal. By learning horse parts and understanding how they work together for running, jumping, and grazing, children appreciate mammalian adaptations. This work prepares for studying mammals broadly.',
  why_it_matters = 'Horses are iconic animals that engage children''s imagination. Understanding horse anatomy develops observation skills and appreciation for specialized adaptations. The work connects to mammals generally and introduces skeletal and muscular systems.'
WHERE LOWER(name) = LOWER('Parts of a Horse');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study of where animals live and habitat requirements
• Habitats: forest, ocean, desert, grassland, tundra, rainforest
• Develops understanding of animal-environment connection
• Classification by home environment
• Connects to geography and ecology',
  presentation_steps = '[{"step": 1, "title": "Habitat Introduction", "description": "''Animals live in different places called habitats.''", "tip": "Define habitat."}, {"step": 2, "title": "Habitat Types", "description": "Present pictures of major habitats.", "tip": "Visual introduction."}, {"step": 3, "title": "Animal Matching", "description": "Match animal cards to appropriate habitats.", "tip": "Where would this animal live?"}, {"step": 4, "title": "Requirements", "description": "Discuss what each habitat provides: food, water, shelter.", "tip": "Needs-based thinking."}, {"step": 5, "title": "Adaptation", "description": "How are animals suited to their habitats?", "tip": "Adaptation introduction."}, {"step": 6, "title": "Diorama", "description": "Create habitat diorama with animals.", "tip": "Artistic representation."}]',
  control_of_error = 'Wrong habitat',
  direct_aims = '["Habitat vocabulary"]',
  materials = '["Habitat cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Animal Habitats');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduce animals living on each of seven continents
• Present using continent cards or puzzle maps
• Classify animals by where they live
• Use 3-part cards for animal names
• Discuss adaptations to different climates',
  presentation_steps = '[{"step": 1, "title": "Prepare Continent Materials", "description": "Use continent cards or puzzle maps showing all seven continents. Gather pictures of 5-8 representative animals from each continent.", "tip": "Include diverse animals: predators, herbivores, birds, reptiles"}, {"step": 2, "title": "Present Continent-by-Continent", "description": "Introduce one continent at a time. Show where it is on map. Present 5-8 animals native to that continent. Discuss climate and habitat.", "tip": "Start with familiar animals children recognize"}, {"step": 3, "title": "Three-Period Lesson", "description": "Show animal pictures. Period 1: Name animal and continent. Period 2: Child identifies. Period 3: Child recalls names and continents.", "tip": "Use 3-part cards: picture, name label, continent label"}, {"step": 4, "title": "Sorting Activity", "description": "Mix all animal pictures. Child sorts by continent, placing animals on correct continent card. Discuss reasoning for placements.", "tip": "This activity develops geography and zoology together"}, {"step": 5, "title": "Study Continent Characteristics", "description": "For each continent, discuss climate, terrain, vegetation. Explain how animals adapt to their continent''s specific environment.", "tip": "This creates foundation for ecosystem and adaptation studies"}]',
  control_of_error = 'Correct animal-to-continent sorting; accurate naming; reference map',
  direct_aims = '["Learn animals native to each continent", "Understand global biodiversity", "Develop geography and zoology vocabulary"]',
  materials = '["Continent cards or puzzle map", "Pictures of animals from each continent", "3-part cards: animal picture, name, continent", "World map for reference", "Optional: animal figurines for sorting"]',
  parent_description = 'Animals of the Continents teaches children where different animals live around the world. By learning which animals are native to each continent and understanding how they''ve adapted to their environment''s climate, children develop global awareness and appreciation for biodiversity.',
  why_it_matters = 'This work integrates geography and zoology, showing how animals and their environments are interconnected. It fosters global perspective and appreciation for biodiversity. The work prepares for understanding ecosystems, adaptation, and conservation.'
WHERE LOWER(name) = LOWER('Animals of the Continents');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study life cycles of different animal types
• Compare mammal, bird, fish, amphibian, insect cycles
• Use 3-part cards showing cycle stages
• Observe real examples (tadpole to frog, caterpillar to butterfly)
• Create cycle diagrams',
  presentation_steps = '[{"step": 1, "title": "Introduce Concept of Life Cycle", "description": "Discuss that all animals are born, grow, reproduce, and die. This pattern is the life cycle. Different animals have very different cycles.", "tip": "Connect to children''s own lives: baby, child, adult"}, {"step": 2, "title": "Study Mammal Cycle", "description": "Using horse or other mammal example: born live from mother, nursed, grow, become adult, reproduce. Create simple diagram showing stages.", "tip": "Children can relate to mammal reproduction from human experience"}, {"step": 3, "title": "Study Bird Cycle", "description": "Bird eggs, incubation, hatching, fledgling stage, adult. Show pictures of eggs, nestlings, fledglings, adults.", "tip": "Find pictures of actual bird nests and development"}, {"step": 4, "title": "Study Metamorphosis Cycles", "description": "Focus on dramatic transformations: tadpole\u2192frog, caterpillar\u2192butterfly. Use pictures and 3-part cards showing each stage. Observe if possible.", "tip": "Metamorphosis is particularly fascinating to children"}, {"step": 5, "title": "Compare Cycles", "description": "Create chart comparing life cycles of different animal types. Discuss why cycles are different and how they fit each animal''s habitat.", "tip": "This shows diversity of reproductive strategies"}]',
  control_of_error = 'Correct sequence of life cycle stages; accurate stage identification from pictures; reference diagrams',
  direct_aims = '["Understand basic life cycle concept", "Learn life cycles of various animal types", "Recognize stages of metamorphosis"]',
  materials = '["3-part cards showing life cycle stages", "Pictures of different animal cycles", "Life cycle figurines or models (Safari Ltd.)", "Real specimens if possible (tadpoles, caterpillars)", "Chart paper for creating comparison diagrams"]',
  parent_description = 'Animal Life Cycles teaches children how different animals are born, grow, reproduce, and eventually die. By studying specific examples like tadpole-to-frog transformation and caterpillar-to-butterfly metamorphosis, children appreciate the diversity of life strategies. This work connects to Montessori''s cosmic education.',
  why_it_matters = 'Understanding life cycles is fundamental to biology and ecology. This work develops observation skills and respect for living things. The cycles connect to seasons, weather, and environmental changes, integrating multiple curriculum areas.'
WHERE LOWER(name) = LOWER('Animal Life Cycles');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Physical science experiment: what sinks and what floats
• Develops hypothesis testing and observation skills
• Introduces concepts of density and buoyancy
• Connects to water properties and states of matter
• Scientific method introduction',
  presentation_steps = '[{"step": 1, "title": "Question", "description": "''Will this sink or float?'' Present various objects.", "tip": "Create curiosity."}, {"step": 2, "title": "Prediction", "description": "Child predicts before testing.", "tip": "Hypothesis formation."}, {"step": 3, "title": "Testing", "description": "Gently place object in water. Observe.", "tip": "Scientific observation."}, {"step": 4, "title": "Recording", "description": "Record results: sink or float column.", "tip": "Data collection."}, {"step": 5, "title": "Pattern Finding", "description": "''What do floating things have in common?''", "tip": "Analysis."}, {"step": 6, "title": "Explanation", "description": "Simple density explanation.", "tip": "Concept introduction."}]',
  control_of_error = 'Object sinks/floats',
  direct_aims = '["Density concept"]',
  materials = '["Water basin", "Various objects"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Sink and Float');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Exploring magnetic attraction and repulsion
• Sorting magnetic and non-magnetic materials
