  presentation_steps = '[{"step": 1, "title": "Magnet Introduction", "description": "Present magnet. Demonstrate attraction to metal.", "tip": "Wonder creation."}, {"step": 2, "title": "Prediction", "description": "''Will the magnet attract this?'' Predict before testing.", "tip": "Hypothesis practice."}, {"step": 3, "title": "Testing", "description": "Test various objects with magnet.", "tip": "Systematic testing."}, {"step": 4, "title": "Sorting", "description": "Sort objects: magnetic and non-magnetic.", "tip": "Classification."}, {"step": 5, "title": "Pattern Finding", "description": "''What material are magnetic things made of?''", "tip": "Metal discovery."}, {"step": 6, "title": "Poles", "description": "Explore magnetic poles: attract and repel.", "tip": "Force direction."}]',
  control_of_error = 'Attracts or not',
  direct_aims = '["Magnetism concept"]',
  materials = '["Magnets", "Various objects"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Magnetic/Non-Magnetic');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Solid, liquid, gas exploration and identification
• State changes through heating and cooling
• Develops observation and classification skills
• Connects to water cycle and everyday experiences
• Foundation for chemistry concepts',
  presentation_steps = '[{"step": 1, "title": "Three States", "description": "Present ice cube, water, steam. ''Same substance, different forms.''", "tip": "Water example."}, {"step": 2, "title": "Solid Properties", "description": "''Solids keep their shape.'' Examples: ice, book, table.", "tip": "Definition and examples."}, {"step": 3, "title": "Liquid Properties", "description": "''Liquids take container shape.'' Pour water between containers.", "tip": "Demonstration."}, {"step": 4, "title": "Gas Properties", "description": "''Gases spread out. We can''t always see them.'' Breath visible in cold.", "tip": "Abstract concept."}, {"step": 5, "title": "Changes", "description": "Melt ice, boil water, freeze water.", "tip": "Transformation observation."}, {"step": 6, "title": "Sorting", "description": "Sort pictures/objects by state.", "tip": "Classification practice."}]',
  control_of_error = 'Classification',
  direct_aims = '["Three states"]',
  materials = '["Ice, water"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('States of Matter');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Exploring primary and secondary color relationships
• Hands-on mixing with paint or colored water
• Develops prediction and observation skills
• Connects art and science
• Foundation for color theory',
  presentation_steps = '[{"step": 1, "title": "Primary Colors", "description": "Present red, yellow, blue. ''These are primary colors.''", "tip": "Cannot be made from mixing."}, {"step": 2, "title": "Prediction", "description": "''What will happen if we mix red and yellow?''", "tip": "Hypothesis formation."}, {"step": 3, "title": "Mixing", "description": "Mix two primaries. Discover orange.", "tip": "Wow moment."}, {"step": 4, "title": "Secondary Colors", "description": "Discover all secondaries: orange, green, purple.", "tip": "Systematic exploration."}, {"step": 5, "title": "Recording", "description": "Create color mixing chart.", "tip": "Documentation."}, {"step": 6, "title": "Application", "description": "Use mixing knowledge in art projects.", "tip": "Practical application."}]',
  control_of_error = 'Color result',
  direct_aims = '["Color theory"]',
  materials = '["Primary colors"]',
  parent_description = NULL,
  why_it_matters = NULL
