UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study six simple machines: lever, wheel and axle, pulley, ramp, wedge, screw
• Use hands-on activities to demonstrate each machine
• Identify simple machines in classroom and home
• Create or manipulate examples of each type
• Discuss mechanical advantage',
  presentation_steps = '[{"step": 1, "title": "Introduce Simple Machines Concept", "description": "Discuss that machines help us do work. Simple machines are the basic types all other machines are based on. There are six simple machines.", "tip": "Start with machines children use daily"}, {"step": 2, "title": "Present Lever", "description": "A lever is a bar that pivots on a fulcrum. Examples: seesaw, crowbar, prying open a can. Use a ruler and pencil to demonstrate.", "tip": "Children can feel the mechanical advantage"}, {"step": 3, "title": "Present Wheel and Axle", "description": "A wheel attached to an axle rotates together. Examples: doorknob, steering wheel, bicycle wheel. Discuss how it makes work easier.", "tip": "Bring in examples children can turn and manipulate"}, {"step": 4, "title": "Present Pulley, Ramp, Wedge, Screw", "description": "Pulley: changes direction of force. Ramp: inclined plane reducing effort. Wedge: two inclined planes for splitting. Screw: inclined plane wrapped around cylinder.", "tip": "Demonstrate each with concrete examples"}, {"step": 5, "title": "Identify Machines Everywhere", "description": "Hunt for simple machines in classroom and school: doorknobs (wheel/axle), scissors (lever), stairs (ramp), pencil sharpener (screw).", "tip": "This shows how fundamental these machines are"}]',
  control_of_error = 'Teacher demonstration of how machines work; child''s ability to identify examples; hands-on manipulation',
  direct_aims = '["Understand the six simple machines", "Learn how machines reduce work and effort", "Develop vocabulary for machine types"]',
  materials = '["Examples of each simple machine or materials to build them", "Levers: rulers, crowbars, pencils", "Wheels: doorknobs, toy wheels, bicycle parts", "Pulleys: rope, pulleys, flagpole", "Ramps: boards, inclined planes", "Wedges: axes, door stops, knives", "Screws: screwdrivers, jars, spiral models"]',
  parent_description = 'Simple Machines introduces children to engineering fundamentals through study of six basic machines: lever, wheel and axle, pulley, ramp, wedge, and screw. By identifying these machines in daily life and manipulating examples, children understand how machines reduce the effort needed for work.',
  why_it_matters = 'Understanding simple machines provides foundation for physics and engineering. This work develops problem-solving thinking and helps children appreciate design. Recognizing simple machines everywhere fosters scientific observation and critical thinking about how things work.'
WHERE LOWER(name) = LOWER('Simple Machines');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Establish regular outdoor nature observation
• Use magnifying glasses and observation journals
• Study seasonal changes and patterns
• Collect specimens for classroom study
• Foster wonder and respect for natural world',
  presentation_steps = '[{"step": 1, "title": "Establish Outdoor Observation Practice", "description": "Take children outside regularly to observe nature. Use all senses: look, listen, touch, smell. Notice plants, animals, weather, seasonal changes.", "tip": "Regular routine outdoors is more valuable than occasional trips"}, {"step": 2, "title": "Use Observation Tools", "description": "Provide magnifying glasses, binoculars, collection containers, observation notebooks. Encourage careful observation and documentation.", "tip": "Children love using tools and feeling like scientists"}, {"step": 3, "title": "Create Nature Journal", "description": "Keep a journal documenting observations: draw plants and insects, record weather, write descriptions, note seasonal changes, press flowers.", "tip": "This develops writing, drawing, and observation skills"}, {"step": 4, "title": "Study Seasonal Cycles", "description": "Visit same location through all seasons. Observe: what changes? What plants bloom? What animals appear/disappear? How does weather change?", "tip": "This connects to calendar work and life cycles"}, {"step": 5, "title": "Cultivate Wonder and Care", "description": "Encourage children to develop relationship with nature. Notice details. Ask questions. Develop commitment to care for environment.", "tip": "Nature study is spiritual practice in Montessori"}]',
  control_of_error = 'Accurate observations recorded; identification of plants and animals; evidence of learning through documentation',
  direct_aims = '["Develop careful observation skills", "Learn to identify common plants and animals", "Understand seasonal patterns and cycles"]',
  materials = '["Magnifying glasses", "Binoculars", "Observation journal/notebook", "Colored pencils", "Collection containers", "Field guides for local flora and fauna", "Pressing paper for flowers", "Recording materials"]',
  parent_description = 'Nature Study is central to Montessori education—children develop observational skills and love of nature through regular outdoor exploration. By keeping observation journals, using tools like magnifying glasses, and visiting the same places through seasons, children notice patterns and develop scientific thinking.',
  why_it_matters = 'Nature study is foundational to Montessori''s cosmic education. Direct observation of nature develops sensory refinement and scientific thinking. This work fosters environmental consciousness and responsibility that shapes children''s values and future choices.'
WHERE LOWER(name) = LOWER('Nature Study');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Observe weather patterns through all five senses", "Use real materials (rain gauges, weather maps, thermometers)", "Connect outdoor observations to classroom learning", "Introduce weather vocabulary in three-period lessons", "Explore cultural and seasonal variations in weather"]',
  presentation_steps = '[{"title": "Sensory Weather Observation", "description": "Take children outdoors at different times of day and in varied weather conditions. Guide them to observe using all senses: feel the wind, listen to rain or rustling leaves, watch cloud formations, smell fresh air or petrichor. Encourage careful observation without judgment.", "tip": "Use the same observation spot at different times to notice changes and patterns."}, {"title": "Weather Recording Materials", "description": "Introduce charts, graphs, and weather journals where children can track daily observations. Use pictorial weather symbols (sun, clouds, rain, snow) that children can place on calendars or charts. Show how temperatures change throughout the day and seasons.", "tip": "Keep recordings simple and visual for younger children; gradually introduce more sophisticated graphs for older students."}, {"title": "Weather Tools Presentation", "description": "Demonstrate how to use weather instruments: thermometer (temperature), rain gauge (precipitation), wind sock or simple indicator (wind direction). Allow children to handle and explore each tool, then use them to gather real data from your classroom environment.", "tip": "Start with thermometers and rain gauges before introducing more complex meteorological concepts."}, {"title": "Three-Period Lesson: Weather Vocabulary", "description": "Period 1 (Introduction): Show real materials while naming weather terms (cumulus, stratus, fog, sleet, hail). Period 2 (Recognition): Ask ''Show me the stratus clouds'' or ''Where is precipitation?'' Period 3 (Recall): ''What is this called?'' Use the same materials throughout all three periods.", "tip": "Use photographs and real outdoor observations rather than just pictures. Repeat over multiple lessons."}, {"title": "Weather and Culture Connections", "description": "Explore how different cultures respond to weather through clothing, housing, agriculture, and traditions. Look at houses designed for hot climates, cold climates, rainy regions. Discuss seasonal celebrations and festivals tied to weather patterns.", "tip": "Use maps and globes to show regions and their typical weather patterns. Connect to other cultural studies."}, {"title": "Weather Experiments and Extensions", "description": "Conduct age-appropriate weather experiments: making rainbows with water and light, creating wind with fans, evaporation demonstrations, cloud formation in jars. Children observe cause and effect in weather phenomena.", "tip": "Connect experiments directly to observations children have made outdoors."}, {"title": "Independent Weather Station", "description": "Set up a weather station where children can independently record observations, check instruments, and maintain weather journals. Provide a format that children can use repeatedly, fostering independence and responsibility for data collection.", "tip": "Create a visual checklist showing the steps for weather observation and recording."}]',
  control_of_error = '["Correct use of weather instruments (reading gauges accurately)", "Accurate observation of cloud types and weather phenomena", "Proper recording methods in weather journals and charts", "Matching weather observations to correct weather vocabulary", "Understanding cause-effect relationships in weather patterns"]',
  direct_aims = '["To understand weather as a natural phenomenon affecting daily life", "To develop observational skills using scientific tools", "To learn weather vocabulary and meteorological concepts", "To recognize weather patterns and seasonal changes", "To understand connections between weather, geography, and human activity"]',
  materials = '["Thermometer (standard and/or large demonstration model)", "Rain gauge", "Wind indicators or weather vane", "Weather charts and calendars with picture symbols", "Weather journals or notebooks", "Pictures of different cloud types", "Weather maps and globes", "Real samples: rain water, soil from different climates, pictures of weather phenomena", "Seasonal clothing items to discuss", "Art materials for weather representations"]',
  parent_description = 'In this work, children become real meteorologists, learning to observe and understand the weather that touches their daily lives. They use actual weather instruments and scientific methods to gather data, building both practical skills and scientific thinking. By connecting weather to cultural practices, geography, and natural cycles, children develop a deeper understanding of how the environment shapes human life across the planet.',
  why_it_matters = 'Weather study develops scientific observation skills while connecting children to the natural world. It builds understanding of cause-and-effect relationships, introduces measurement and data collection, and fosters environmental awareness. Through weather study, children recognize patterns in nature and understand the interconnections between climate, geography, and culture.'
WHERE LOWER(name) = LOWER('Weather Study');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Focus on careful observation of real objects and still life", "Provide quality materials: sharp pencils, fine paper, good erasers", "Model slow, deliberate drawing techniques without demonstration of final products", "Emphasize process and observation over artistic product", "Allow extended, uninterrupted work time for sustained focus"]',
  presentation_steps = '[{"title": "Introduction to Drawing Materials", "description": "Present quality drawing tools individually: pencils of different grades (HB, 2B), fine-tipped colored pencils, erasers, sharpeners. Show proper grip and posture. Demonstrate care for materials and how to maintain sharp pencil points. Allow children to practice pencil control with basic strokes on scrap paper.", "tip": "Keep material presentation simple and sensory; let children feel the difference between pencil grades."}, {"title": "Observation and Still Life Setup", "description": "Arrange a simple still life with 2-3 interesting objects: a potted plant, a flower in a vase, fruits, natural items like shells or stones. Position it at eye level where the child can observe from multiple angles. Discuss what they see: shapes, lines, textures, light and shadow, proportions.", "tip": "Start with organic forms (plants, fruit) rather than geometric objects. Keep arrangements simple to avoid overwhelming."}, {"title": "Guided Observation Lesson", "description": "Before drawing, spend time just observing. Ask open-ended questions: ''What shapes do you see?'' ''How are the parts connected?'' ''Where is the light coming from?'' ''What textures do you notice?'' Encourage detailed looking without immediately beginning to draw.", "tip": "This observation period is crucial for developing looking skills that transfer to drawing quality."}, {"title": "Drawing Technique Presentation", "description": "Show how to start with light lines to establish proportions and basic shapes before adding detail. Demonstrate using long, flowing strokes rather than scratchy marks. Show how to use different pencil pressures to create variation. Work slowly and deliberately, narrating your thinking about what you observe.", "tip": "Do not demonstrate a finished product; instead, show the process of careful observation and mark-making."}, {"title": "Independent Drawing Work", "description": "Child works independently, drawing the still life at their own pace. Adult observes but does not interrupt or direct. If the child becomes frustrated, ask questions to redirect their observation: ''What shapes do you see?'' rather than ''Your lines should be straighter.''", "tip": "Extended work time (20-40 minutes) helps develop focus and deeper observation."}, {"title": "Appreciation and Reflection", "description": "After drawing, discuss what the child noticed while drawing. Ask: ''What was most difficult?'' ''What did you observe that surprised you?'' ''How does your drawing help you remember what you saw?'' Appreciate the effort and observation, not just the final product.", "tip": "Create a portfolio or display where students can track their drawing development over time."}]',
  control_of_error = '["Quality of observation reflected in accuracy of proportions", "Appropriate use and care of drawing materials", "Pencil control and line quality improvement over time", "Sustained attention and completion of works", "Self-evaluation of effort and improvement"]',
  direct_aims = '["To develop careful observational skills", "To learn proper techniques for using drawing materials", "To represent observed objects accurately on paper", "To develop fine motor control and hand-eye coordination", "To appreciate the role of drawing in understanding the world"]',
  materials = '["Variety of pencils: HB, 2B, 4B for different effects", "Fine white and cream drawing paper (not thin copy paper)", "Erasers: kneaded erasers (gentle) and regular erasers", "Pencil sharpener (hand-crank or electric for consistency)", "Still life objects: potted plants, flowers, fruits, natural items (shells, stones), bottles, simple vessels", "Smooth table or drawing board", "Good lighting (natural or focused lamp)", "Optional: colored pencils for extension, fixative spray to preserve finished work"]',
  parent_description = 'Drawing in the Montessori classroom is much more than art class. It''s a tool for learning to truly see the world. Children spend time carefully observing real objects, then practice representing what they see with pencil and paper. The focus is on the observational process, not on making a perfect picture. Through this work, children develop the patience to look deeply, the coordination to control their pencils precisely, and the confidence to capture their observations on paper.',
  why_it_matters = 'Drawing develops observational skills that enhance learning across all subjects. It builds fine motor control, hand-eye coordination, and visual discrimination. Drawing teaches patience, focus, and the value of sustained effort. It connects children to the artistic tradition and helps them understand how artists see and represent the world.'
WHERE LOWER(name) = LOWER('Drawing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Start with process-based painting, not product-focused projects", "Limit initial color choices (one color or primary colors only)", "Model proper brush techniques: full brush, deliberate strokes, careful cleanup", "Provide quality watercolors or tempera paints with appropriate brushes", "Create a well-organized, accessible painting station with clear care procedures"]',
  presentation_steps = '[{"title": "Introduction to Painting Materials", "description": "Present painting materials one at a time: watercolor cakes or tempera paint, quality brushes of different sizes, water containers, paper, palette. Show how to care for each: dampening watercolors gently, rinsing brushes thoroughly, organizing materials neatly. Let children handle and explore materials sensory.", "tip": "Use two containers for water (one for rinsing, one for clean water) to keep water fresh longer."}, {"title": "Brush Control and Technique", "description": "Demonstrate how to hold a brush properly (like a pencil but lower, near the ferrule for control), how to load the brush with paint, and how to make different strokes. Show full brush strokes versus dry brush, light touches versus heavy pressure. Work slowly and deliberately, showing thoughtful use rather than frantic painting.", "tip": "Demonstrate on large paper so all children can see clearly. Use a single color at first."}, {"title": "Color Exploration with Limited Palette", "description": "Introduce one color first, allowing extensive exploration with that color in different intensities and applications. After many sessions, introduce a second color, then a third. Work with primary colors before introducing secondary color mixing. Let children discover color combinations through direct experience.", "tip": "Limiting choices prevents overwhelm and allows deeper exploration of color properties."}, {"title": "Water and Wash Techniques", "description": "Teach how to adjust paint consistency with water: very wet creates washes that flow, drier paint creates bold strokes. Show how water can be used deliberately to create effects. Demonstrate rinsing colors into each other versus keeping them separate. This is particularly important for watercolor work.", "tip": "Much of the learning in painting happens through experimenting with water and paint consistency."}, {"title": "Care and Cleanup Procedures", "description": "Model thorough brush rinsing in running water until bristles are completely clean. Show how to gently squeeze water from brushes and store them bristles-up in containers. Demonstrate wiping up spills immediately, rinsing water containers, and organizing materials for the next person. Make cleanup part of the respected work.", "tip": "Create a visual checklist showing cleanup steps. Make cleanup as structured as the painting itself."}, {"title": "Independent Painting Work", "description": "Child works independently with painting materials. Provide quality paper (not thin), adequate space, and protective covering. Allow extended time (30-45 minutes) for children to explore and develop their work. Observe without interrupting; only help with practical issues (spilled water) or answer direct questions.", "tip": "Process is more important than product. Children might paint over their work or create many small paintings\u2014both are valuable."}, {"title": "Reflection and Appreciation", "description": "After painting, discuss the experience: ''What colors did you mix?'' ''How did the paint behave differently when you added more water?'' ''What was easy or challenging?'' Display finished works respectfully, appreciating both the color explorations and any representational attempts.", "tip": "Help children notice their own growth: comparing early paintings to later ones shows development."}]',
  control_of_error = '["Quality of brush control and technique use", "Proper care of materials (clean brushes, organized supplies)", "Appropriate water management (not too wet, not too dry)", "Understanding when paint is properly mixed", "Ability to follow cleanup procedures independently"]',
  direct_aims = '["To learn proper techniques for using painting materials", "To understand color properties and relationships", "To develop fine motor control through brush work", "To explore color as a means of expression", "To create paintings independently from concept to completion"]',
  materials = '["Quality watercolors (cake or tube) or tempera paints", "Natural bristle brushes: round and flat in various sizes (small, medium, large)", "Watercolor paper or heavy drawing paper (not thin copy paper)", "Two containers for water (rinsing and clean water)", "Palettes or mixing surfaces (watercolor palettes or white dishes)", "Paper towels or cotton rags for drying brushes", "Newspaper or plastic covering for tables", "Aprons or smocks to protect clothing", "Optional: spray bottle for keeping watercolors moist, water bucket for rinsing brushes under running water"]',
  parent_description = 'Painting in Montessori is about process and discovery, not about creating perfect pictures. Children learn to use brushes and paints skillfully, to understand how colors behave in water, and to express themselves through color and mark-making. They explore color relationships, develop fine motor skills, and build confidence in their creative abilities. The focus is on enjoying the medium, not achieving a specific product.',
  why_it_matters = 'Painting develops fine motor skills, color perception, and creative expression. It builds patience and sustained focus as children spend extended time with materials. Through painting, children learn about color theory and visual composition. It provides an emotional outlet for expression and builds confidence in artistic endeavors.'
WHERE LOWER(name) = LOWER('Painting');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Provide variety of colored papers, fabrics, and natural materials to choose from", "Focus on process of selecting, arranging, and combining materials", "Teach proper use of scissors and adhesives with clear demonstrations", "Celebrate diverse approaches to composition and material selection", "Encourage thoughtful material choices rather than random gluing"]',
  presentation_steps = '[{"title": "Introduction to Collage Materials", "description": "Present a variety of materials for collage: colored papers of different textures (construction paper, tissue, metallic, textured paper), fabric scraps (cotton, silk, burlap, felt), natural materials (dried leaves, seeds, twigs, grasses), and other items (foil, felt pieces, paper scraps). Organize materials in accessible containers so children can see color and variety. Allow exploration of how materials feel and look.", "tip": "Change materials seasonally; include natural items children can collect outdoors."}, {"title": "Scissors and Cutting Technique", "description": "Demonstrate proper scissors grip (thumb in small hole, fingers in large hole, hand below the scissors). Show how to open and close scissors with controlled movements. Practice cutting straight lines, curves, and small pieces. Allow children to practice cutting before beginning a collage project. Provide child-safe scissors with round ends but sharp enough to cut paper.", "tip": "Some children may need hand-strengthening activities before using scissors successfully."}, {"title": "Adhesive Use and Technique", "description": "Show how to apply glue or paste: a light, even coating on the back of paper pieces, not excessive. Demonstrate with a glue stick first (simpler), then introduce liquid glue if desired. Show pressing pieces down gently to secure. Demonstrate cleanup with a damp cloth. Emphasize that less glue is better than more.", "tip": "Glue sticks are easier for young children to control; liquid glue offers more challenge for older students."}, {"title": "Material Selection and Planning", "description": "Invite the child to think about their collage: ''What colors do you like? What materials will you use? Will you represent something, or create an abstract design?'' Encourage thoughtful selection rather than random gluing. Some children might plan on paper first by arranging pieces; others might create spontaneously. Both approaches are valid.", "tip": "Ask open-ended questions rather than directing specific designs: ''What do you want to create?'' not ''Make a butterfly.''"}, {"title": "Collage Creation Process", "description": "Child selects and arranges materials on a base paper (often heavier stock to support glued pieces). They experiment with placement, overlap, and composition before gluing. Once satisfied, they glue pieces in place, pressing to secure. Work can be completely abstract or representational\u2014both are equally valuable. Allow extended time for this creative process.", "tip": "Some children benefit from trying dry arrangements first (without glue) to plan their design."}, {"title": "Variation: Themed Collages", "description": "After children become comfortable with basic collage, introduce themed collages: using only warm colors, using only natural materials, creating a specific subject (landscape, portrait, seasons). This adds focus and creative challenge without limiting expression.", "tip": "Themed collages help children practice composition and material selection more intentionally."}, {"title": "Cleanup and Display", "description": "Model quick cleanup: gathering scraps, putting glue containers away, wiping tables. Display finished collages respectfully where light can enhance the materials and colors. Appreciate each child''s unique choices and combinations.", "tip": "Collages often look best when displayed near a light source where textures and colors shine."}]',
  control_of_error = '["Proper use of scissors and safety", "Appropriate amount of adhesive used", "Stability of glued materials (enough glue to secure without excess)", "Thoughtful material selection and arrangement", "Completion and presentation of work"]',
  direct_aims = '["To learn to use scissors safely and effectively", "To explore color, texture, and material combinations", "To make thoughtful creative choices in composition", "To develop fine motor skills through cutting and gluing", "To create collage artwork independently from concept to completion"]',
  materials = '["Variety of colored papers: construction paper, tissue, metallic, patterned paper", "Base papers: heavier stock (cardstock, watercolor paper) to support collage pieces", "Fabric scraps: cotton, silk, burlap, felt, lace", "Natural materials: dried leaves, pressed flowers, seeds, twigs, grasses, moss", "Other materials: foil, foam pieces, corrugated cardboard scraps, magazine cutouts", "Child-safe scissors (round-ended but sharp)", "Glue sticks and/or liquid glue (PVA or craft glue)", "Containers for organizing materials by color", "Newspaper or plastic covering for tables"]',
  parent_description = 'Collage invites children to explore the visual world through cutting, selecting, and combining materials. They develop practical skills with scissors and glues while making creative decisions about color, texture, and composition. Children learn by doing—discovering how materials work together, what appeals to their eye, and how to plan and execute their own artistic vision. The beauty of collage is that there''s no ''wrong'' way to arrange materials.',
  why_it_matters = 'Collage develops fine motor skills, visual discrimination, and creative decision-making. It builds understanding of composition, color relationships, and texture. Collage activities are accessible to children of all abilities and encourage diverse artistic expression. They provide tactile, visual, and creative engagement that builds confidence in artistic pursuits.'
WHERE LOWER(name) = LOWER('Collage');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Provide quality clay or playdough for pure sensory exploration and manipulation", "Focus on process: squeezing, rolling, pinching, patting, pressing", "Offer minimal tools; let hands and fingers be primary tools", "Allow extended, unstructured time for material exploration", "Introduce basic techniques (pinch bowls, coils, simple forms) gradually"]',
  presentation_steps = '[{"title": "Introduction to Clay and Playdough", "description": "Present high-quality clay or playdough that is soft enough to manipulate but holds shape. Show the material in its raw form; let children feel it, squeeze it, notice its properties. Demonstrate that it can be shaped, flattened, and reformed repeatedly. Allow free exploration before teaching specific techniques.", "tip": "Homemade playdough (flour, salt, water, oil, food coloring) works well and costs little; store in airtight containers."}, {"title": "Sensory Exploration: Hands and Fingers", "description": "Invite the child to explore how their hands affect the material: squeeze it, pat it, press it, roll it between palms. Push fingers into it, flatten it with whole hand, make patterns with fingertips. This sensory exploration is valuable in itself\u2014not focused on making a finished product.", "tip": "Extended time with material (20-30 minutes) allows deep sensory engagement and reduces frustration."}, {"title": "Simple Technique: Pinch Bowls", "description": "Show how to make a small ball of clay, then use thumb to create a bowl shape by pinching and rotating. Demonstrate slowly, showing how to maintain thickness as you pinch. This teaches simple hand coordination and creates a usable form. Emphasize that pinch bowls don''t need to be perfect\u2014they''re about the process.", "tip": "Pinch bowls teach hand control without requiring complex motor planning."}, {"title": "Rolling and Coils", "description": "Demonstrate rolling clay into long ''snakes'' or coils by rolling between palms or on a table surface. Show how coils can be arranged in spirals, circles, or patterns. Explore what happens when coils are pressed together or stacked. This develops bilateral coordination and planning.", "tip": "Children often love the repetitive motion of rolling coils; allow extensive practice time."}, {"title": "Flattening and Forming", "description": "Show how to flatten clay with hand, palm, or rolling pin. Demonstrate punching or pressing objects into flattened clay to create patterns. Show how flattened pieces can be folded, twisted, or shaped into forms. This develops understanding of transformation through manipulation.", "tip": "Provide simple tools (rolling pins, wooden spoons, shells, buttons) for creating impressions."}, {"title": "Advanced: Simple Sculptural Forms", "description": "After extensive free exploration, teach children to plan simple sculptures: rolling legs for animals, forming heads, attaching pieces. This requires planning and problem-solving. Start with very simple animals or objects before moving to complex forms.", "tip": "Using scoring (scratching surfaces to create grip) and slip (clay mixed with water) helps pieces stick together better."}, {"title": "Storage and Cycle", "description": "Show how to store clay/playdough in airtight containers. Explain that they can use the same material again and again. If desired, allow children to work with clay over multiple sessions, adding to previous creations. This shows that clay is a renewable material for ongoing exploration.", "tip": "Some children appreciate keeping their creations; others prefer the freedom to start fresh each time."}]',
  control_of_error = '["Material control and ability to manipulate without frustration", "Proper technique for rolling, pinching, and shaping", "Understanding how to attach pieces securely", "Appropriate use of clay tools safely", "Cleanup and storage of materials"]',
  direct_aims = '["To experience tactile sensations and material properties", "To develop fine motor control through manipulation", "To learn basic clay techniques and hand-building methods", "To form simple representational and abstract shapes", "To build strength and coordination in hands and fingers"]',
  materials = '["Natural clay (air-dry or modeling clay, non-toxic)", "Homemade playdough or commercial play clay", "Clay board or flat surface for working", "Simple tools: rolling pin, wooden spoon, butter knife, fork, toothpick", "Objects for making impressions: shells, buttons, stamps, leaves", "Water container for dampening clay and creating slip", "Airtight containers for storage", "Cotton cloth for cleanup", "Aprons or protective clothing (optional)"]',
  parent_description = 'Clay and playdough work engages children''s senses through hands-on manipulation. It''s both relaxing and challenging—children feel the resistance and responsiveness of the material as they squeeze, roll, and shape it. This work builds hand strength and coordination while providing a sensory outlet for stress and emotion. The focus is on the pleasure of working with material, not on creating a ''masterpiece.''',
  why_it_matters = 'Clay and playdough work develops fine and gross motor strength and coordination through repetitive, satisfying manipulation. It provides sensory engagement that aids emotional regulation and stress relief. This tactile work builds understanding of three-dimensional form and teaches how to plan and execute simple sculptures. It''s foundational to more advanced art and craft skills.'
WHERE LOWER(name) = LOWER('Clay and Playdough');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Explore how objects make marks and images when pressed into paint or ink", "Provide variety of materials for printing: sponges, stamps, objects from nature", "Focus on pattern discovery and experimentation rather than product", "Teach careful paint/ink application and pressing techniques", "Celebrate uniqueness and variety in printed results"]',
  presentation_steps = '[{"title": "Introduction to Printing Concept", "description": "Show various objects that can make prints: sponges, stamps, fruits, leaves, cork, foam pieces. Demonstrate how pressing these into paint or ink creates an image. Show the same object printed multiple times, creating a pattern. Discuss that printing is how many things we see are made: fabric, wallpaper, books, newspapers.", "tip": "Bring in examples of printed fabric or wallpaper to show real-world printing."}, {"title": "Paint/Ink Application Technique", "description": "Show how to apply paint to the printing surface. Use a shallow dish or tray with a thin layer of paint (tempera or printing ink). Demonstrate pressing the printing object into the paint to coat it completely. Show applying paint with a brush if using foam stamps. Allow practice before printing.", "tip": "Too much paint creates muddy prints; too little leaves blank spaces. Model finding the right amount."}, {"title": "Pressing and Printing Technique", "description": "Demonstrate pressing the paint-coated object firmly onto paper in one deliberate motion. Show pressing straight down (not twisting or wiggling) to transfer a clear image. Explain that each print will be slightly different because of pressure and amount of paint. Allow children to practice with low-pressure feedback.", "tip": "Pressing firmly is important; some children need reminders to use enough pressure."}, {"title": "Object Printing: Stamps and Natural Materials", "description": "Set out various printing objects: commercial stamps, cookie cutters filled with foam, cut sponges, leaves, flowers, seed pods, small vegetables (potatoes cut in half, lemons). Allow children to select objects and create prints. Experiment with multiple colors and prints on same paper.", "tip": "Fresh leaves and flowers work beautifully; allow children to collect printing materials from nature."}, {"title": "Crayon Resist Printing", "description": "Show how to draw with white or light crayons on white paper (the drawing is barely visible). Then paint over the entire paper with watercolor or diluted tempera. The crayon ''resists'' the paint, revealing the hidden image. Children are delighted by this magic technique.", "tip": "This technique is particularly good for showing cause-effect and adding an element of surprise."}, {"title": "Pattern Development", "description": "Show how repeated printing of same object creates patterns and designs. Encourage children to arrange prints in lines, grids, or organic patterns. Explore what happens when you overlap prints in different colors. This develops pattern recognition and composition.", "tip": "Provide paper with grid guidelines to help organize repetitive printing."}, {"title": "Advanced: Block Printing or Foam Stamp Carving", "description": "For older children, show how to carve simple designs into foam or linoleum blocks. Demonstrate inking the carved block and pressing onto paper. This requires planning (mirror-image thinking) and fine motor control but teaches valuable art concepts.", "tip": "Pre-carved foam blocks or stamps make this accessible without complex carving skills."}]',
  control_of_error = '["Proper amount of paint or ink on printing object", "Firm and even pressure when pressing", "Clarity of printed images based on technique", "Pattern organization and composition", "Ability to plan multi-color or multi-object designs"]',
  direct_aims = '["To understand how printing creates images and patterns", "To develop fine motor control in pressing and applying paint", "To explore color relationships through multicolor printing", "To create prints using various found and commercial objects", "To understand that artists use printing as a technique"]',
  materials = '["Printing objects: commercial stamps, foam stamps, sponges, cookie cutters", "Natural materials: leaves, flowers, seed pods, small vegetables", "Paint or ink: tempera paint, watercolor, printing ink (block printing)", "Shallow trays or plates for paint application", "Quality paper: printing paper, newspaper, kraft paper", "Water for rinsing printing objects", "Brushes for applying paint to some objects", "Crayons for crayon-resist technique", "Optional: foam blocks or linoleum blocks for carving (with carving tools)"]',
  parent_description = 'Printmaking is like a magic trick for children. They discover that pressing an object into paint creates an image—and they can repeat it again and again, creating patterns and designs. This work teaches both fine motor control and artistic composition. Children explore color relationships, pattern development, and the satisfaction of creating multiple copies of their designs. It connects to real-world printing found in textiles, wallpaper, and books.',
  why_it_matters = 'Printmaking develops understanding of how marks and images are created and transferred. It builds fine motor control and teaches planning and precision. Printmaking introduces children to an art form used across cultures and history. It provides a different approach to image-making than drawing or painting, expanding artistic vocabulary and confidence.'
WHERE LOWER(name) = LOWER('Printmaking');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Exposure to famous artworks and artists
• Developing observation and discussion skills
• Various media and styles explored
• Cultural and historical connections
• Foundation for visual literacy',
  presentation_steps = '[{"step": 1, "title": "Artwork Presentation", "description": "Present quality print of famous artwork.", "tip": "Silent observation first."}, {"step": 2, "title": "Observation", "description": "''What do you see?'' Open-ended exploration.", "tip": "No right answers."}, {"step": 3, "title": "Discussion", "description": "Discuss colors, shapes, subject, feelings.", "tip": "Guided questions."}, {"step": 4, "title": "Artist Information", "description": "Share simple information about artist.", "tip": "Human connection."}, {"step": 5, "title": "Technique", "description": "Discuss how artwork was made.", "tip": "Media exploration."}, {"step": 6, "title": "Response Art", "description": "Create art inspired by studied work.", "tip": "Personal expression."}]',
  control_of_error = 'Discussion reveals understanding; child''s verbal descriptions indicate comprehension; comparison activities show ability to identify elements; portfolio work demonstrates growth over time',
  direct_aims = '["Art vocabulary"]',
  materials = '["Art prints", "Artist cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Art Appreciation');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Developing singing voice and musical expression
• Variety of songs: cultural, seasonal, movement
• Develops pitch matching and memory
• Connects to language and cultural studies
• Foundation for lifelong music enjoyment',
  presentation_steps = '[{"step": 1, "title": "Listening", "description": "Teacher sings song. Children listen.", "tip": "Model quality."}, {"step": 2, "title": "Echo Singing", "description": "Sing phrase, children echo.", "tip": "Small chunks."}, {"step": 3, "title": "Full Song", "description": "Sing complete song together.", "tip": "Repeated practice."}, {"step": 4, "title": "Movement", "description": "Add movement or actions to song.", "tip": "Body involvement."}, {"step": 5, "title": "Variety", "description": "Build repertoire of different songs.", "tip": "Song collection."}, {"step": 6, "title": "Performance", "description": "Sing for others: parents, other classes.", "tip": "Sharing music."}]',
  control_of_error = 'None - creative',
  direct_aims = '["Musical expression"]',
  materials = '["Song cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Singing');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Introduce each instrument individually by name, appearance, and sound", "Use three-period lessons to reinforce instrument names and timbres", "Teach proper playing technique and care for each instrument", "Focus on listening and sound discrimination before group play", "Allow extended exploration and experimentation with each instrument"]',
  presentation_steps = '[{"title": "Individual Instrument Introduction", "description": "Present one rhythm instrument at a time over separate lessons. Show the instrument, say its name clearly, explain what it''s made from (wood, metal, beads, etc.), and demonstrate its sound. Have the child hold and feel the instrument, then show the proper way to play it. Allow exploration of different ways to use it (soft, loud, fast, slow).", "tip": "One instrument per lesson prevents overwhelm and allows deep familiarity."}, {"title": "Sound Discrimination Activities", "description": "Play two instruments and ask the child to identify which was played (auditory matching). Play an instrument while child''s eyes are closed, then child finds the matching instrument from several choices. Play soft and loud versions of same instrument and discuss the difference.", "tip": "Sound discrimination is a developmental skill; it develops gradually with practice."}, {"title": "Three-Period Lesson: Instrument Names", "description": "Period 1 (Introduction): Present two instruments. Play each and name them clearly. Period 2 (Recognition): ''Show me the maracas. Play the tambourine.'' Child responds to requests. Period 3 (Recall): ''What is this?'' Child names the instrument. Repeat over multiple sessions; gradually add third instrument.", "tip": "Keep it to 2-3 instruments until children demonstrate mastery before adding more."}, {"title": "Proper Playing Technique", "description": "Demonstrate correct grip and playing method for each instrument: shaking maracas (wrist motion, not arm), striking triangle (light tap with correct beater), holding rhythm sticks properly, playing drums with appropriate striking technique. Let child practice the technique before playing musically.", "tip": "Proper technique prevents frustration and produces better sounds, encouraging continued use."}, {"title": "Instrument Care and Storage", "description": "Show how to care for instruments: gentle handling, no throwing or banging inappropriately, returning to proper storage spot, keeping beads/seeds in shakers contained. Make care part of respected work. Create a designated shelf or basket where children can select and return instruments.", "tip": "Instruments last longer and maintain better sound when cared for properly."}, {"title": "Playing With Music and Stories", "description": "Introduce instruments in context of a familiar song or story the child knows well. Show how to play the instrument along with the music. Establish hand signals for starting, stopping, playing loudly, playing softly. Start with simple, repetitive music; gradually introduce more complex pieces.", "tip": "Music familiarity makes group playing easier; children can focus on instrument technique rather than learning music."}, {"title": "Guided Ensemble Playing", "description": "Once children know instruments well, guide small group playing together. Each child holds an assigned instrument. Teacher or lead child starts, others follow. Practice starting and stopping together. Gradually add more complexity (playing louder for chorus, softer for verse).", "tip": "Start with very simple songs (nursery rhymes, folk songs); success builds confidence."}]',
  control_of_error = '["Correct identification of instruments by sight and sound", "Proper playing technique for each instrument", "Appropriate care and handling of instruments", "Ability to follow conductor signals (start, stop, louder, softer)", "Maintaining steady rhythm with music"]',
  direct_aims = '["To learn the names and sounds of various rhythm instruments", "To develop listening and auditory discrimination skills", "To learn proper technique for playing each instrument", "To participate in group musical activities", "To understand rhythm and its relationship to melody and song"]',
  materials = '["Rhythm instruments: maracas, tambourine, triangle (with beater), rhythm sticks, hand drum, cymbals, bells, shakers", "Mallets and beaters appropriate for instruments", "Basket or shelf for instrument storage", "Recorded music for playing along", "Optional: hand signals or conductor''s baton", "Optional: music notation showing rhythm patterns"]',
  parent_description = 'In rhythm instruments work, children explore how sounds are made and discover the joy of making music together. They learn to listen carefully to distinguish different instrument sounds, develop proper technique for playing, and participate in group music-making. The focus is on the pleasure of sound, the skill of listening, and the satisfaction of playing along with music and other musicians.',
  why_it_matters = 'Learning rhythm instruments develops auditory discrimination and listening skills that support all music and language learning. It builds fine and gross motor control through proper playing technique. Group instrument playing develops social skills and the ability to listen to others while contributing your own part. Music-making builds confidence and provides emotional expression.'
WHERE LOWER(name) = LOWER('Rhythm Instruments');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Use music from diverse cultures and styles as foundation", "Establish clear hand signals for starting, stopping, and speed changes", "Focus on natural movement responding to music, not choreography", "Teach basic movements: walking, marching, skipping, swaying, spinning", "Create safe space for free, expressive movement"]',
  presentation_steps = '[{"title": "Introduction to Movement and Music Connection", "description": "Play different styles of music and invite children to listen first without moving. Discuss what they hear: ''Is it fast or slow? Calm or energetic? Do you want to move?'' Show that different music makes us want to move differently. Model moving gently to slow music and energetically to fast music.", "tip": "Start with familiar music before introducing new pieces."}, {"title": "Basic Movements in Free Exploration", "description": "Play simple music and invite children to move freely in space. Model basic movements: walking, marching, swaying, turning, jumping (age-appropriate). Don''t demonstrate exact movements; instead, show that each person can move their own way. Emphasize space awareness (not bumping others) and responding to music''s changes.", "tip": "Clear, spacious room with music playing is all that''s needed; formal choreography isn''t necessary."}, {"title": "Responding to Tempo and Dynamics", "description": "Play music that changes speed or loudness. Guide children to notice and respond: ''The music is getting faster\u2014walk faster.'' ''The music is soft now\u2014move gently.'' This develops listening skills and teaches how music controls movement. Vary between fast/slow, loud/soft, calm/energetic.", "tip": "Use hand signals (conductor style) to help children transition between tempos."}, {"title": "Teaching Basic Movement Vocabulary", "description": "Teach specific movements one at a time: marching (arms and legs together), skipping (lift and hop), dancing (swaying with music), spinning (controlled rotation), stretching (reaching toward ceiling or sides). Demonstrate clearly then invite children to try. Practice until comfortable before combining movements.", "tip": "Children learn movement by observing and imitating; repeated practice builds proficiency."}, {"title": "Hand Signals and Group Control", "description": "Establish clear signals the group understands: raise hand = stop moving, nod head = start, clap = change movement, etc. Practice these signals so children respond consistently. This allows the teacher to guide movement without talking, keeping focus on music and movement.", "tip": "Consistent signals make transitions smooth; practice signals during circle time before using them during movement."}, {"title": "Cultural and Diverse Music Exploration", "description": "Play music from different cultures: lullabies (soothing movements), traditional folk dances (specific stepping patterns), world music (responding to unfamiliar rhythms), ceremonial music (respectful movement). Discuss the origins and cultural significance of music. Children can learn simple movement patterns from specific traditions.", "tip": "Respectfully present cultural music; explain context and appropriate responses when relevant."}, {"title": "Movement to Familiar Songs", "description": "Choose a familiar song (nursery rhyme, folk song, simple melody). Sing while children move. Gradually introduce movements that match lyrics: ''wheels on the bus'' with hand motions, ''twinkle twinkle'' with gentle swaying. This creates memorable connection between song, music, and movement.", "tip": "Songs with narrative (stories) or repeated actions work best for connecting movement to music."}, {"title": "Free Expressive Movement Sessions", "description": "Regularly offer time for children to move freely to music of their choice. No instruction, no ''correct'' way to move\u2014just music and individual expression. This builds confidence, provides emotional outlet, and celebrates diverse ways of responding to music.", "tip": "Children often surprise us with creative, beautiful movement when free from instructions or judgment."}]',
  control_of_error = '["Appropriate space awareness and not bumping other movers", "Responding to conductor signals consistently", "Matching movement to music''s tempo and dynamics", "Age-appropriate movement skills and safety", "Following group expectations while maintaining individual expression"]',
  direct_aims = '["To develop body awareness and movement control", "To learn to respond physically to music", "To understand the connection between music and movement", "To develop balance, coordination, and motor skills through movement", "To appreciate music from diverse cultural traditions"]',
  materials = '["Music player (speakers with good sound quality)", "Diverse music collection: slow and fast, loud and soft, many cultural styles", "Large, clear space for safe movement (gymnasium, open classroom, outdoors)", "Optional: scarves or ribbons for sensory enhancement", "Optional: hand signals or conductor''s baton", "Optional: visual rhythm instruments children can observe while moving"]',
  parent_description = 'Movement to music combines listening, physical development, and joy. Children hear music from around the world and respond with their whole bodies—skipping, swaying, spinning, dancing according to what the music inspires. It''s not about learning specific choreography, but about the freedom to express themselves through movement while building coordination, balance, and listening skills. It''s pure fun combined with physical and musical development.',
  why_it_matters = 'Movement to music develops gross motor skills, balance, and coordination essential for physical development. It builds auditory perception and listening skills, teaching children to hear and respond to musical elements. Music and movement combination provides emotional expression and stress relief. It introduces children to diverse cultural musical traditions and builds appreciation for worldwide music diversity.'
WHERE LOWER(name) = LOWER('Movement to Music');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '["Begin with white bells (diatonic scale) before black bells (chromatic)", "Use the three-period lesson structure to teach pitch names and recognition", "Focus on careful listening and auditory discrimination before notation", "Match bells to their pairs as initial work before grading", "Introduce musical notation only after extensive sensory exploration"]',
  presentation_steps = '[{"title": "Introduction to Bell Instruments", "description": "Show the Montessori bells in their stands (white bells or natural wood stands). Explain that bells are a musical instrument that show the musical scale. Show how to strike a bell with the wooden striker, producing a clear tone. Demonstrate that different bells make different sounds (higher and lower pitches). Allow child to ring a bell gently, noticing the sound.", "tip": "Start with just 2-3 bells before introducing all 13 pairs."}, {"title": "Listening Exercise: Contrast", "description": "Ring the lowest bell (do or C), then the highest bell (do or C one octave higher). Ask the child to listen to the difference. Ring them again and have the child point to which is higher and which is lower. This establishes understanding of pitch contrast\u2014the foundation for all bell work.", "tip": "Repeat this contrast work multiple times; it''s foundational for all subsequent bell activities."}, {"title": "Matching Pairs Activity", "description": "Present 2-3 matching pairs of bells (white bells or brown bells): two C bells, two D bells, two E bells. Ring one bell and ask the child to find its matching partner by sound. Child rings bells until finding the match. This sensory auditory discrimination is crucial. Start with obvious contrasts (far apart on scale); gradually make matches closer together.", "tip": "Matching pairs teaches listening without requiring knowledge of note names yet."}, {"title": "Three-Period Lesson: Pitch Names", "description": "Period 1 (Introduction): Ring individual bells while naming the notes: ''This is C (do), this is D (re), this is E (mi).'' Ring each two or three times. Period 2 (Recognition): ''Show me the C bell. Ring the E bell.'' Child responds to requests by listening and touching/ringing. Period 3 (Recall): ''What bell is this?'' Child names the pitch. Repeat over multiple lessons.", "tip": "Use do, re, mi, fa, sol, la, ti, do (solfege) names or C, D, E, F, G, A, B, C (letter names)."}, {"title": "Grading the Scale Activity", "description": "Once child knows bell names, present bells in random order. Child listens to each bell, identifies it, and places it in order from lowest (C) to highest (C-octave). This teaches understanding that bells follow a sequence from low to high pitch. Grading develops listening and sequencing skills.", "tip": "Start with fewer bells (do-sol); gradually add all 13 bells (full octave scale)."}, {"title": "Playing Simple Melodies", "description": "Once bells are sequenced correctly, show how to play simple melodies: ''Twinkle Twinkle Little Star,'' ''Mary Had a Little Lamb,'' ''Do Re Mi.'' Ring the bells in order to play the melody. Child rings bells to play familiar melodies. This shows practical application of the bells.", "tip": "Write melody in color (C=red, D=orange, etc.) or use bell notation cards showing which bells to ring."}, {"title": "Introduction to Musical Notation", "description": "After extensive sensory exploration, introduce musical notation. Show the musical staff (five lines). Demonstrate how notes are placed on the staff to show different pitches (higher on staff = higher pitch). Connect staff notation to the bells: ''This note is on this line, so you ring this bell (E, for example).''", "tip": "Introduce notation only after child is comfortable with bells and pitch relationships through listening."}, {"title": "Reading and Playing from Notation", "description": "Present simple melodies written on the staff. Child reads the notation and plays the corresponding bells. This bridges sensory auditory work with symbolic musical literacy. Start with very simple melodies (3-5 notes) before increasing complexity.", "tip": "Color-coded notation (staff lines and notes in colors matching bells) helps younger children."}]',
  control_of_error = '["Correct identification of bells by listening alone", "Proper sequencing from lowest to highest pitch", "Accurate striking of bells with appropriate pressure", "Correct reading of musical notation", "Ability to play simple melodies accurately", "Gentle handling and care of expensive instruments"]',
  direct_aims = '["To develop auditory discrimination and listening skills", "To understand the concept of pitch and the musical scale", "To learn the names of pitches (do-re-mi or C-D-E)", "To play melodies using the bells", "To connect sensory auditory experience with musical notation"]',
  materials = '["Montessori Bells set: 13 pairs of bells (diatonic white and chromatic black, or brown bells) from C to C (one octave)", "Wooden strikers (one per child)", "Bell stands (usually come with sets)", "Felt or carpeted area for work to protect bells and reduce noise", "Optional: Musical notation cards color-coded to match bells", "Optional: Bell notation charts showing which bells play which melodies", "Optional: Notation paper and pencils for children to write melodies"]',
  parent_description = 'The Montessori bells are one of the most beautiful and sophisticated materials in the classroom. Children develop listening skills refined enough to hear the smallest differences in pitch, learning to match, grade, and eventually play melodies with exquisite sensitivity. This material connects sensory auditory experience directly to music reading and notation. Through bells, children understand that music has structure and order—the musical scale—and gain confidence and joy in musical expression.',
  why_it_matters = 'The Montessori bells develop auditory discrimination essential for music learning and language development. They introduce the concept of the musical scale and pitch relationships—foundational to all music education. Bells connect sensory experience directly to symbolic notation, bridging concrete and abstract understanding. This material provides a bridge from pure sensory exploration to formal music literacy, building both listening skills and musical confidence.'
WHERE LOWER(name) = LOWER('Montessori Bells');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Exposure to various musical styles and composers
• Developing listening skills and musical vocabulary
• Classical, world, folk, and contemporary music
• Cultural and historical connections
• Foundation for lifelong music appreciation',
  presentation_steps = '[{"step": 1, "title": "Listening", "description": "Play musical selection. Silent, focused listening.", "tip": "Quality recordings."}, {"step": 2, "title": "Response", "description": "''How did that make you feel?'' Share reactions.", "tip": "Personal response."}, {"step": 3, "title": "Discussion", "description": "Discuss instruments, tempo, mood.", "tip": "Musical elements."}, {"step": 4, "title": "Composer", "description": "Share simple information about composer/musician.", "tip": "Human connection."}, {"step": 5, "title": "Movement", "description": "Move freely to music.", "tip": "Body response."}, {"step": 6, "title": "Comparison", "description": "Compare different pieces or styles.", "tip": "Critical listening."}]',
  control_of_error = 'Child''s movement and responses indicate engagement; verbal descriptions reveal listening skills; ability to identify instruments and moods; comparison of different pieces shows growing discernment',
  direct_aims = '["Music vocabulary"]',
  materials = '["Music recordings", "Composer cards"]',
  parent_description = NULL,
  why_it_matters = NULL
WHERE LOWER(name) = LOWER('Music Appreciation');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Track daily weather using chart system
• Observe cloud types and weather patterns
• Understand how seasons change throughout year
• Record temperatures and precipitation
• Connect weather to animal and plant changes',
  presentation_steps = '[{"step": 1, "title": "Establish Weather Tracking", "description": "Create a simple weather chart. Each day, add symbols for: sunny, cloudy, rainy, snowy, windy. Check temperature and record.", "tip": "Make this part of morning routine"}, {"step": 2, "title": "Study Cloud Types", "description": "Learn common cloud types: cumulus (puffy), stratus (flat layers), cirrus (wispy). Observe clouds and identify types. Draw cloud pictures.", "tip": "Children love looking for shapes in clouds"}, {"step": 3, "title": "Track Seasonal Changes", "description": "Throughout year, observe and record: temperature changes, precipitation, plant growth/dormancy, animal activity, daylight hours.", "tip": "Connect to calendar work\u2014months group into seasons"}, {"step": 4, "title": "Create Seasonal Transition Activities", "description": "At season changes, celebrate with observations, activities, discussions. What changed? What stays same? How do we dress differently?", "tip": "Make season transitions conscious and celebratory"}, {"step": 5, "title": "Connect Weather to Life", "description": "Discuss how weather affects animals and plants. Why do birds migrate? Why do leaves fall? Why do animals hibernate? How do plants adapt?", "tip": "This connects weather science to biology"}]',
  control_of_error = 'Accurate weather observations; correct temperature readings; consistent tracking over time',
  direct_aims = '["Understand weather patterns and cycles", "Learn about clouds and weather prediction", "Recognize seasonal changes and patterns"]',
  materials = '["Weather chart paper and symbols", "Thermometer", "Rain gauge", "Wind indicators or anemometer", "Cloud identification guide", "Seasonal pictures and decorations", "Recording materials (pencils, colors)"]',
  parent_description = 'Weather and Seasons teaches children to observe and track weather patterns throughout the year. By maintaining a weather chart, learning cloud types, and observing seasonal transitions, children develop meteorological awareness and understand how weather affects all life on Earth.',
  why_it_matters = 'Weather awareness connects children to their environment and influences daily life. Understanding seasons and weather patterns prepares for climate studies and fosters environmental consciousness. This work develops careful observation and helps children predict and prepare for weather.'
WHERE LOWER(name) = LOWER('Weather and Seasons');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study evaporation, condensation, and precipitation
• Observe water cycle in nature
• Use diagrams and arrows showing water movement
• Create simple water cycle experiments
• Understand role of sun in water cycle',
  presentation_steps = '[{"step": 1, "title": "Introduce Water Cycle Concept", "description": "Explain that water moves in a cycle: evaporates from water bodies (becomes invisible vapor), condenses in atmosphere (forms clouds), falls as precipitation (rain/snow), flows back to water bodies.", "tip": "Use hand motions to show circular movement"}, {"step": 2, "title": "Explain Evaporation", "description": "Water from oceans, lakes, rivers turns into invisible water vapor when heated by sun. Observe puddles drying up after rain. Discuss this as evaporation.", "tip": "Concrete observation of puddles disappearing makes concept real"}, {"step": 3, "title": "Explain Condensation", "description": "Water vapor cools in upper atmosphere and forms water droplets. These droplets make clouds. Discuss how breath fogs a mirror\u2014that''s condensation.", "tip": "Use foggy bathroom mirror as relatable example"}, {"step": 4, "title": "Explain Precipitation", "description": "Water droplets in clouds combine and become heavy, falling as rain or snow. When precipitation reaches ground, it flows to rivers, lakes, oceans\u2014completing cycle.", "tip": "Connect to weather tracking and seasonal rain/snow"}, {"step": 5, "title": "Create Cycle Diagram", "description": "Together, draw the water cycle showing all stages with arrows. Label evaporation, condensation, precipitation. Display permanently as reference.", "tip": "Visual diagram helps understanding"}]',
  control_of_error = 'Correct sequence of water cycle stages; accurate labeling on diagrams; understanding of cause and effect',
  direct_aims = '["Understand water cycle stages", "Learn how water moves through environment", "Develop vocabulary for water cycle processes"]',
  materials = '["Water cycle diagram", "Pictures showing each cycle stage", "Jar for simple evaporation/condensation experiment", "Plastic wrap and rubber band", "Chart paper for creating diagram", "Colored markers"]',
  parent_description = 'Water Cycle teaches children how water moves continuously through Earth''s systems via evaporation, condensation, and precipitation. Through observation and simple experiments, children understand that the water cycle is essential to all life and is driven by the sun''s energy.',
  why_it_matters = 'Understanding the water cycle is foundational to studying weather, climate, and ecosystems. This work connects abstract atmospheric processes to concrete observation. The work develops systems thinking and appreciation for Earth''s interdependence.'
WHERE LOWER(name) = LOWER('Water Cycle');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study insect body structure: head, thorax, abdomen
• Identify six legs, antennae, wings
• Use 3-part cards for insect nomenclature
• Observe real insects with magnifying glass
• Compare different insect types',
  presentation_steps = '[{"step": 1, "title": "Introduce Insect Classification", "description": "Explain insects are invertebrates with three main body parts: head, thorax, abdomen. All insects have six legs and antennae. Most have wings.", "tip": "Show actual insect or very clear picture"}, {"step": 2, "title": "Identify Body Parts", "description": "Name parts: head, compound eyes, antennae, mouthparts. Thorax: where legs and wings attach. Abdomen: contains digestive and reproductive organs.", "tip": "Use diagrams showing all parts clearly labeled"}, {"step": 3, "title": "Count and Study Legs", "description": "All insects have exactly six legs (three pairs) attached to thorax. Observe how insects use legs for walking, jumping, holding objects.", "tip": "This distinguishes insects from spiders (8 legs)"}, {"step": 4, "title": "Three-Period Lesson", "description": "Use 3-part cards showing insect parts. Period 1: Name parts. Period 2: Child identifies parts. Period 3: Child recalls names independently.", "tip": "Include various insect examples to show diversity"}, {"step": 5, "title": "Observe Real Insects", "description": "Use magnifying glass to observe real insects carefully. Find head, thorax, abdomen. Count legs. Look for antennae, wings, eyes.", "tip": "Direct observation is most powerful learning"}]',
  control_of_error = 'Correct identification of insect body parts; accurate counting of legs; teacher verification',
  direct_aims = '["Understand basic insect structure", "Identify distinguishing features of insects", "Develop vocabulary for insect anatomy"]',
  materials = '["Real insects (dead or preserved, or live in containers)", "3-part nomenclature cards of insect parts", "Insect anatomy diagrams", "Magnifying glasses", "Colored pencils for drawing", "Collection containers"]',
  parent_description = 'Insects and Their Parts teaches children to observe and understand insect anatomy through magnifying glass exploration. By learning that all insects have six legs, antennae, and three body parts, children can identify and classify insects they encounter.',
  why_it_matters = 'Insects are fascinating and the most diverse animals. Understanding their structure prepares for studying their diversity, life cycles, and ecological importance. This work develops careful observation and helps children overcome fear or disgust toward insects.'
WHERE LOWER(name) = LOWER('Insects and Their Parts');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study three types of rocks: igneous, sedimentary, metamorphic
• Examine mineral properties (hardness, color, crystal structure)
• Use Mohs hardness scale for testing
• Create rock and mineral collection
• Understand rock formation processes',
  presentation_steps = '[{"step": 1, "title": "Introduce Rock Types", "description": "All rocks are made of minerals. Three types: igneous (formed from molten rock), sedimentary (formed from compressed layers), metamorphic (changed by heat and pressure).", "tip": "Show examples of each type"}, {"step": 2, "title": "Examine Rock Properties", "description": "Observe and test rocks: color, texture (smooth/rough), hardness, streak (color when scratched). Create a chart recording properties of various rocks.", "tip": "Hands-on examination is most engaging"}, {"step": 3, "title": "Study Minerals", "description": "Minerals are pure substances with crystal structures. Examine crystals, hardness, color. Discuss common minerals: quartz, feldspar, mica, calcite.", "tip": "Mineral collections are visually beautiful"}, {"step": 4, "title": "Test Hardness with Mohs Scale", "description": "Use Mohs scale (1-10) to test mineral hardness. Test with fingernail, copper penny, glass. Record hardness of various minerals.", "tip": "This provides concrete methodology"}, {"step": 5, "title": "Create Classification Chart", "description": "Create chart showing rock types and mineral properties. Organize rocks and minerals by characteristics. Display collection attractively.", "tip": "Organization deepens understanding"}]',
  control_of_error = 'Correct classification of rock types; accurate hardness testing; organized collection system',
  direct_aims = '["Understand three types of rocks", "Learn mineral properties and testing", "Develop vocabulary for geology"]',
  materials = '["Rock and mineral specimens", "Magnifying glasses", "Mohs hardness scale kit or testing materials", "Fingernail, copper penny, glass for testing", "Charts for recording observations", "Collection containers and storage"]',
  parent_description = 'Rocks and Minerals teaches children to observe and classify the building materials of our planet. By collecting, examining, and testing rocks and minerals, children learn about Earth''s composition and the processes that form different rock types.',
  why_it_matters = 'Understanding rocks and minerals is foundational to geology and earth science. This work develops careful observation and classification skills. Children gain appreciation for the physical world and understand that rocks tell the story of Earth''s history.'
WHERE LOWER(name) = LOWER('Rocks and Minerals');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Identify common tree species by leaves
• Study leaf shapes, venation, and margins
• Use classification system for leaf types
• Create leaf collection and identification book
• Observe seasonal changes in trees',
  presentation_steps = '[{"step": 1, "title": "Introduce Tree Identification", "description": "Trees can be identified by their leaves. Examine leaf shape, size, color, vein pattern. Collect diverse leaves for study.", "tip": "Fall is ideal time for leaf collection"}, {"step": 2, "title": "Study Leaf Parts", "description": "Name parts of leaf: blade (flat part), petiole (stem), stipules (small leaves at base), veins (transport nutrients). Discuss function of each part.", "tip": "Use real leaves and diagrams showing labeled parts"}, {"step": 3, "title": "Classify by Venation", "description": "Three types: parallel venation (grass), pinnate venation (one main vein), palmate venation (multiple main veins from base). Observe patterns in collected leaves.", "tip": "Hold leaves to light to see vein patterns"}, {"step": 4, "title": "Classify by Margins", "description": "Leaf edges vary: entire (smooth), lobed (rounded indentations), serrated (saw-tooth), dentate (tooth-like). Study diversity of margins.", "tip": "Create chart showing examples of each type"}, {"step": 5, "title": "Create Leaf Collection Book", "description": "Press and mount leaves in a book. Label each leaf with tree name, leaf shape, venation, margin type, season collected. Create a field guide.", "tip": "This creates beautiful reference and learning tool"}]',
  control_of_error = 'Accurate tree identification; correct classification of leaf features; teacher verification',
  direct_aims = '["Identify common trees by leaves", "Understand leaf structure and variation", "Develop botanical vocabulary"]',
  materials = '["Live leaves or branch samples", "Leaf pressing materials and boards", "Tree identification guide/keys", "3-part nomenclature cards", "Poster board or paper for book", "Glue, colored pencils for mounting"]',
  parent_description = 'Trees and Leaves teaches children to observe and identify trees through leaf study. By learning leaf shapes, venation patterns, and margin types, children can identify trees in their environment and appreciate botanical diversity.',
  why_it_matters = 'Understanding leaves develops close observation skills and helps children recognize the trees around them. Creating a leaf collection book is engaging and builds a useful reference. This work fosters appreciation for trees and plant diversity.'
WHERE LOWER(name) = LOWER('Trees and Leaves');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Dissect flowers to study reproductive parts
• Identify stamen (male) and pistil (female)
• Understand pollination by insects and wind
• Observe bees visiting flowers
• Create flower diagram with labeled parts',
  presentation_steps = '[{"step": 1, "title": "Introduce Flower Structure", "description": "Flowers have four parts: calyx (protects bud), corolla (colorful petals), stamen (male reproductive part), pistil (female reproductive part).", "tip": "Show a whole flower before dissecting"}, {"step": 2, "title": "Examine Flower Carefully", "description": "Look at complete flower. Identify sepals, petals. Gently examine stamen (pollen-bearing) and pistil (receives pollen). Draw or photograph.", "tip": "Magnifying glass reveals beautiful details"}, {"step": 3, "title": "Dissect Flower", "description": "Carefully remove petals, sepals, and stamens. Expose pistil in center. Observe pollen on stamen. Discuss function of each part.", "tip": "Handle with care and respect for plant"}, {"step": 4, "title": "Understand Pollination", "description": "Explain that pollen from stamen must reach pistil for reproduction. Insects (bees, butterflies) carry pollen. Wind also carries pollen. Pollination allows seed production.", "tip": "Observe bee on flower if possible"}, {"step": 5, "title": "Create Flower Diagram", "description": "Draw labeled diagram showing flower parts and pollination process. Display for reference. Connect to fruit and seed production.", "tip": "Diagram helps organize learning"}]',
  control_of_error = 'Correct identification of flower parts; accurate understanding of pollination; teacher verification',
  direct_aims = '["Understand flower structure and parts", "Learn how pollination enables reproduction", "Develop vocabulary for flower anatomy"]',
  materials = '["Fresh flowers for dissection", "Magnifying glasses", "Dissecting tools or tweezers", "Flower diagrams and pictures", "Colored pencils for drawing", "Chart paper for flower diagram", "Optional: pollen collection containers"]',
  parent_description = 'Flowers and Pollination teaches children about plant reproduction through flower dissection and observation. By studying flower parts and understanding how pollinators carry pollen, children appreciate flowers'' role in reproduction and food production.',
  why_it_matters = 'Understanding flowers develops appreciation for plant biology and the importance of pollinators. This work shows how flowers are specially designed to attract specific pollinators, demonstrating nature''s intricate relationships. The knowledge prepares for studying seeds and fruit.'
WHERE LOWER(name) = LOWER('Flowers and Pollination');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Study how different animals sense their environment
• Explore sensory adaptations: vision, hearing, smell, taste, touch
• Compare animal senses to human senses
• Discuss how senses help animals survive
• Research extreme sensory adaptations',
  presentation_steps = '[{"step": 1, "title": "Introduce Animal Senses", "description": "All animals use senses to navigate their world. Different animals have different sensory abilities adapted to their habitat and lifestyle.", "tip": "Start with familiar animals children know"}, {"step": 2, "title": "Study Vision Adaptations", "description": "Discuss: eagles have amazing vision, owls see at night, fish see in water, insects have compound eyes. Vision helps animals find food and avoid danger.", "tip": "Show pictures of different eye types"}, {"step": 3, "title": "Study Hearing Adaptations", "description": "Bats use echolocation, owls have asymmetrical ears for locating prey, elephants hear distant sounds, snakes feel vibrations. Hearing is survival adaptation.", "tip": "Discuss how animals without eyes rely on hearing"}, {"step": 4, "title": "Study Other Senses", "description": "Smell: dogs have thousands of times better smell than humans. Taste: some fish taste with whole body. Touch: whiskers and skin sense vibrations and pressure.", "tip": "Compare to human sensory abilities"}, {"step": 5, "title": "Research Extreme Adaptations", "description": "Study fascinating examples: bees see ultraviolet light, sharks sense electrical fields, snakes see heat, platypuses sense electrical fields. Create examples display.", "tip": "These examples generate enthusiasm"}]',
  control_of_error = 'Accurate descriptions of sensory adaptations; correct matching of sense to animal; teacher discussion',
  direct_aims = '["Understand animal sensory adaptations", "Compare different animals'' sensory abilities", "Develop vocabulary for senses and adaptations"]',
  materials = '["Pictures of animal sensory organs", "Diagrams showing different eye types", "3-part cards showing animal and sense", "Books about animal senses", "Optional: specimens (feathers, shells, etc.)"]',
  parent_description = 'Senses of Animals teaches children to understand how different animals perceive their world through specialized sensory abilities. By exploring examples like echolocation in bats, heat sensing in snakes, and electroreception in sharks, children appreciate the diversity of animal adaptations.',
  why_it_matters = 'Understanding animal senses develops appreciation for how evolution has created remarkable diversity. This work builds empathy for animals and helps children understand why animals behave as they do. Sensory study connects biology to behavior and ecology.'
WHERE LOWER(name) = LOWER('Senses of Animals');

UPDATE montree_classroom_curriculum_works SET
  quick_guide = '• Introduce concept of cosmic order and interconnection
• Present the Five Great Lessons framework
• Use stories to inspire wonder about universe
• Connect all knowledge to cosmic perspective
• Develop child''s sense of purpose in cosmos',
  presentation_steps = '[{"step": 1, "title": "Introduce Cosmic Education Philosophy", "description": "Explain that everything in the universe is connected. From atoms to galaxies, all things have a role. Children are part of this cosmic order.", "tip": "Use wonder and storytelling, not just facts"}, {"step": 2, "title": "Present First Great Lesson: Coming of the Universe", "description": "Tell the dramatic story of the universe''s beginning: the Big Bang, formation of stars and planets, Earth''s creation. Use hand motions and imagination.", "tip": "This is a narrative, not just scientific facts"}, {"step": 3, "title": "Present Second and Third Great Lessons", "description": "Second: Coming of Life. Story of how life began and evolved on Earth. Third: Coming of Humanity. Story of human ancestors and development.", "tip": "These stories span vast time scales"}, {"step": 4, "title": "Present Fourth and Fifth Great Lessons", "description": "Fourth: Story of Writing. How humans developed language and writing systems. Fifth: Story of Numbers and Mathematics.", "tip": "Connect human achievements to cosmic perspective"}, {"step": 5, "title": "Inspire Sense of Purpose", "description": "Help children understand their role: We are part of the cosmos. We have responsibility to care for Earth and each other. We are cosmic beings.", "tip": "This creates profound shifts in children''s perspective"}]',
  control_of_error = 'Engagement with stories; evidence of expanded perspective; discussions about interconnection',
  direct_aims = '["Understand cosmic order and interconnection", "Learn Earth and human history through narratives", "Develop sense of wonder about universe"]',
  materials = '["Great Lesson story scripts", "Timeline of universe and Earth history", "Pictures and diagrams of cosmic events", "Books about cosmos, evolution, and human history", "Art materials for response activities"]',
  parent_description = 'Cosmic Education and the Great Lessons introduce children to the Montessori framework that connects all knowledge. Through five powerful stories—the coming of the universe, life, humanity, writing, and numbers—children develop a cosmic perspective that inspires wonder and responsibility.',
  why_it_matters = 'Cosmic education is transformative. By understanding that everything is connected and that they are part of a vast cosmos, children develop a profound sense of purpose and responsibility. This philosophical framework elevates all other learning and creates meaning beyond individual subjects.'
WHERE LOWER(name) = LOWER('Cosmic Education and the Great Lessons');

COMMIT;

-- Verify the update
SELECT 
  COUNT(*) as total_works,
  COUNT(quick_guide) as with_quick_guide,
  COUNT(presentation_steps) as with_steps
FROM montree_classroom_curriculum_works;
